PRINT(N'Update rows in [dbo].[NEW_REQUIREMENT]')
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} employs electronic means to monitor CDA access to ensure that only authorized systems and services are used.' WHERE [Requirement_Id] = 1197
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} applies configuration settings for CDAs by(1) documenting and employing automated mechanisms to [centrally] manage, apply, and verify configuration settings,(2) documenting and employing [automated mechanisms/manual mechanisms] to respond to unauthorized changes to {{Licensee/Applicant}}-defined configuration settings.' WHERE [Requirement_Id] = 1199
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'For those CDAs located outside of the [Site] protected area, {{Licensee/Applicant}} developed, implemented, and [annually] reviews and updates the following:(1) a formal, documented physical and environmental protection policy that addresses the following:(a) the purpose of the physical security program as it relates to protecting the CDAs,(b) the scope of the physical security program as it applies to the organization''s staff and third-party contractors, and(c) the roles, responsibilities, and management accountability structure of the physical security program to ensure compliance with the (2) formal, documented procedures to facilitate the implementation of the physical and environmental protection policy and associated physical and operational environmental protection security controls.' WHERE [Requirement_Id] = 1233
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} developed, disseminated, and [annually] reviews and updates the following:(1) a formal, documented CDA system and communications protection policy that addresses the purpose, scope, roles, responsibilities, management commitments, and internal coordination of the system, and(2) formal, documented procedures that facilitate the implementation of the CDA system and communications protection policy and associated CDA system and communications protection security controls.' WHERE [Requirement_Id] = 1236
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} developed, disseminated, and [annually] reviews and updates the following:(1) a formal, documented system and information integrity policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among {{Licensee/Applicant}} entities, and compliance, and(2) formal, documented procedures to facilitate the implementation of CDAs and an information integrity policy and associated system and information integrity controls.' WHERE [Requirement_Id] = 1237
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} developed, disseminated, and [annually] reviews and updates the following:(1) a formal, documented media protection policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among [Site/Licensee/Applicant] entities, and compliance for each information category, as defined by the site policies, and ensures that any media which can provide information to assist an adversary is marked at a minimum to identify the sensitive nature of the media, and(2) a formal, documented procedure to facilitate the implementation of the media protection policy and all associated media protection controls, including the methodology that defines the purpose, scope, roles, responsibilities, and management commitments in the areas of media receipt, storage, handling, sanitization, removal, reuse, and disposal necessary to provide high assurance that the risk of unauthorized disclosure of information that could be used in a cyberattack to adversely impact the "safety, security, and emergency preparedness" (SSEP) functions of the nuclear facility is prevented.' WHERE [Requirement_Id] = 1241
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} developed, disseminated, and [annually] reviews and updates a formal, documented, "critical digital asset" (CDA) access control policy which addresses the purpose, scope, roles, responsibilities, management commitments, and internal coordination of such policy. {{Licensee/Applicant}} has also developed formal, documented procedures to facilitate the implementation of the access control policy and associated access security controls.' WHERE [Requirement_Id] = 1242
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} developed, disseminated, and [annually] reviews and updates the following:(1) a formal, documented identification and authentication policy, which addresses purpose, scope, roles, responsibilities, management commitments, and internal coordination, to positively identify potential network users, hosts, applications, services, and resources using a combination of identification factors or credentials, and(2) formal, documented procedures that facilitate the implementation of the identification and authentication policy and associated identification and authentication controls.' WHERE [Requirement_Id] = 1243
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) screening, enforcing, and documenting security controls for third-party personnel (including service contractors and other organizations providing control system operation and maintenance, development, information technology services, outsourced applications, and network and security management) and monitoring service provider behavior and compliance, and(2) explicitly including personnel security controls in acquisition-related contract and agreement documents.' WHERE [Requirement_Id] = 1262
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) implementing identification and authentication technology to uniquely identify and authenticate individuals and processes acting on behalf of users interacting with CDA and ensuring that CDAs, security boundary devices, physical controls of the operating environment, and individuals interacting with CDAs, are uniquely identified and authenticated and that all processes acting on behalf of users are equally authenticated and identified,(2) ensuring that the authentication technology employs strong multifactor authentication using protected processing levels.' WHERE [Requirement_Id] = 1282
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} ensures that CDAs authenticate cryptographic modules in accordance with [Federal Information Processing Standards (FIPS)140-2 Security Requirements for Cryptographic Modules].' WHERE [Requirement_Id] = 1285
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} applies configuration settings for CDAs by(1) documenting the most restrictive mode,(2) evaluating operational requirements, and(3) enforcing and documenting the most restrictive operational configuration settings based upon explicit operational requirements. This is achieved by the following:(1) establishing and documenting configuration settings for CDAs that reflect the most restrictive mode,(2) documenting and approving any exceptions from the most restrictive mode configuration settings for individual components within CDAs based upon explicit operational requirements,(3) enforcing the configuration settings in CDAs and monitoring and controlling changes to the configuration settings in accordance with {{Licensee/Applicant}} policies and procedures.' WHERE [Requirement_Id] = 1323
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} ensures that for situations in which (1) third-party security solutions are not allowed because of vendor license and service agreements and (2) loss of service support would occur if third-party applications were to be installed without vendor acknowledgement or approval.' WHERE [Requirement_Id] = 1353
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following: reviewing and analyzing the CDA audit records [no less frequently than once every 30 days] for indications of inappropriate or unusual activity and reporting findings to designated {{Licensee/Applicant}} official,(1) adjusting the level of audit review, analysis, and reporting within the CDAs when there is a change in threat or risk to {{Licensee/Applicant}} safety, security, and emergency preparedness functions based on credible sources of information as designated by {{Licensee/Applicant}} or the NRC, and(2) employing automated mechanisms on CDAs to integrate audit review, analysis, and reporting into {{Licensee/Applicant}} processes for investigation and response to suspicious activities.' WHERE [Requirement_Id] = 1364
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} security architecture provides the following:(1) audit record generation capability for the auditable events on CDAs,(2) audit record generation capability and the capability for authorized users to select which auditable events are to be audited by specific components of CDAs,(3) audit records for the selected list of auditable events on CDAs, and(4) the capability to compile audit records from multiple components within CDAs into a site wide (logical or physical) audit trail that is time correlated to within {{Licensee/Applicant}} defined level of tolerance for the relationship between time stamps of individual records in the audit trail.' WHERE [Requirement_Id] = 1370
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} ensures the following:(1) A "system use notification" message is displayed before granting system access informing potential users of the following:(a) The user is accessing a restricted system.(b) System usage is monitored, recorded, and subject to audit.(c) Unauthorized use of CDA is prohibited and subject to criminal and civil penalties. The use of CDAs indicates consent to monitoring and recording.(2) The CDA system use notification message provides privacy and security notices.(3) The CDA system use notification message is approved before its use.(4) The CDA system use notification message remains on the screen until the user takes explicit actions to log on to the CDA.(5) Physical notices are installed in those instances in which a CDA cannot support system use notifications.' WHERE [Requirement_Id] = 1381
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1)documenting, supervising, and reviewing the activities of users with respect to the enforcement and usage of access controls, and(2) employing automated mechanisms within CDAs to support and facilitate the review of user activities.' WHERE [Requirement_Id] = 1384
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- documenting and implementing additional precautions to protect networks and bus communications from unauthorized access when protocols lack security controls,- prohibiting the protocols from initiating commands except within the same boundary, and- prohibiting these protocols from initiating commands that could change the state of the CDA from a more secured posture to a less secured posture.' WHERE [Requirement_Id] = 1388
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) establishing and documenting usage restrictions and implementation guidance for controlled portable and mobile devices,(2) authorizing, monitoring, and controlling device access to CDAs,(3) enforcing and documenting that mobile device security and integrity are maintained at a level consistent with the CDA they support, and(4) enforcing and documenting that mobile devices are only used in one security level and that mobile devices are not moved between security levels.' WHERE [Requirement_Id] = 1391
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} ensures the following:(1) CDAs provide a warning when allocated audit record storage volume reaches a defined percentage of maximum audit record storage capacity, which is based on [the function of how quickly storage capacity is consumed and what the organization''s resources and response times are] and documented.(2) Justification and details for alternate compensating security controls are documented for those instances in which a CDA cannot respond to audit processing failures.(3) Responses to audit failures by the {{Licensee/Applicant}} include the use of an external system to provide these capabilities.(4) If audit processing capabilities fail for a CDA or security boundary device, the following occurs:(a) Alerts are sent to designated {{Licensee/Applicant}} officials in the event of an audit processing failure.(b) Auditing failures are treated as a failure of the CDA or security boundary device and {{Licensee/Applicant}} will take action in accordance with the technical specification.(c) CDAs with auditing failures take the following additional actions: 1). Shut down the CDA. 2). Failover to a redundant CDA where necessary to prevent adverse impact to safety, security, or emergency preparedness functions. 3). Overwrite only the oldest audit records. 4). Stop generating audit records.' WHERE [Requirement_Id] = 1396
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} CDAs use a time source protected at an equal or greater level than the CDAs or an internal system clocks to generate time stamps for audit records, and {{Licensee/Applicant}} synchronizes the time on all CDAs.{{Licensee/Applicant}} synchronizes the time of all CDAs from a dedicated source protected at an equal or greater level than the CDA existing on the security network, attached directly to the CDA or via SNTP and a trusted key management process.{{Licensee/Applicant}} implements only methods of time synchronization that do not introduce a vulnerability to cyberattack and/or common-mode failure.' WHERE [Requirement_Id] = 1397
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) protecting audit information and audit tools from unauthorized access, modification, and deletion in a manner consistent with the CDA sources, and(2) ensuring that all audit information is protected at the same level as the device sources.' WHERE [Requirement_Id] = 1398
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) configuring systems that provide name/address resolution to supply additional data origin and integrity artifacts along with the authoritative data returned in response to resolution queries, and(2) configuring systems that provide name/address resolution to CDAs, when operating as part of a distributed, hierarchical namespace, to provide the means to indicate the security status of child subspaces and, if the child supports secure resolution services, enabled verification of a chain of trust among parent and child domains.' WHERE [Requirement_Id] = 1406
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) CDAs fail in a known-state to ensure that SSEP functions are not adversely impacted by the CDAs failure, and(2) to prevent a loss of confidentiality, integrity, or availability in the event of a failure of the CDA or a component of the CDA.' WHERE [Requirement_Id] = 1407
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) implementing secure domain-based authentication, as well as the following:(a) maintaining domain controllers within the given security level they are meant to service,(b) physically and logically securing domain controllers to prevent unauthorized access and manipulation,(c) prohibiting domain trust relationships between domains that exist at different security levels,(d) prohibiting domain authentication protocols from being passed between boundaries, and(e) implementing role-based access control where possible to restrict user privileges to only those required to perform the task, and(2) where domain-based authentication is not used, {{Licensee/Applicant}} is responsible for the following:(a) documenting and justifying the reason for not implementing secure domain-based authentication,(b) implementing localized authentication when feasible,(c) implementing the strongest possible challenge-response authentication mechanism within a scenario, as supported by the application, and(d) implementing role-based access control where possible to restrict user privileges to only those required to perform the task.' WHERE [Requirement_Id] = 1416
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} ensures that, where used, passwords meet the following requirements:(1) The length, strength, and complexity of passwords balance security and operational ease of access within the capabilities of the CDA.(2) Passwords have length and complexity commensurate with the required security.(3) Passwords are changed every [describe the periods for each class of system, for example 30 days for workstations, 3 months for CDAs in the vital area, etc. 90 days].(4) Passwords cannot be found in a dictionary and do not contain predictable sequences of numbers or letters.(5) Copies of master passwords are stored in a secure location with limited access.(6) Authority to change master passwords is limited to authorized personnel.' WHERE [Requirement_Id] = 1419
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) ensuring that, for those situations in which a human machine interaction (HMI) for a CDA cannot support authentication because of operational requirements, adequate physical security controls exist that require that operators are both authorized and properly identified and are monitored so that operator actions are audited and recorded,(2) controlling access to non-authenticated human machine interactions (NHMI) so as to not hamper HMI while maintaining security of the NHMI and ensuring that access to the NHMI is limited to only authorized personnel,(3) verifying that SSEP functions are not adversely affected by authentication, session lock, or session termination controls, and(4) implementing auditing capability on NHMIs to ensure that all operator activity is recorded and monitored by authorized and qualified personnel and maintaining historical records to provide for auditing requirements.' WHERE [Requirement_Id] = 1423
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} manages and documents user identifiers by performing all of the following:(1) uniquely identifying each user,(2) verifying the identity of each user,(3) receiving authorization to issue a user identifier from an organization official,(4) issuing the user identifier to the intended party,(5) disabling the user identifier after a maximum of [30 days] of inactivity, and(6) archiving user identifiers consistent with records retention for the access authorization program.' WHERE [Requirement_Id] = 1424
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} manages CDA authenticators by performing all of the following:(1) defining initial authenticator content, such as defining password length and composition, tokens, keys, and other means of authenticating,(2) establishing administrative procedures for initial authenticator distribution; lost, compromised, or damaged authenticators; and revoking authenticators,(3) changing default authenticators upon CDA installation, and(4) changing/refreshing authenticators [annually].' WHERE [Requirement_Id] = 1425
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) ensuring that CDAs obscure feedback of authentication information during the authentication process to protect the information from possible exploitation or use by unauthorized individuals, and(2) ensuring that CDAs and feedback from CDA do not provide information that would allow an unauthorized user to compromise the authentication mechanism.' WHERE [Requirement_Id] = 1426
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} establishes, implements, and documents the following requirements:(1) Configure the host intrusion detection system (HIDS) to include attributes, such as static file names, dynamic file name patterns, system and user accounts, execution of unauthorized code, host utilization, and process permissions, to enable the system to detect cyberattacks up to and including the DBT.(2) Configure HIDS to log system and user account connections in such a way that the user or security personnel are alerted if an abnormal situation occurs.(3) Configure the HIDS in a manner that does not adversely impact the CDA safety, security, and emergency preparedness functions.(4) Configure security logging storage devices as "append only" to prevent alteration of records on those storage devices.(5) Perform rules updates and patches to the HIDS as security issues are identified to maintain the established level of system security.{{Licensee/Applicant}} secures HIDS configuration documents to ensure that only authorized personnel may access them.' WHERE [Requirement_Id] = 1449
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} establishes, implements, and documents the following requirements:(1) Configure CDAs with the lowest privilege, data, commands, file, and account access.(2) Configure the system services to execute at the lowest privilege level possible for that service and document the configuration.(3) Document the changing or disabling of access to files and functions.(4) Validate that baseline permission and security settings are not altered after modifications or upgrades.' WHERE [Requirement_Id] = 1452
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} sanitizes CDA media, both digital and non-digital, before disposal or release for reuse.{{Licensee/Applicant}} [follows the guidance in NIST SP 800-88] to sanitize CDA media. The information is destroyed by a method that precludes reconstruction by means available to the DBT adversaries.{{Licensee/Applicant}} identifies CDA media requiring sanitization and the appropriate techniques and procedures to be used in the process; sanitizes identified CDA media, both paper and digital, before disposal or release for reuse; and implements this control so that media sanitization is consistent.{{Licensee/Applicant}} tracks, documents, and verifies media sanitization and disposal actions and performs [quarterly] tests on sanitized data to ensure that equipment and procedures are functioning properly.' WHERE [Requirement_Id] = 1467
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}}, upon termination or transfer of an individual''s employment, follows the access authorization program established under 10 CFR 73.56 and promptly performs the following actions:(1) terminates all CDA and system access,(2) conducts exit interviews,(3) informs appropriate personnel of status change or termination,(4) retrieves all security-related organizational property, and(5) retains access to organizational information and CDAs formerly controlled by terminated individual.' WHERE [Requirement_Id] = 1469
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} established, implemented, and documented procedures for the following purposes:(1) identifying the security alerts and vulnerability assessment process,(2) communicating vulnerability information,(3) correcting the flaw expeditiously utilizing the configuration management process,(4) correcting security flaws in CDAs, and(5) performing vulnerability scans and assessments of the CDA to validate that the flaw has been eliminated before the CDA is put into production.Before implementing corrections, {{Licensee/Applicant}} documents and tests software updates related to flaw remediation to determine the effectiveness and potential side effects on CDAs.The {{Licensee/Applicant}} captures flaw remediation information in its Corrective Action Program.' WHERE [Requirement_Id] = 1475
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) receiving timely security alerts, bulletins, advisories, and directives from credible external organizations as designated by the NRC and the {{Licensee/Applicant}} on an ongoing basis, such as third-party security alert notification services and vendor security alert lists, and maintaining a copy of these documents,(2) independently evaluating and determining the need, severity, methods, and timeframes for implementing security directives consistent with the security controls for the CDA (Section 3.1 of Appendix A to Regulatory Guide (RG) 5.71), and(3) within established timeframes set by the licensee or as directed by the NRC, {{Licensee/Applicant}}:(a) generates and documents internal security alerts, advisories, and directives as necessary,(b) disseminates and documents security alerts, advisories, and directives to designated personnel for action and tracks their status and completion,(c) implements and documents security directives in accordance with established timeframes or implements an alternate security measure,(d) implements and documents any required mitigation measures in accordance with the [configuration management process], and(e) employs automated or other mechanisms (e.g., e-mail lists) to make security alert and advisory information available to [Site], as needed.' WHERE [Requirement_Id] = 1478
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} developed, disseminated, and [annually] reviews the following:(1) a formal, documented CDA maintenance policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among {{Licensee/Applicant}} entities, associated CDA maintenance controls, and compliance,(2) formal, documented procedures to facilitate the implementation of the CDA maintenance policy and associated maintenance controls, and(3) the system maintenance policy and procedures which cover assets located in all security boundaries, including the following:(a) owner-controlled area: the outermost protected area boundary for a plant that is outside the plant''s security area,(b) protected area: an area within the boundaries of a nuclear facility that is encompassed by physical barriers and to which access is controlled (see 10 CFR 73.2, "Definitions"),(c) vital areas: areas containing any equipment, system, device, or material, the failure, destruction, or release of which could directly or indirectly endanger the public health and safety by exposure to radiation. Vital areas may also contain equipment or systems which would be required to function to protect public health and safety following such failure, destruction, or release, and(d) public access area: locations outside the physical control of the plant.' WHERE [Requirement_Id] = 1483
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) maintaining and documenting a current list of authorized maintenance personnel consistent with its access authorization program and insider mitigation program, and(2) implementing and documenting [automated mechanism or non-automated mechanism] to detect unauthorized use or execution of commands by an escorted individual, or designating and documenting {{Licensee/Applicant}} personnel with required access authorization and knowledge necessary to supervise escorted personnel interacting with CDAs.' WHERE [Requirement_Id] = 1484
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) developing and maintaining a list of, and issuing authorization credentials (e.g., badges, identification cards, smart cards) to, personnel with authorized access to facilities containing CDAs and security boundary systems, and(2) designating officials within the organization to review and approve the above access lists and authorization credentials, consistent with the access authorization program.' WHERE [Requirement_Id] = 1485
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} implements and documents a defensive strategy, as well as the following:(1) allocates the highest degree (i.e., Level 4) of cyber security protection to CDAs that carry out safety, important to safety, and security functions and protects those CDAs from lower defensive levels,(2) prevents remote access to CDAs located in the highest defensive level,(3) prevents spoofing of addresses from one security level to another,(4) only one-way data flow is allowed from Level 4 to Level 3 and from Level 3 to Level 2,(5) initiation of communications from digital assets at lower security levels to digital assets at higher security levels is prohibited,(6) bi-directional (2-way) communication between CDAs in Level 4 is only conducted within a security Level 4,(7) any non-safety system that has bi-directional communication to a safety system is afforded the same level of protection as the safety system,(8) provides intrusion prevention and detection capabilities within and at the boundaries between security levels,(9) ensures for defense-in-depth levels using bi-directional (2 way) communication that data flow from one level to other levels occurs only through a device that enforces the security policy between each level and detects, prevents, delays, mitigates, and recovers from a cyber-attack coming from the lower security level, and(10) moves data, software, firmware, and devices from lower levels of security to higher levels of security using a documented validation process or procedure which is trustworthy at or above the trust level of the device on which the data, code, information, or device will be installed or connected with to ensure that the data, software, firmware, or devices are free from known malicious code, Trojan viruses, worms, and other passive attacks.CDAs that provide safety, important-to-safety, security, or control functions are allocated defensive Level 4 protection.CDAs that provide data acquisition functions are allocated at least defensive Level 3 protection.' WHERE [Requirement_Id] = 1503
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} implements and documents security boundary control devices between higher security levels and lower security levels that include the following elements:(1) physically and logically secures and hardens CDAs to prevent unauthorized access or manipulation,(2) employs secure management communications and encryption in accordance with Appendix B to RG 5.71,(3) provides logging and alert capabilities,(4) provides intrusion detection and prevention capabilities,(5) detects and prevents malware from moving between boundaries,(6) possesses the ability to perform more than stateful inspection with respect to the protocols used in communication across the boundary, such as through a bastion host or application proxy.' WHERE [Requirement_Id] = 1509
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} implements and documents security boundary control devices between higher security levels and lower security levels that except in the case of data diodes, contains a rule set that at a minimum:(a) is configured to deny traffic, except that which is explicitly authorized,(b) provides protocol, source, and destination filtering such as IP addresses, MAC addresses, TCP ports, and UDP ports, bases blocking on source and destination address pairs, services, and ports where the protocol supports this,(c) does not permit either incoming or outgoing traffic by default,(d) is managed either through a direct connection to the firewall from a management device, such as a laptop, or through a dedicated interface connected to a site centric security network,(e) does not permit direct communication to the firewall from any of the managed interfaces,(f) records information relative to accepted and rejected connections, traffic monitoring, analysis, and intrusion detection,(g) forwards logs to a centralized logging server,(h) enforces destination authorization and restricts users by allowing them to reach only the CDAs necessary for their function,(i) records information flow for traffic monitoring, analysis, and intrusion detection,(j) is deployed and maintained by authorized personnel adequately trained in the technologies used,(k) documents and designs with minimal connections that permit acquisition and control networks to be severed from corporate networks, should that decision be made, in times of serious cyber incidents or when directed by authorized personnel who are designated to do so,(l) is evaluated, analyzed, and tested before deployment and routinely upon modification of the rule set and updates to the operational software and firmware required to operate the firewall,(m) receives time synchronization from a trusted and dedicated source existing on the security network, attached directly to the CDA or via SNTP and a trusted key management process,(n) synchronizes time with CDAs to provide for event correlation,(o) is capable of forwarding logging information in a standard format to a secure logging server or uses an external device to provide this logging (as in the case of a data diode),(p) routinely reviews logs by personnel that are appropriately trained in such analysis to detect malicious or anomalous activity,(q) is updated [quarterly],(r) uses only physically and logically secured and hardened computing devices and flow control to prevent unauthorized access or manipulation of data streams,(s) allows no information of any kind, including handshaking protocols, to be transferred directly from networks, systems, or CDAs existing at a lower security level to networks, systems, or CDAs existing at Level 4, and(t) employs measures to prevent viruses or other malicious or unwanted programs from propagating information between security levels.' WHERE [Requirement_Id] = 1530
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) training personnel in their incident response roles and responsibilities with respect to the CDAs and providing refresher training [at least annually],(2) incorporating simulated events into incident response training to facilitate effective response by personnel in crisis situations, and(3) documenting incident response training exercises and acknowledgements that personnel are qualified and trained.' WHERE [Requirement_Id] = 1534
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) testing and conducting drills of the incident response capability for CDAs [at least annually],(2) using {{Licensee/Applicant}}-defined tests or drills or both to update the incident response capability to maintain its effectiveness,(3) documenting the results of testing and drills,(4) providing incident response testing and drills procedures,(5) employing automated mechanisms to thoroughly and effectively test or drill the incident response capability, and(6) performing and documenting announced and unannounced tests and drills.' WHERE [Requirement_Id] = 1540
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) implementing and documenting an ongoing incident handling capability for security incidents that includes preparation, detection and analysis, containment, eradication, and recovery [rolled into existing incident handling program],(2) incorporating lessons learned from ongoing incident handling activities into incident response procedures and implementing the procedures accordingly,(3) forming an integrated cyber security incident response team (CSIRT),(4) in the event of an unplanned incident that reduces the number of required cyber security personnel, compensating, by using other trained and qualified onsite cyber security personnel or calling in off-duty personnel within 2 hours from the time of discovery,(5) providing the team with the technical skills and authority to effectively respond to a potential cyber security event,(6) developing and documenting processes, procedures, and controls that the team will employ upon the discovery or identification of a potential or actual cyber security attack, and(7) documenting and defining response to the following:(a) identification of what constitutes a cyber security incident,(b) identification of threat level classification for incidents,(c) description of actions to be taken for each component of the Incident Response &Recovery (IR&R) process,(d) description of individual postulated classes or categories of incidents or attacks, as analyzed during attack vector analysis, and indicators and potential or planned methods of mitigation,(e) identification of defensive strategies that would assist in identifying and containing a cyberattack,(f) description of the CSIRT incident notification process,(g) description of incident documentation requirements,(h) establishment of coordinated and secure communication methods to be used between local and remote CSIRT members and outside agencies, and(i) description of response escalation requirements.Incident data collected includes the following:(1) incident title,(2) date of incident,(3) reliability of report,(4) type of incident (e.g., accident, virus),(5) entry point (e.g., Internet, wireless, modem),(6) perpetrator,(7) type of system, hardware and software impacted,(8) brief description of incident,(9) impact on organization,(10) measures to prevent recurrence, and(11) references.' WHERE [Requirement_Id] = 1554
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The {{Licensee/Applicant}} CSIRT consists of individuals with knowledge and experience in the following areas:(1) Information and digital system technology - This covers the areas of cyber security, software development and application, computer system administration, and computer networking. In particular, knowledge is required of the digital systems involved in plant operations, including digital instrumentation and control systems, and those involved in plant business systems. In the plant operations area, this includes programmable logic controllers, control systems, and distributed control systems. In the business area, this includes computer systems and databases containing information used to design, operate, and maintain CDAs. In the networking arena, knowledge is required of both plant and corporate-wide networks. An experienced and highly skilled cyber security staff member might have expertise in all of these areas.Nuclear facility operations, engineering, and safety - This includes knowledge of overall facility operations and plant technical specifications. Staff representing this technical area must be able to trace the impact of a vulnerability or series of vulnerabilities in a CDA (or connected digital asset) outward through plant subsystems and systems so that the overall impact on safety, security, and emergency preparedness of the plant can be evaluated.(2) Physical and operational security - This includes in-depth knowledge of the plant''s physical and operational security program. In addition to the above requirements, specialized in-depth cyber security skills are required to perform the electronic validation testing and optional scanning activities.(3) {{Licensee/Applicant}} may not have onsite personnel trained and experienced in all arenas. If this expertise is not available on site, corporate-level cyber security personnel, an independent cyber security organization, or other sources of the necessary validation expertise are considered. In addition, individuals with the following roles join the CSIRT on an as-needed basis (depending on the incident):(a) site security (physical),(b) senior plant management,(c) corporate public relations, and(d) corporate legal.' WHERE [Requirement_Id] = 1557
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} developed an incident response plan that:(1) describes the structure and organization of the cyber incident response capability,(2) provides a high-level approach for how the cyber incident response capability fits into the overall organization,(3) defines reportable cyber incidents consistent with Regulatory Position C.8.6,(4) provides metrics for measuring the cyber incident response capability within the organization,(5) defines the resources and management support needed to effectively maintain and mature an incident response capability, and(6) is reviewed and approved by the Cyber Security Program Sponsor.{{Licensee/Applicant}} distributes copies of the incident response plan plant personnel including incident response personnel, reviews the incident response plan [annually], revises the incident response plan to address changes or problems encountered during plan implementation, execution, or testing, and communicates incident response plan changes to plant personnel including incident response personnel.' WHERE [Requirement_Id] = 1560
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}}''s contingency plan includes the following:(1) required response to events or conditions of varying duration and severity that would activate the recovery plan,(2) procedures for operating the CDAs in manual mode with external electronic connections severed until secure conditions can be restored,(3) roles and responsibilities of responders,(4) processes and procedures for the backup and secure storage of information,(5) complete and up-to-date logical diagrams depicting network connectivity,(6) current configuration information for components,(7) personnel list (according to title or function or both) for authorized physical and cyber access to the CDA,(8) communication procedure and list of personnel (according to title or function or both) to contact in the case of an emergency, and(9) documented requirements for the replacement of components.' WHERE [Requirement_Id] = 1568
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) implementing a cyber security contingency plan to maintain the SSEP functions by developing and disseminating roles, responsibilities, assigned individuals with contact information, and activities associated with determining the effects of CDAs after a compromise, disruption or failure and restoring those CDAs,(2) coordinating contingency plan development with {{Licensee/Applicant}} organizations responsible for related plans (e.g., emergency plan, physical security plan) and requirements (e.g., technical specifications),(3) maintaining the necessary resources and capacity to ensure that necessary information processing, telecommunications, and environmental support exist during crisis situations,(4) documenting the resources needed to ensure that the capacity necessary for information processing, telecommunications, and environmental support exists during crisis situations, and(5) deploying CDAs such that, in the event of a loss of processing within a CDA or a loss of communication with operational facilities, CDAs will execute predetermined actions.' WHERE [Requirement_Id] = 1574
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) training personnel in their contingency roles and responsibilities with respect to the CDAs and providing refresher training [at least annually] or consistent with the [Licensee/Applicant''s] overall contingency program, whichever period is shorter,(2) maintaining training procedures and documenting training records of individuals,(3) including training drills to familiarize contingency personnel with the facility, CDAs, and available resources and evaluating the site''s capabilities to support contingency operations,(4) employing automated mechanisms to thoroughly and effectively test/drill the contingency plan by providing more complete coverage of contingency issues, and(5) selecting realistic test/drill scenarios and environments, effectively stressing the CDAs.' WHERE [Requirement_Id] = 1582
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} employs mechanisms with supporting procedures that allow CDAs to be recovered and reconstituted to a known secure state following a disruption or failure and only when initiated by authorized personnel.{{Licensee/Applicant}} performs regression testing before returning to normal operations to ensure that CDA are performing correctly.' WHERE [Requirement_Id] = 1584
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} establishes, implements, and documents requirements for training to provide the following:(1) cyber security awareness training for {{Licensee/Applicant}} employees and contractors which addresses the following:(a) the site-specific objectives, management expectations, programmatic authority, roles and responsibilities, policies, procedures, and consequences for noncompliance with the cyber security program,(b) general attack methodologies, including social engineering techniques and appropriate and inappropriate cyber security practices,(c) attack indicators, such as the following: 1. unusually heavy network traffic, 2. out of disk space or significantly reduced free disk space, 3. unusually high CPU usage, 4. creation of new user accounts, 5. attempted or actual use of administrator-level accounts, 6. locked-out accounts, 7. account in-use when the user is not at work, 8. cleared log files, 9. full log files with unusually large number of events, 10. antivirus or IDS alerts, 11. disabled antivirus software and other security controls, 12. unexpected patch changes, 13. machines connecting to outside IP addresses, 14.  requests for information about the system (social engineering attempts), 15. unexpected changes in configuration settings, 16. unexpected system shutdown, 17. unusual activity from control devices, 18. loss of signal from control devices, and 19. unusual equipment in secure areas,(d) organizational contacts to whom to report suspicious activity, incidents, and violations of cyber security policies, procedures, or practices, (e) an explanation as to why access and control methods are required,(f) measures users can employ to reduce risks, and(g) the impact on the organization if the control methods are not incorporated.' WHERE [Requirement_Id] = 1614
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} establishes, implements, and documents training programs for personnel performing, verifying, or managing activities within the scope of the program to ensure that suitable proficiency is achieved and maintained.{{Licensee/Applicant}} individuals that have cyber security responsibilities related to programs, processes, procedures, or individuals that are involved in the design, modification, and maintenance of CDAs, will receive technical training.{{Licensee/Applicant}} establishes, implements, and documents requirements to do the following: provide cyber security-related technical training to individuals:(a) before authorizing access to CDAs or performing assigned duties,(b) when required by policy or procedure changes and plant modifications, and(c) annually or at an interval as defined by the {{Licensee/Applicant}}, whichever is shorter, to mitigate risk and to ensure personnel maintain competency, and (1) provide cyber security-related technical training on applicable cyber security concepts and practices to those individuals whose roles and responsibilities involve designing, installing, operating, maintaining, or administering (e.g., serving as a system administrator) CDAs or associated networks which addresses the following:(a) knowledge of specific cyber security and engineering procedures, practices, and technologies, including implementation methods and design requirements, which apply to the assets they may encounter as part of their job and(b) general information on cyber vulnerabilities, potential consequences to CDAs and networks of successful cyberattacks, and cyber security risk reduction methods.{{Licensee/Applicant}} provides system managers, cyber security specialists, system owners, network administrators, and other personnel having access to system-level software with security-related technical training to perform their assigned duties.' WHERE [Requirement_Id] = 1623
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} individuals who have programmatic and procedural cyber security authority and require the necessary skills and knowledge to execute capabilities expected of a cyber security specialist receive specialized cyber security training in order to design, execute, and manage the cyber defensive strategy effectively.{{Licensee/Applicant}} establishes, implements, and documents requirements for advanced training for individuals who are designated security experts or specialists, including the cyber security specialists with roles and responsibilities for cyber security, incident response, and the execution and management of defense-in-depth protective strategies. Advanced training addresses the following:(1) achievement and maintenance of the necessary up-to-date skills and knowledge in core competencies of data security, operation system security, application security, network security, security controls, intrusion analysis, incident management and response, digital forensics, penetration testing, and plant system functionality and operations,(2) competency in the use of tools and techniques to physically and logically harden CDAs and networks to reduce vulnerabilities to cyberattack,(3) the provision of cyber security guidance, assistance, and training for other staff members,(4) the review of programmatic and system-specific cyber security plans and practices,(5) assessment of CDAs, networks, and assets for compliance with cyber security policies, and(6) design, acquisition, installation, operation, maintenance, or administration of security controls.' WHERE [Requirement_Id] = 1631
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} develops, implements, and documents a cross-functional cyber security team (CST).{{Licensee/Applicant}} develops, implements, and documents a program to share expertise and varied domain knowledge between members of the CST.{{Licensee/Applicant}}''s CST includes, at a minimum, a member of the organization''s information technology staff, an instrumentation and control system engineer, a control system operator, a subject matter expert in cyber security, and a member of the management staff.{{Licensee/Applicant}}''s cyber security subject matter experts'' skills include network architecture and design, security processes and practices, and secure infrastructure design and operation.{{Licensee/Applicant}}''s CST also includes the control system vendor or system integrator, as needed.{{Licensee/Applicant}}''s CST reports [directly to organizational structure how and who].' WHERE [Requirement_Id] = 1637
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} creates, documents, and staffs the following positions (roles) with appropriately qualified personnel:Role: Cyber Security SponsorRequirements: member of senior site managementResponsibilities:(1) overall responsibility and accountability for the cyber security program, and(2) provides resources required for the development, implementation and sustenance of the cyber security program.Role: Cyber Security Program ManagerResponsibilities:(1) provides oversight of the plant cyber security operations,(2) functions as a single point of contact for issues related to site cyber security,(3) provides oversight and direction on issues regarding nuclear plant cyber security,(4) initiates and coordinates CSIRT functions as required,(5) coordinates with the NRC as required during cyber security events,(6) oversees and approves the development and implementation of a cyber security plan,(7) ensures and approves the development and operation of the cyber security education, awareness, and training program, and(8) oversees and approves the development and implementation of cyber security policies and procedures.Role: Cyber Security SpecialistResponsibilities:(1) protects CDAs from cyber threat,(2) understands the cyber security implications surrounding the overall architecture of plant networks, control systems, safety systems, operating systems, hardware platforms, plant specific applications, and the services and protocols upon which those applications rely,(3) performs cyber security evaluations of digital plant systems,(4) conducts security audits, network scans, and penetration tests against CDAs as necessary,(5) conducts cyber security investigations involving compromise of CDAs,(6) preserves evidence collected during cyber security investigations to prevent loss of evidentiary value, and(7) maintains expert skill and knowledge level in the area of cyber security.Role: Cyber Security Incident Response TeamRequirements:(1) personnel have knowledge of cyber forensics and(2) functions in accordance with the incident response planResponsibilities:(1) initiates emergency action when required to safeguard CDAs from compromise and to assist with the eventual recovery of compromised systems,(2) contains and mitigates incidents involving critical and other support systems, and(3) restores compromised CDAs.' WHERE [Requirement_Id] = 1665
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} documents and implements a program to ensure that new acquisitions contain security design information, capabilities or both to implement security controls in Appendix B to RG 5.71. Such security capabilities include the following:(1) being cognizant of evolving cyber security threats and vulnerabilities,(2) being cognizant of advancements in cyber security protective strategies and security controls,(3) conducting analyses of the effects that each advancement could have on the security, safety, and operation of critical assets, systems, CDAs, and networks and implementing these advancements in a timely manner, and(4) replacing legacy systems as they reach end of life with systems that incorporate security capabilities.{{Licensee/Applicant}} establishes timeframes to minimize the time it takes to deploy new andmore effective protective strategies and security controls.' WHERE [Requirement_Id] = 1678
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} verifies and validates the results of the developer''s security testing in conducted in accordance with Section 12.5 above. {{Licensee/Applicant}} is responsible for the following:(1) testing CDA (e.g., offline on a comparable CDA) security devices, security controls, and software to ensure that they do not compromise the CDA or the operation of an interconnected CDA operation before installation,(2) testing to ensure that CDAs do not provide a pathway to compromise the CDA or other CDAs,(3) implementation of the security controls in Appendices B and C to RG 5.71 in accordance with the process described in Section 3.1.6 of Appendix A to RG 5.71,(4) testing of the security controls for effectiveness, as described in Section 4.1.2 of Appendix A to RG 5.71,(5) performance of vulnerability scans, in accordance with Section 4.1.3 of Appendix A to RG 5.71 and Section 13.1 of this plan, against the CDA in its integrated state and correction, elimination, or discussion of discovered vulnerabilities,(6) installation and testing of the CDA in the target environment, and(7) performance of an acceptance review and test of the CDA security features.{{Licensee/Applicant}} documents the following:(1) Security controls implemented in accordance with Appendix B of RG 5.71.(2) Verification of the effectiveness of the security controls implemented in accordance with Appendix C.(3) Security design features developed to address the identified security requirements for the CDA (if any), in addition to the security controls implemented in accordance with Appendix B to 5.7.1. For each security feature or configuration to be implemented, the documentation includes a description of the feature, its method of implementation, and any configurable options associated with the feature are provided. Each security feature designed into the system is traceable to its corresponding security requirement.The security reviews of the implemented design by the cyber security organization responsible for the protection of the critical assets/systems/networks are documented. The review ensures that the security design configuration item transformations from the requirements implemented are correct, accurate, and complete.{{Licensee/Applicant}} requires [annual] audits of CDAs to verify the following:(1) The security controls present during testing remain in place and are functioning correctly in the production system.(2) CDAs are free from known vulnerabilities and security compromises and continue to provide information on the nature and extent of compromises, should they occur.(3) The change management [process/program] is functioning effectively and is recording configuration changes appropriately.' WHERE [Requirement_Id] = 1715
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'Protection and mitigation of risk are achieved by implementing:(1) the defense-in-depth strategies discussed in Section 3.2 of RG 5.71,(2) the security controls described in Appendices B and C to RG 5.71, and(3) digital equipment and software cyberattack detection, prevention, and recovery techniques and tools to the systems, structures, and components within the scope of the rule and(4) Section 4 of Appendix A of RG 5.71.{{Licensee/Applicant}} has the detailed information on how these requirements are implemented to achieve the high assurance objectives of security controls specified in this plan. The detailed information is available for NRC inspections and audits.' WHERE [Requirement_Id] = 1725
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} develops, disseminates, and [annually] reviews and updates a formal, documented configuration management policy and implementing procedures that address the purpose, scope, roles, responsibilities, management commitment, [coordination among {{Licensee/Applicant}} entities], associated configuration management controls, and compliance.{{Licensee/Applicant}} documents its configuration management policy as a part of the [Site] configuration management plan and includes hardware configurations, software configurations, and access permissions. Changes to hardware or software are documented and accessed in accordance with these policies and implementing procedures.The structured configuration management process evaluates and controls changes to CDAs to ensure that CDAs remains secure. Before any change is implemented, {{Licensee/Applicant}} confirms that new vulnerabilities are not introduced.' WHERE [Requirement_Id] = 1728
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'Measures necessary to deny, deter, and detect cyberattacks are implemented by [system, CDA, network protective devices] and align with the {{Licensee/Applicant}} defensive strategy.{{Licensee/Applicant}} establishes, implements, and documents security controls to deny, deter, and detect adverse threats and conditions to CDAs that may be susceptible to cyberattacks. Security controls employed counteract postulated threats.{{Licensee/Applicant}} establishes, implements, and documents the methods used to respond to incidents and to escalate cyber security events to the [Site/Licensee]''s incident response personnel, appropriate law enforcement authorities, or the NRC.The {{Licensee/Applicant}}''s Corrective Action Program evaluates, tracks, manages, provides corrective action and documents cyberattacks.{{Licensee/Applicant}} procedures that govern response to cyber events direct timely identification, detection, and response to cyberattacks. When there is a reasonable suspicion of a cyberattack, response instructions direct notification to the [shift superintendent operations, site security superintendent, manager nuclear information technology, cyber security incident response team] and other emergency response actions.{{Licensee/Applicant}} procedures direct containment activities. These measures include (but are not limited to) activities necessary for the following:(1) assist operations in conducting an operability determination,(2) isolate the affected CDA with approval by [shift superintendent operations], if possible, and(3) verify that surrounding or interconnected CDAs, networks, and support systems are not contaminated, degraded, or compromised. Eradication activities identify the attack and the compromised pathway.{{Licensee/Applicant}} patches, cleans, reimages, or replaces the CDA using disaster recovery procedures.{{Licensee/Applicant}} governing procedures direct measures necessary to mitigate the consequences of cyberattacks. Recovery activities include, but are not limited to, functional recovery tests, security function and requirements tests, restoration to an operational state, verification of operability, and return to active service. Systems, networks, or equipment affected by cyberattacks are restored and returned to operation as directed by {{Licensee/Applicant}} procedures.{{Licensee/Applicant}} conducts post incident analysis in accordance with its Corrective Action Program.{{Licensee/Applicant}} reports cyberattacks to the NRC as directed by {{Licensee/Applicant}} procedures, in accordance with the requirements of Appendix G, "Reportable Safeguards Events," to 10 CFR Part 73 and as further described in Regulatory Position C.8.6.' WHERE [Requirement_Id] = 1732
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} developed, disseminated, and [annually] reviews and updates the following:(1) a formal, documented incident response policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among {{Licensee/Applicant}} entities, and compliance,(2) formal, documented procedures to facilitate the implementation of the incident response policy and associated incident response controls that establish procedures for the following:(a) notifying staff and operators, determining whether unexpected indications or fault conditions could be the result of a cyberattack in progress,(b) in the event that the cyberattack was the result of previous activities that have lain dormant within a CDA, using the Corrective Action Program to perform an analysis to identify entry mechanisms and take steps to close down the vulnerability, and(c) establishing a disaster recovery plan that specifically permits rapid recovery from a cyberattack, including system backups which allow rapid reconstruction of the CDA, and(3) recovery plans that are exercised to ensure that they are effective and that personnel are sufficiently familiar with how to employ them in accordance with [disaster recovery plans, business continuity or emergency plans] and that changes made are based on lessons learned from exercises and drills and actual incidents and events.{{Licensee/Applicant}} includes stakeholders in the development of incident response policies, procedures, and plans, including the following groups:(1) physical security,(2) cyber security team,(3) operations,(4) engineering,(5) information technology,(6) human resources,(7) system support vendors,(8) management, and(9) legal.' WHERE [Requirement_Id] = 1733
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} developed, disseminated, and [annually] reviews and updates the following while using an independent party for the audit reviews:(1) a formal, documented audit and accountability policy that addresses the purpose, scope, roles, responsibilities, management commitments, and internal coordination of the policy, and(2) formal, documented procedures that facilitate the implementation of the audit and accountability policy and associated audit and accountability security controls.' WHERE [Requirement_Id] = 1734
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) managing and documenting CDA accounts, including authorizing, establishing, activating, modifying, reviewing, disabling, and removing accounts,(2) reviewing CDA accounts in a manner consistent with the access control list provided in the [design control package, access control program, cyber security procedures] and initiating required actions on CDA accounts [no less frequently than once every 30 days],(3) requiring access rights to be job function based,(4) conducting reviews when as individuals job function changes to ensure that rights remain limited to the individuals job function,(5) reviewing and documenting CDA accounts at a maximum interval consistent with the most recent version of Nuclear Energy Institute (NEI) 03-12, "Security Plan, Training and Qualification Plan, and Safeguards Contingency Plan," endorsed by the U.S. Nuclear Regulatory Commission (NRC), and(6) employing automated mechanisms that support CDA account management functions and enable CDA to automatically:(a) terminate temporary, guest, and emergency accounts [no less frequently than once every 30 days],(b) disable inactive accounts [no less frequently than once every 30 days],(c) create and protect audit records for account creation, deletion, and modification,(d) document and notify system administrators of all account creation, deletion, and modification activities so that system administrators are aware of any account modifications and can investigate potential cyberattacks in a timely manner.' WHERE [Requirement_Id] = 1739
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) establishing and documenting divisions of responsibility and separating functions as needed to eliminate conflicts of interest and to ensure independence in the responsibilities and functions of individuals,(2) enforcing separation of CDA functions through assigned access authorizations,(3) implementing alternative controls and documenting the justification for alternative controls and countermeasures for increased auditing for those situations in which a CDA cannot support the differentiation of roles and a single individual must perform all roles within the CDA, and(4) restricts security functions to the least amount of users necessary to ensure the security of CDAs.' WHERE [Requirement_Id] = 1741
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1)assigning the most restrictive set of rights and privileges or access needed by users for the performance of specified tasks,(2) configuring CDAs to enforce the most restrictive set of rights and privileges or access needed by users.' WHERE [Requirement_Id] = 1743
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1)identifying and documenting specific user actions that can be performed on CDAs during normal and emergency conditions without identification or authentication, and(2) permitting actions to be performed without identification and authentication only to the extent necessary to accomplish mission objectives, without adversely affecting safety, security, and emergency preparedness (SSEP) functions, and in a manner consistent with NRC regulations.' WHERE [Requirement_Id] = 1745
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} configures CDAs to do the following:(1) initiate a session lock after [within 30 minutes of inactivity],(2) provide the capability for users to directly initiate session lock mechanisms,(3) maintain the session lock on a CDA until the user reestablishes access using identification and authentication procedures.' WHERE [Requirement_Id] = 1748
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) ensuring that external systems cannot be accessed from higher levels, such as Levels 4 and 3,(2) prohibiting external systems from accessing CDAs in Levels 3 and 4, and(3) prohibiting users from using an external system to access CDAs or to process, store, or transmit organization-controlled information except in situations in which {{Licensee/Applicant}} verifies the implementation of equivalent security measures on the external system.' WHERE [Requirement_Id] = 1750
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) establishing usage restrictions and implementation guidance for mobile code technologies based on their potential to cause damage to CDAs if used maliciously, and(2) authorizing, monitoring, and controlling the use of mobile code within the CDAs.' WHERE [Requirement_Id] = 1752
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) configuring CDAs to prevent unauthorized and unintended information transfer via shared system resources, and(2) using physically separate network devices to create and maintain logical separation of Levels 3 and 4 from each other and from all other levels.' WHERE [Requirement_Id] = 1759
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} configures and documents CDA configuration settings to provide only essential capabilities and specifically prohibits, protects, and restricts the use of insecure functions, ports, protocols and services.{{Licensee/Applicant}} reviews CDAs [monthly] to identify and eliminate unnecessary functions, ports, protocols, and services.{{Licensee/Applicant}} documents and employs automated mechanisms to prevent program execution.{{Licensee/Applicant}} uses [white-lists, black-lists, gray-lists] application control technologies.' WHERE [Requirement_Id] = 1761
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) configuring CDAs to protect the integrity of transmitted information,(2) employing cryptographic mechanisms to recognize changes to information during transmission and upon receipt, unless otherwise protected by alternative physical measures,(3) implementing mechanisms to prevent "man-in-the-middle" (MITM) attacks via the following methods:(a) Media Access Control Address Locking - {{Licensee/Applicant}} locks devices and ports via address locking to prevent MITM attacks and rogue devices from being added to the network(b) Network Access Control - {{Licensee/Applicant}} implements network access control to prevent MITM attacks and rogue devices from being added to the network,(4) implementing monitoring to detect MITM and address resolution protocol poisoning.' WHERE [Requirement_Id] = 1764
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) configuring the CDAs to protect the confidentiality of transmitted information,(2) employing cryptographic mechanisms to prevent unauthorized disclosure of information during transmission and receipt unless otherwise protected by alternative physical measures.' WHERE [Requirement_Id] = 1766
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for ensuring the following:(1) The capability to input information to CDAs is restricted to only authorized sources.(2) Information is checked automatically for accuracy, completeness, validity, and authenticity as close to the point of origin as possible. Rules for checking the valid syntax of CDA inputs (e.g., character set, length, numerical range, acceptable values) are documented and in place to verify that inputs match specified definitions for format and content. Inputs passed to interpreters are prescreened to prevent the content from being unintentionally interpreted as commands.' WHERE [Requirement_Id] = 1772
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} documents and implements controls for CDAs to ensure the following:(1) Error conditions are identified.(2) Generated error messages provide information necessary for corrective actions without revealing potentially harmful information that could be exploited by adversaries.(3) Error messages are revealed only to authorized personnel.(4) Inclusion of sensitive information, such as passwords, in error logs or associated administrative messages is prohibited.' WHERE [Requirement_Id] = 1776
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) monitoring and documenting physical access to CDAs and security boundaries to detect and respond to physical security incidents,(2) reviewing physical access logs,(3) coordinating results of reviews and investigations with {{Licensee/Applicant}}''s incident response personnel,(4) monitoring real-time physical intrusion alarms and surveillance equipment,(5) employing automated mechanisms to assess and recognize potential intrusions and initiates appropriate response actions, and(6) providing adequate lighting for access monitoring devices (e.g., cameras).' WHERE [Requirement_Id] = 1777
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) controlling and documenting visitor physical access to CDAs by verifying the identity and confirming access authorization of these individuals prior to entry, and(2) escorting visitors and monitoring visitor activity to prevent adverse impact to SSEP functions.' WHERE [Requirement_Id] = 1778
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) authorizing and documenting changes to CDAs(2) retaining and reviewing records of CDA configuration changes and audit activities associated with CDA configuration changes and employing [manual/automated] mechanisms to:(a) document changes to CDAs,(b) notify designated approval authorities, and(c) prohibit implementation of changes until designated approvals are received and documented.' WHERE [Requirement_Id] = 1780
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) controlling all physical access points (including designated entry and exit points) to locations where CDAs reside and verifying individual access authorization before granting access to these areas,(2) approving individual access privileges and enforcing physical and logical access restrictions associated with changes to CDAs,(3) controlling logical access through the use of electronic devices and software,(4) generating, retaining, and reviewing records pertaining to access restrictions,(5) ensuring that only qualified and authorized individuals obtain access to CDAs, and(6) controlling physical access to the CDAs independent of the physical access controls for the facility.' WHERE [Requirement_Id] = 1783
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} develops, documents, and maintains a current baseline configuration of CDAs and their connections including the interface characteristics, security requirements, and the nature of the information communicated.. As a part of the configuration management process, {{Licensee/Applicant}} employs [manual/automated] mechanisms to maintain an up-to-date, complete, accurate, and readily available baseline configuration of each CDA.{{Licensee/Applicant}} documents the up-to-date baseline configurations and audits the configurations [quarterly].Baseline configurations include [but are not limited to] a current list of all components (e.g., hardware, software), configuration of peripherals, version releases of current software, and switch settings of machine components.For each CDA, {{Licensee/Applicant}} maintains a log of configuration changes made, the name of the person who implemented the change, the date of the change, the purpose of the change, and any observations made during the course of the change.{{Licensee/Applicant}} documents and maintains baseline configurations for development and test environments that are managed separately from the operational/production baseline configuration.{{Licensee/Applicant}} employs a "deny-all, permit-by-exception" authorization policy to identify and authorize software permitted on {{Licensee/Applicant}} CDAs (i.e., white lists of authorized software).After authorized changes are implemented, {{Licensee/Applicant}} verifies that security features still function properly and that adequate cyber security levels are maintained. Individuals authorized to modify CDA configurations are properly trained and qualified to perform the modifications.{{Licensee/Applicant}} defines the minimum physical and logical access for the modifications.' WHERE [Requirement_Id] = 1784
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} develops, documents, and maintains an inventory of the components of CDAs that has the following attributes:(1) accurately reflects the current system configuration,(2) ensures that the location (logical and physical) of each component is consistent with the authorized boundary of the CDA,(3) provides the proper level of granularity deemed necessary for tracking and reporting and for effective property accountability,(4) updates the inventory of system components as an integral part of component installations and system updates,(5) employs automated mechanisms to maintain an up-to-date, complete, accurate, and readily available inventory of system components,(6) employs automated mechanisms to detect the addition of unauthorized components or devices into the environment and disables access by such components or devices or notifies designated {{Licensee/Applicant}} officials, and(7) documents the [names or roles] of the individuals responsible for administering those components.' WHERE [Requirement_Id] = 1785
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} provides competent and trained incident response support personnel who are available year round, 24 hours per day to offer advice and assistance to users of CDAs in response to and reporting of cyber security incidents. The support resource is an integral part of {{Licensee/Applicant}}''s incident response capability.{{Licensee/Applicant}} employs mechanisms to increase the availability of incident response-related information and support.' WHERE [Requirement_Id] = 1786
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} identifies and documents alternate storage locations and initiates necessary agreements to permit the storage of CDA backup information. The frequency of CDA backups and the transfer rate of backup information to the alternate storage locations are consistent with {{Licensee/Applicant}}''s recovery time objectives and recovery plan objectives.{{Licensee/Applicant}} is responsible for the following:(1) identifying an alternate storage location that is geographically separated from the primary storage location so as not to be susceptible to a common hazard,(2) configuring the alternate storage location to facilitate recovery of operation, and(3) identifying and documenting potential accessibility problems to the alternate storage location in the event of a wide area disruption or disaster and implementing explicit mitigation actions.' WHERE [Requirement_Id] = 1796
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) conducting backups of user-level and system-level information,(2) backing up CDAs at an interval identified for the CDA or based on trigger events,(3) protecting backup information at the storage location,(4) testing and documenting backup information [monthly] to verify media reliability and information integrity,(5) using backup information in the restoration of CDA functions as part of contingency plan testing,(6) protecting system backup information from unauthorized modification,(7) storing backup copies of the operating system and other critical CDA software in a separate facility or in a fire-rated container that is not collocated with the operational software, and(8) establishing and documenting the timeframe in which data or the CDA must be restored and the frequency at which critical data and configurations are changing.' WHERE [Requirement_Id] = 1797
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} documents and requires that system developers and integrators of acquired CDAs create, implement, and document a security test and evaluation plan to ensure that the acquired products meet all specified security requirements and developers cyber security program maintains the integrity of the acquired system until the product is delivered to the {{Licensee/Applicant}} by implementing equivalent security controls as described in RG 5.71 to prevent tampering and to provide high assurance that the integrity of the developed CDA is maintained until delivered to the licensee.{{Licensee/Applicant}} requires that CDA developers/integrators:(1) perform configuration management during CDA design, development, implementation, and operation,(2) manage and control changes to the CDA,(3) implement only {{Licensee/Applicant}} approved changes,(4) document approved changes to the CDA, and(5) track security flaws and flaw resolution.' WHERE [Requirement_Id] = 1810
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} requires the developer to perform and document that security requirements are verified and validated and that security controls implemented in the product and used to meet the requirements of this plan are tested to ensure they are effective per section A.4.1.2. {{Licensee/Applicant}} requires documentation of all of the following activities:(1) system design transformed into code, database structures, and related machine executable representations,(2) hardware and software configuration and setup,(3) software coding practices and testing,(4) communication configuration and setup (including the incorporation of reused software and commercial off-the-shelf products),(5) The results of unit tests performed to ensure that the code was developed correctly and accurately and completely reflects the security design configuration transformations from the requirements,(6) details of the implementation of each required security feature within the developed code base. The listing includes reference the coded functions and modules within the code base that were developed to implement the security features,(7) security configurations implemented to meet security design features specified in the requirements,(8) operating system security configurations implemented to meet security design features specified in the requirements are documented,(9) For programming languages that support static analysis source code scanners, results of the following are documented:(a) the static source code vulnerability analysis performed to inspect the developed code for potential security defects, poor programming practices, hidden functions, and vulnerable features within the code during the implementation of the code base and methods applied to eliminate these vulnerabilities,(b) the security defect tracking metrics used to capture and track the identification, type, classification, cause, and remediation of security defects found within the code, and(c) the defects encountered during the translation of the design features specified in the requirements into code.(10) For all programming languages, the results of the following are documented:(a) a dynamic source code vulnerability analysis performed to inspect the developed code for potential security defects, poor programming practices, hidden functions, and vulnerable features within the code during the implementation of the code base and methods applied to eliminate these vulnerabilities,(b) the security defect tracking metrics used to capture and track the identification, type, classification, cause, and remediation of security defects found within the code, and(c) the defects encountered during the translation of the design features specified in the requirements into code.' WHERE [Requirement_Id] = 1811
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} protects the availability of CDAs through compliance with technical specifications, preventive maintenance programs, maintenance rule programs, security plans, emergency plans, or the corrective action program. Where these programs do not apply, the availability of CDAs is provided by the following means:(1) substitution of components, when needed, and a mechanism to exchange active and standby roles of the components, and(2) consideration of the mean time to failure for components in specific environments of operation(3) having adequate inventory of essential spare parts.' WHERE [Requirement_Id] = 1815
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) determining and documenting with SSEP functions those CDAs related events that require auditing,(2) defining the list of auditable events and frequency of auditing for each identified auditable event,(3) at a minimum, auditing all CDA connections, user login/logouts, configuration/software/firmware changes, audit setting changes, privileged access, privileged commands, and any modifications of the security functions of CDAs.' WHERE [Requirement_Id] = 1816
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) reviewing and updating the list of defined auditable events [no less frequently than once a year],(2) including execution of privileged functions in the list of events to be audited by the CDAs,(3) preventing CDAs from purging audit event records on restart,(4) coordinating security audit functions within the facility to enhance mutual support and to help guide the selection of auditable events, configuring all CDAs so that auditable events are adequate to support after-the-fact investigations of security incidents, and(5) adjusting the events to be audited within the CDAs based on current threat information and effectiveness analysis described in Section 4.1.2 of Appendix A to RG 5.71.' WHERE [Requirement_Id] = 1817
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) ensuring that CDAs produce audit records that contain sufficient information to establish what events occurred, when the events occurred, where the events occurred, the sources of the events, and the outcomes of the events;(2) ensuring that CDAs provide the capability to include additional, more detailed information in the audit records for audit events identified by type, location, or subject; and(3) implementing architecture that provides the capability to centrally manage the content of audit records generated by individual components throughout CDAs, and to prevent CDAs from altering or destroying audit records.' WHERE [Requirement_Id] = 1818
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} has configured and deployed all CDA to do the following:(1) provide CDA audit reduction and report generation capability, and(2) provide the capability to automatically process audit records for events of interest based upon selectable event criteria.{{Licensee/Applicant}} documents the justification and details for alternate compensating security controls for situations in which a CDA cannot support auditing reduction and report generation by providing this capability through a separate system.' WHERE [Requirement_Id] = 1820
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for taking the following actions:(1) tests and/or exercises and documents the contingency plan [at least annually] to verify its effectiveness and the organization''s readiness to execute this plan,(2) reviews the contingency plan test and exercise results and initiates appropriate corrective actions,(3) coordinates contingency plan testing and/or exercises with {{Licensee/Applicant}} elements responsible for related plans,(4) tests and/or exercises and documents the contingency plan at emergency and/or backup sites to familiarize contingency personnel with these facilities and their available resources and to evaluate the [Site''s] capabilities to support contingency operations,(5) employs automated mechanisms to thoroughly and effectively test/exercise the contingency plan by providing a more complete coverage of contingency issues and selecting more realistic test/exercise scenarios and environments,(6) includes recovery and reconstitution of CDAs as part of contingency plan testing,(7) establishes and documents alternate controls when the contingency plan cannot be tested or exercised on production CDAs because of the potential for a significant adverse impact on safety, security, performance, or reliability of the site or CDA, and(8) uses scheduled and unscheduled system maintenance activities, including responding to CDA component and system failures, as an opportunity to test or exercise the contingency plan.' WHERE [Requirement_Id] = 1822
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}}''s cyber security awareness training is designed to increase an individual''s sensitivity to cyber threats and vulnerabilities and their recognition of the need to protect data and information. Policy-level awareness training provides employees and contractors with the ability to understand security policies so that the program is effectively implemented. Individual users must understand their responsibility for adherence to applicable policies and standards.{{Licensee/Applicant}} establishes, implements, and documents requirements for the following:(1) Training programs provide basic cyber security awareness training for facility personnel. Refresher or continuous training provides updates on new threats and technology.(2) Cyber security awareness is provided by displaying posters, offering security-messaged items, generating e-mail advisories and notices, and displaying logon screen messages.(3) Training includes practical exercises to simulate actual cyber incidents, recovery plans, response plans and adversary attacks.{{Licensee/Applicant}} develops and documents the content of cyber security training based on the following:(1) assigned roles and responsibilities,(2) specific requirements identified by the defensive strategy, and(3) CDAs to which personnel have authorized access.' WHERE [Requirement_Id] = 1823
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) enforcing assigned authorizations for controlling access to CDAs in accordance with established policies and procedures,(2) assigning all user rights and privileges on the CDA consistent with the user authorizations,(3) defining and documenting privileged functions and security-relevant information for the CDAs,(4) authorizing personnel access to privileged functions and security-relevant information consistent with established policies and procedures,(5) restricting access to privileged functions (deployed in hardware, software, and firmware) and security-relevant information to authorized personnel (e.g., security administrators),(6) defining and documenting privileged functions for CDAs,(7) requiring dual authorization for critical privileged functions and the creation of any privileged access for users, and(8) ensuring and documenting that access enforcement mechanisms do not adversely impact the operational performance of CDAs and employing alternate compensating security controls when access enforcement cannot be used.' WHERE [Requirement_Id] = 1826
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) upon successful logon, configuring CDA to display the date and time of the last logon and the number of unsuccessful logon attempts since the last successful logon, and(2) requiring all end users to report any suspicious activity to the Cyber Security Program manager.' WHERE [Requirement_Id] = 1827
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} ensures the following:(1) Security controls are implemented to limit the number of invalid access attempts by a user. The access control policy documents this requirement. The number of failed login attempts in a specified time period may vary by CDA. For example, more than three invalid attempts within a 1-hour time period will automatically lock out the account. The {{Licensee/Applicant}} system enforces the lock out mode automatically.(2) The access control policy includes a requirement that only authorized individuals, who are not the user, can unlock accounts once the maximum number of unsuccessful login attempts has been exceeded.' WHERE [Requirement_Id] = 1828
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) only allowing wireless access through a boundary security control device and treating wireless connections as outside of the security boundary,(2) prohibiting the use of wireless technologies for CDAs associated with safety-related and important-to-safety functions,(3) disabling wireless capabilities when not utilized,(4) establishing usage restrictions and implementation guidance for wireless technologies,(5) documenting, justifying, authorizing, monitoring, and controlling wireless access to CDAs and ensuring that the wireless access restrictions are consistent with defensive strategies and defensive models, as articulated in RG 5.71, and(6) conducting scans [no less frequently than once every week] for unauthorized wireless access points, in accordance with this document, and disabling access points if unauthorized access points are discovered.' WHERE [Requirement_Id] = 1829
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) configuring CDAs to separate applications into user functionality (including user interface services) and CDA management functionality,(2) configuring CDAs to isolate security functions from non-security functions, which is accomplished through [partitions, domains, etc.], including control of access to and integrity of the hardware, software, and firmware that perform these security functions,(3) configuring CDAs to employ underlying hardware separation mechanisms to facilitate security function isolation,(4) configuring CDAs to isolate critical security functions (i.e., functions enforcing access and information flow control) from both non-security functions and other security functions,(5) configuring CDAs to minimize the number of non-security functions included within the isolation boundary containing security functions,(6) configuring CDA security functions as independent modules that avoid unnecessary interactions between modules,(7) configuring CDA security functions as a layered structure minimizing interactions between levels of the design and avoiding any dependence by lower levels on the functionality or correctness of higher levels, and implementing alternative controls and documenting the justification for alternative controls or countermeasures for situations in which a CDA cannot support security function isolation and taking all of the following actions:(a) physically restrict access to the CDA,(b) monitor and record physical access to the CDA to detect and respond to intrusions in a timely manner,(c) use auditing/validation measures (e.g., security guard rounds, periodic monitoring of tamper seals) to detect unauthorized access and modifications to the CDAs,(d) ensure that individuals who have access to the CDAs are qualified, and(e) ensure that those individuals are trustworthy and reliable in accordance with 10 CFR 73.56.' WHERE [Requirement_Id] = 1833
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} documents and restricts access to "critical digital asset" (CDA) media to authorized individuals only. CDA media includes both digital media (e.g., diskettes, magnetic tapes, external or removable hard drives, flash/thumb drives, compact disks, and digital video disks) and non-digital media (e.g., paper, microfilm).{{Licensee/Applicant}} restricts access to any security information on mobile computing and communications devices with information storage capability (e.g., notebook computers, personal digital assistants, cellular telephones) to authorized individuals only.{{Licensee/Applicant}} employs automated mechanisms to restrict access to media storage areas and audits access attempts and accesses granted.' WHERE [Requirement_Id] = 1837
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) configuring CDAs to prohibit remote activation of collaborative computing mechanisms and providing an explicit indication of use to the local user, and(2) configuring CDAs to provide physical disconnection of cameras and microphones in a manner that supports ease of use, except when these technologies are used to control and monitor the CDA for security purposes.' WHERE [Requirement_Id] = 1840
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) configuring the systems that serve name/address resolution service for CDAs to perform data origin authentication and data integrity verification on the resolution response they receive from authoritative sources, and(2) configuring CDAs so that, upon receipt of data, they perform data origin authentication and data integrity verification on resolution responses whether or not the CDAs explicitly request this service.' WHERE [Requirement_Id] = 1842
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) enforcing and documenting assigned authorizations for controlling the flow of information, in near-real time, within CDAs and between interconnected systems in accordance with the established defensive strategy,(2) maintaining documentation that demonstrates that {{Licensee/Applicant}} has analyzed and addressed the types of permissible and impermissible flow of information between CDAs, security boundary devices, and boundaries and the required level of authorization to allow information flow as defined in the defensive strategy,(3) implementing and documenting information flow control enforcement using protected processing level (e.g., domain type-enforcement) as a basis for flow control decisions,(4) implementing near-real time capabilities to detect, deter, prevent, and respond to illegal or unauthorized information flows,(5) preventing encrypted data from bypassing content-checking mechanisms,(6) implementing one-way data flows using hardware mechanisms,(7) implementing dynamic information flow control based on policy that allows or disallows information flows based on changing conditions or operational considerations, and(8) configuring CDAs such that user credentials are not transmitted in clear text and documenting this requirement in the access control policy.' WHERE [Requirement_Id] = 1846
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} establishes, implements, and documents the following requirements:(1) Disable, through software or physical disconnection, unneeded networks, wireless and communication ports and removable media drives or provided engineered barriers.(2) Password protect the BIOS from unauthorized changes.(3) Document mitigation measures in cases for which password protection of the BIOS is not technically feasible.(4) Document the hardware configuration.(5) Use network devices to limit access to and from specific locations, where appropriate.(6) Allow system administrators the ability to re-enable devices if the devices are disabled by software and document the configuration.(7) Verify that replacement devices are configured in a manner that is equal to or better than the original.' WHERE [Requirement_Id] = 1848
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} documents all required applications, utilities, system services, scripts, configuration files, databases, and other software and the appropriate configurations, including revisions or patch levels, for each of the computer systems associated with the CDAs. {{Licensee/Applicant}} maintains a list of services required for CDAs. The listing includes all necessary ports and services required for normal and emergency operations. The listing also includes an explanation or cross reference to justify why each service is necessary for operation. Only those services and programs that are necessary for operation are allowed. {{Licensee/Applicant}} verifies and documents that all CDAs are patched or mitigated in accordance with the Flaw Remediation security controls in C 3.2.{{Licensee/Applicant}} documents the remediation period appropriate for software and service updates or workarounds to mitigate all vulnerabilities associated with the product and to maintain the established level of security.{{Licensee/Applicant}} documents the operating system and software patches as CDAs evolve to allow traceability and verifies that no extra services are reinstalled or reactivated.{{Licensee/Applicant}} removes or disables software components that are not required for the operation and maintenance of the CDA before incorporating the CDA into the production environment.{{Licensee/Applicant}} documents components that were removed or disabled. The software removed or disabled includes, but is not limited to the following:(1) device drivers for network devices not delivered,(2) device drivers for unused peripherals,(3) messaging services (e.g., MSN, AOL IM),(4) servers or clients for unused services,(5) software compilers in all user workstations and servers except for development workstations and servers,(6) software compilers for languages that are not used in the control system,(7) unused networking and communications protocols,(8) unused administrative utilities, diagnostics, network management, and system management functions,(9) backups of files, databases, and programs used only during system development,(10) all unused data and configuration files,(11) sample programs and scripts,(12) unused document processing utilities (e.g., Microsoft Word, Excel, Power Point, Adobe Acrobat, OpenOffice),(13) unused removable media support, and(14) games.' WHERE [Requirement_Id] = 1849
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} establishes, implements, and documents the following:(1) the patch management program, update process, and individuals responsible for installation,(2) notification of vulnerabilities affecting CDAs to be conducted [within 4 hours of receipt of the vulnerability information],(3) notification to authorized personnel of patches affecting cyber security,(4) the authorization of updates or workarounds to the baseline before implementation,(5) the patch management process for the CDA after installation, including policies, procedures, and programs relating to mitigation strategies for instances in which the vendor of the CDA informs {{Licensee/Applicant}} not to apply released patches, and(6) the level of support for testing patch releases.{{Licensee/Applicant}} establishes, implements, and tests the following:(1) received cyber security updates on a nonproduction system/device for testing and validation before installing on production systems, and(2) all updates for security impact.{{Licensee/Applicant}} ensures that the nonproduction system/device accurately replicate the production CDA.' WHERE [Requirement_Id] = 1850
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'If technically feasible, CDAs provide automated support for the management of distributed security testing and {{Licensee/Applicant}} documents the results of this testing.' WHERE [Requirement_Id] = 1851
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) detecting and documenting unauthorized changes to software and information,(2) employing hardware access controls (e.g., hardwired switches), where technically feasible, to prevent unauthorized software changes,(3) reassessing and documenting the integrity, operation, and functions of software and information by performing regular integrity, operation, and functional scans consistent with manufacturer or vendor recommendations, [quarterly] or as defined in NEI 03-12 or as required by NRC regulation, whichever is more frequent,(4) employing and documenting automated tools, where technically feasible, that provide notification to designated individuals upon discovering discrepancies during integrity verification,(5) employing and documenting centrally managed integrity verification tools,(6) requiring the use of physical tamper evident packaging or seals for system components,(7) requiring, when tamper evident packaging is used, that seals be inspected on a regular basis, and(8) ensuring and documenting that the use of integrity verification applications does not adversely impact the operational performance of the CDA and applying alternate controls when integrity verification applications cannot be used.' WHERE [Requirement_Id] = 1853
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) configuring CDAs to protect against or limit the effects of denial of service attacks,(2) configuring CDAs to restrict the ability of users to launch denial of service attacks against other CDAs or networks, and(3) configuring CDAs to manage excess capacity, bandwidth, or other redundancy to limit the effects of information-flooding and saturation types of denial-of-service attacks.' WHERE [Requirement_Id] = 1855
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} established, deployed, and documents real-time malicious code protection mechanisms at security boundary device entry and exit points, CDAs (if applicable), workstations, servers, and mobile computing devices (i.e., calibrators) on the network to detect and eradicate malicious code resulting from the following:(1) data communication between systems, CDAs, removable media, or other common means, and(2) exploitation of CDA vulnerabilities.{{Licensee/Applicant}} documents and updates malicious code protection mechanisms (including signature definitions) whenever new releases are available in accordance with the {{Licensee/Applicant}}''s configuration management policy and procedures.{{Licensee/Applicant}} documents and configures malicious code protection mechanisms to ensure the following:(1) Scans are performed of security boundary devices, CDAs (if applicable), workstations, servers, and mobile computing devices weekly and real-time scans of files from external sources are performed as the files are downloaded, opened, or executed.(2) Infected files are disinfected and quarantined.{{Licensee/Applicant}} documents and employs malicious code protection software products from multiple vendors as part of a defense-in-depth strategy and addresses the receipt of false positives during malicious code detection and eradication and the resulting potential impact on the availability of the CDA.{{Licensee/Applicant}} centrally manages malicious code protection mechanisms to achieve the following:(1) The CDAs prevent users from circumventing malicious code protection capabilities.(2) The CDAs update malicious code protection mechanisms only when directed by a privileged user.{{Licensee/Applicant}} does not allow users to introduce unauthorized removable media into the CDAs. {{Licensee/Applicant}} disables all media interfaces (e.g., USB ports) that are not required for the operation of the CDA.{{Licensee/Applicant}} documents and implements malicious code protection mechanisms to identify data containing malicious code and responds accordingly when CDAs encounter data not explicitly allowed by the security policy.' WHERE [Requirement_Id] = 1856
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}}''s system and information integrity procedures contain the following attributes:(1) detects malicious or suspicious access control or networking anomalies occurring at established defensive level boundaries and within security levels,(2) alerts appropriate staff to the detected malicious or suspicious activity using a secure communications mechanism that is protected from the network being monitored,(3) isolates and contains malicious activity,(4) neutralizes malicious activity,(5) centralizes logging of cyber security events to support correlations,(6) provides for secure monitoring and management of security mechanisms,(7) provides time synchronization for all security-related devices, and(8) provides high assurance that the physical and logical security of the monitoring network (or systems/CDAs) matches or exceeds, and differs from, the systems/CDAs or networks being monitored.' WHERE [Requirement_Id] = 1858
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) monitoring events on the CDAs,(2) detecting CDAs attacks,(3) detecting and blocking unauthorized connections,(4) retaining event logs in accordance with information retention requirements,(5) identifying unauthorized use of the CDAs, and(6) monitoring devices that are deployed to provide visibility across CDAs for the following capabilities:(a) to collect information to detect attacks, unauthorized behavior and access, and authorized access, and(b) to track specific types of transactions of interest to {{Licensee/Applicant}}.{{Licensee/Applicant}} heightens the level of monitoring activity whenever {{Licensee/Applicant}} or the U.S. Nuclear Regulatory Commission (NRC) determines that there is an indication of increased risk to the safety, security, or emergency operations of the site.{{Licensee/Applicant}} documents, interconnects, and configures individual intrusion detection tools into a plant wide intrusion detection system using common protocols.{{Licensee/Applicant}} tests cyber intrusion detection and prevention systems consistent with the timeframe defined in Nuclear Energy Institute (NEI) 03-12, Section 20.1, for intrusion detection systems, and before being placed back in service after each repair or inoperative state. {{Licensee/Applicant}} documents and employs automated tools to support near-real-time analysis of events.{{Licensee/Applicant}} documents and employs automated tools to integrate intrusion detection tools into access control and flow control mechanisms for rapid response to attacks by enabling reconfiguration of these mechanisms in support of attack isolation and elimination.{{Licensee/Applicant}} monitors, logs, and documents inbound and outbound communications for unusual or unauthorized activities or conditions. Monitoring capabilities provide real-time alerts when indications of compromise or potential compromise occur.{{Licensee/Applicant}} prevents users from circumventing intrusion detection and prevention capabilities.{{Licensee/Applicant}} notifies and documents incident response personnel of suspicious events and takes the least-disruptive actions to SSEP functions to investigate and terminate suspicious events.{{Licensee/Applicant}} documents and protects information obtained from intrusion monitoring tools from unauthorized access, modification, and deletion.{{Licensee/Applicant}} uses competent cyber security personnel to randomly test and document intrusion monitoring tools.{{Licensee/Applicant}} documents and makes provisions to ensure that encrypted traffic is visible to monitoring tools.{{Licensee/Applicant}} analyzes and documents outbound communications traffic at the external boundary of CDAs (i.e., system perimeter) and, at selected interior points within the CDAs infrastructure to discover anomalies.{{Licensee/Applicant}} ensures and documents that the use of monitoring tools and techniques does not adversely impact the functional performance of CDAs and that, where monitoring tools and techniques cannot be used, adequate alternate controls are in place to compensate.' WHERE [Requirement_Id] = 1862
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} does the following:(1) Perform assessments and scans for vulnerabilities in CDAs [no less frequently than once a quarter] and at random intervals in accordance with Section 4.1.3 of Appendix A to RG 5.71 and when new potential CDA vulnerabilities are reported or identified.(2) Employ vulnerability scanning tools and techniques that promote interoperability among tools and automating parts of the vulnerability management process by:(a) enumerating platforms, software flaws, and improper configurations,(b) formatting and making transparent checklists and test procedures, and(c) measuring vulnerability impacts.(3) Analyze vulnerability scan reports and remediate vulnerabilities within a time period that will provide high assurance that CDAs are protected from cyberattacks up to and including the DBT.(4) Eliminate similar vulnerabilities in other CDAs.(5) Employ vulnerability scanning tools that include the capability to update the list of cyber vulnerabilities scanned and update the list of CDA vulnerabilities scanned [monthly] and when new vulnerabilities are identified and reported.(6) Employ vulnerability scanning procedures that maximize the breadth and depth of coverage (i.e., CDA components scanned and vulnerabilities checked).(7) Discern and document what information associated with the CDA is discoverable by adversaries.(8) Perform security testing to determine the level of difficulty in circumventing the security controls of the CDA. [Testing methods include penetration testing, malicious user testing, and independent verification and validation].(9) Include privileged access authorization to CDAs for selected vulnerability scanning activities to facilitate more thorough scanning.(10) Employ automated mechanisms to compare the results of vulnerability scans over time to determine trends in CDA vulnerabilities and mitigation/flaw remediation activities.(11) Employ automated mechanisms to detect and notify authorized personnel of the presence of unauthorized software on CDAs.(12) Ensure that SSEP functions are not adversely impacted by the scanning process. Where this may occur, CDAs are removed from service or replicated (to the extent feasible) before scanning is conducted or be scheduled to occur during planned CDA outages whenever possible. Where {{Licensee/Applicant}} cannot conduct vulnerability scanning on a production CDA because of the potential for an adverse impact on SSEP functions, alternate controls (e.g., providing a replicated system or CDA to conduct scanning) are employed.The {{Licensee/Applicant}} reviews historic audit logs to determine if a vulnerability identified in the CDA has been previously exploited.' WHERE [Requirement_Id] = 1864
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) identifying and implementing standard naming conventions for identification of special dissemination, handling, or distribution instructions in compliance with a policy and set of procedures to ensure that sensitive information is protected from inadvertent disclosure and 10 CFR 73.21, "Protection of Safeguards Information: Performance Requirements," and(2) ensuring that CDAs are configured to mark hard and soft copy output using standard naming conventions to identify any special dissemination, handling, or distribution instructions (e.g., Security Related Information).' WHERE [Requirement_Id] = 1865
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} documents and requires that system developers and integrators of acquired CDAs create, implement, and document a security test and evaluation plan to ensure that the acquired products meet all specified security requirements that the products are free from known, testable vulnerabilities and malicious code by identifying and eliminating these following vulnerabilities and other vulnerabilities that may change with new technology:(1) weak, unproven, or nonstandard cryptographic modules, insecure network protocols for sensitive communications,(2) known insecure software components or libraries,(3) known vulnerabilities,(4) insecure configuration files or options that act to control features of the application,(5) inadequate or inappropriate use of access control mechanisms to control access to system resources,(6) inappropriate privileges being granted to users, processes, or applications,(7) weak authentication mechanisms,(8) improperly or failing to validate input and output data,(9) insecure or inadequate logging of system errors or security-related information,(10) inadequately bounded buffers,(11) format string vulnerabilities,(12) privilege escalation vulnerabilities,(13) unsafe database transactions,(14) unsafe use of native function calls,(15) hidden functions and vulnerable features embedded in the code,(16) implemented security features do not themselves act to increase the risk of security vulnerabilities, increase susceptibility to cyberattack, or reduce the reliability of design-basis functions.(17) use of unsupported or undocumented methods or functions, and(18) use of undocumented code or malicious functions that might allow either unauthorized access or use of the system or the system to behave beyond the system requirements.' WHERE [Requirement_Id] = 1867
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} protects against supply chain threats and vulnerability by employing the following list of measures to protect against supply chain threats to maintain the integrity of the CDAs that are acquired:(1) establishment of trusted distribution paths,(2) validation of vendors, and(3) requiring tamper proof products or tamper evident seals on acquired products.{{Licensee/Applicant}} performs an analysis for each product acquisition to determine that the product provides the security requirements necessary to address the security controls in Appendixes B and C to RG 5.71.{{Licensee/Applicant}} uses heterogeneity to mitigate vulnerabilities associated with the use of a single vendor''s product.' WHERE [Requirement_Id] = 1871
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:(1) approving, monitoring, and documenting the use of CDA maintenance tools,(2) inspecting and documenting maintenance tools (e.g., diagnostic and test equipment and mobile devices, such as laptops) carried into a facility by maintenance personnel for obvious improper modifications,(3) checking and documenting all media and mobile devices, such as laptops, containing diagnostic, CDA, and system and test programs or software for malicious code before the media or mobile device is used in or on a CDA,(4) controlling, preventing and documenting the unauthorized removal of maintenance equipment by one of the following:(a) verifying that there is no {{Licensee/Applicant}} information contained on the equipment and validating the integrity of the device before reintroduction into the facility,(b) sanitizing or destroying the equipment,(c) retaining the equipment within the facility, and(d) obtaining approval from an authority explicitly authorizing removal of the equipment from the facility, and(5) employing [automated/manual] mechanisms to restrict the use of maintenance tools to authorized personnel only and employing manual mechanisms only when CDAs or support equipment (e.g., laptops) cannot support automated mechanisms.' WHERE [Requirement_Id] = 1872
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization employs automated mechanisms to facilitate the monitoring and control of remote access methods.The organization uses cryptography to protect the confidentiality and integrity of remote access sessions.The information system routes all remote accesses through a limited number of managed access control points.The organization authorizes the execution of privileged commands and access to security-relevant information via remote access only for compelling operational needs and documents the rationale for such access in the security plan for the information system.The organization monitors for unauthorized remote connections to the information system {{Assignment: organization-defined frequency}}, and takes appropriate action if an unauthorized connection is discovered.The organization ensures that remote sessions for accessing [Assignment: organization-defined list of security functions and security-relevant information] employ [Assignment: organization-defined additional security measures] and are audited.The organization disables [Assignment: organization-defined networking protocols within the information system deemed to be non-secure] except for explicitly identified components in support of specific operational requirements.' WHERE [Requirement_Id] = 4908
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization employs automated mechanisms to enforce access restrictions and support auditing of the enforcement actions.The organization conducts audits of information system changes {{Assignment: organization-defined frequency}} and when indications so warrant to determine whether unauthorized changes have occurred.The information system prevents the installation of [Assignment: organization-defined critical software programs] that are not signed with a certificate that is recognized and approved by the organization.' WHERE [Requirement_Id] = 4977
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization physically allocates publicly accessible information system components to separate subnetworks with separate physical network interfaces.The information system prevents public access into the organization’s internal networks except as appropriately mediated by managed interfaces employing boundary protection devices.The organization limits the number of access points to the information system to allow for more comprehensive monitoring of inbound and outbound communications and network traffic.The organization:(a)   Implements a managed interface for each external telecommunication service; (b)   Establishes a traffic flow policy for each managed interface;(c)   Employs security controls as needed to protect the confidentiality and integrity of the information being transmitted;(d)   Documents each exception to the traffic flow policy with a supporting mission/business need and duration of that need;(e)   Reviews exceptions to the traffic flow policy {{Assignment: organization-defined frequency}}; and(f)    Removes traffic flow policy exceptions that are no longer supported by an explicit mission/business need.The information system at managed interfaces, denies network traffic by default and allows network traffic by exception (i.e., deny all, permit by exception).The information system prevents remote devices that have established a non-remote connection with the system from communicating outside of that communications path with resources in external networks.' WHERE [Requirement_Id] = 5184
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The information system detects unauthorized changes to software and information.The organization reassesses the integrity of software and information by performing {{Assignment: organization-defined frequency}} integrity scans of the information system.' WHERE [Requirement_Id] = 5238
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization:a.    Protects the information system from harm by considering mean time to failure for [Assignment: organization-defined list of information system components] in specific environments of operation; andb.    Provides substitute information system components, when needed, and a mechanism to exchange active and standby roles of the components.The organization takes the information system component out of service by transferring component responsibilities to a substitute component no later than [Assignment: organization-defined fraction or percentage] of mean time to failure.The organization does not allow a process to execute without supervision for more than [Assignment: organization-defined time period].The organization manually initiates a transfer between active and standby information system components at least once per {{Assignment: organization-defined frequency}} if the mean time to failure exceeds [Assignment: organization-defined time period].The organization, if an information system component failure is detected:(a)   Ensures that the standby information system component successfully and transparently assumes its role within [Assignment: organization-defined time period]; and(b)   [Selection (one or more): activates [Assignment: organization-defined alarm]; automatically shuts down the information system].' WHERE [Requirement_Id] = 5248
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization develops, disseminates, and reviews/updates {{Assignment: organization-defined frequency}}:a. A formal, documented access control policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; andb. Formal, documented procedures to facilitate the implementation of the access control policy and associated access controls.' WHERE [Requirement_Id] = 5249
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization manages information system accounts, including:a. Identifying account types (i.e., individual, group, system, application, guest/anonymous, and temporary);b. Establishing conditions for group membership;c. Identifying authorized users of the information system and specifying access privileges;d. Requiring appropriate approvals for requests to establish accounts;e. Establishing, activating, modifying, disabling, and removing accounts;f. Specifically authorizing and monitoring the use of guest/anonymous and temporary accounts;g. Notifying account managers when temporary accounts are no longer required and when information system users are terminated, transferred, or information system usage or need-to-know/need-to-share changes;h. Deactivating: (i) temporary accounts that are no longer required; and (ii) accounts of terminated or transferred users;i. Granting access to the system based on: (i) a valid access authorization; (ii) intended system usage; and (iii) other attributes as required by the organization or associated missions/business functions; andj. Reviewing accounts {{Assignment: organization-defined frequency}}.' WHERE [Requirement_Id] = 5250
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The information system supports and maintains the binding of {{Assignment: organization-defined security attributes}} to information in storage, in process, and in transmission.The information system dynamically reconfigures security attributes in accordance with an identified security policy as information is created and combined.The information system allows authorized entities to change security attributes.The information system maintains the binding of security attributes to information with sufficient assurance that the information--attribute association can be used as the basis for automated policy actions.The information system allows authorized users to associate security attributes with information.The information system displays security attributes in human-readable form on each object output from the system to system output devices to identify [Assignment: organization-identified set of special dissemination, handling, or distribution instructions] using [Assignment: organization-identified human-readable, standard naming conventions].' WHERE [Requirement_Id] = 5271
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The information system routes all remote accesses through a limited number of managed access control points.The organization authorizes the execution of privileged commands and access to security-relevant information via remote access only for compelling operational needs and documents the rationale for such access in the security plan for the information system.The organization monitors for unauthorized remote connections to the information system {{Assignment: organization-defined frequency}}, and takes appropriate action if an unauthorized connection is discovered.The organization ensures that remote sessions for accessing [Assignment: organization-defined list of security functions and security-relevant information] employ [Assignment: organization-defined additional security measures] and are audited.The organization disables [Assignment: organization-defined networking protocols within the information system deemed to be non-secure] except for explicitly identified components in support of specific operational requirements.' WHERE [Requirement_Id] = 5275
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization monitors for unauthorized wireless connections to the information system, including scanning for unauthorized wireless access points {{Assignment: organization-defined frequency}}, and takes appropriate action if an unauthorized connection is discovered.The organization does not allow users to independently configure wireless networking capabilities.The organization confines wireless communications to organization-controlled boundaries.' WHERE [Requirement_Id] = 5279
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization:a. Designates individuals authorized to post information onto an organizational information system that is publicly accessible;b. Trains authorized individuals to ensure that publicly accessible information does not contain nonpublic information;c. Reviews the proposed content of publicly accessible information for nonpublic information prior to posting onto the organizational information system;d. Reviews the content on the publicly accessible organizational information system for nonpublic information {{Assignment: organization-defined frequency}}; ande. Removes nonpublic information from the publicly accessible organizational information system, if discovered.' WHERE [Requirement_Id] = 5287
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization develops, disseminates, and reviews/updates {{Assignment: organization-defined frequency}}:a. A formal, documented security awareness and training policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; andb. Formal, documented procedures to facilitate the implementation of the security awareness and training policy and associated security awareness and training controls.' WHERE [Requirement_Id] = 5288
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization provides basic security awareness training to all information system users (including managers, senior executives, and contractors) as part of initial training for new users, when required by system changes, and {{Assignment: organization-defined frequency}} thereafter.' WHERE [Requirement_Id] = 5289
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization provides role-based security-related training: (i) before authorizing access to the system or performing assigned duties; (ii) when required by system changes; and (iii) {{Assignment: organization-defined frequency}} thereafter.' WHERE [Requirement_Id] = 5291
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization provides employees with initial and {{Assignment: organization-defined frequency}} training in the employment and operation of environmental controls.The organization provides employees with initial and {{Assignment: organization-defined frequency}} training in the employment and operation of physical security controls.' WHERE [Requirement_Id] = 5292
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization develops, disseminates, and reviews/updates {{Assignment: organization-defined frequency}}:a. A formal, documented audit and accountability policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; andb. Formal, documented procedures to facilitate the implementation of the audit and accountability policy and associated audit and accountability controls.' WHERE [Requirement_Id] = 5295
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization reviews and updates the list of auditable events {{Assignment: organization-defined frequency}}.The organization includes execution of privileged functions in the list of events to be audited by the information system.' WHERE [Requirement_Id] = 5297
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization:a. Reviews and analyzes information system audit records {{Assignment: organization-defined frequency}} for indications of inappropriate or unusual activity, and reports findings to designated organizational officials; andb. Adjusts the level of audit review, analysis, and reporting within the information system when there is a change in risk to organizational operations, organizational assets, individuals, other organizations, or the Nation based on law enforcement information, intelligence information, or other credible sources of information.' WHERE [Requirement_Id] = 5305
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The information system synchronizes internal information system clocks {{Assignment: organization-defined frequency}} with [Assignment: organization-defined authoritative time source].' WHERE [Requirement_Id] = 5310
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The information system produces audit records on hardware-enforced, write-once media.The information system backs up audit records {{Assignment: organization-defined frequency}} onto a different system or media than the system being audited.The information system uses cryptographic mechanisms to protect the integrity of audit information and audit tools.The organization:(a)   Authorizes access to management of audit functionality to only a limited subset of privileged users; and(b)   Protects the audit records of non-local accesses to privileged accounts and the execution of privileged functions.' WHERE [Requirement_Id] = 5312
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization develops, disseminates, and reviews/updates {{Assignment: organization-defined frequency}}:a.    Formal, documented security assessment and authorization policies that address purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; andb.    Formal, documented procedures to facilitate the implementation of the security assessment and authorization policies and associated security assessment and authorization controls.' WHERE [Requirement_Id] = 5321
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization:a.    Develops a security assessment plan that describes the scope of the assessment including:-  Security controls and control enhancements under assessment;-  Assessment procedures to be used to determine security control effectiveness; and-  Assessment environment, assessment team, and assessment roles and responsibilities;b.    Assesses the security controls in the information system {{Assignment: organization-defined frequency}} to determine the extent to which the controls are implemented correctly, operating as intended, and producing the desired outcome with respect to meeting the security requirements for the system;c.    Produces a security assessment report that documents the results of the assessment; andd.    Provides the results of the security control assessment, in writing, to the authorizing official or authorizing official designated representative.' WHERE [Requirement_Id] = 5322
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization:a.    Assigns a senior-level executive or manager to the role of authorizing official for the information system;b.    Ensures that the authorizing official authorizes the information system for processing before commencing operations; andc.    Updates the security authorization {{Assignment: organization-defined frequency}}.' WHERE [Requirement_Id] = 5329
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization employs an independent assessor or assessment team to monitor the security controls in the information system on an ongoing basis.The organization plans, schedules, and conducts assessments {{Assignment: organization-defined frequency}}, [Selection: announced; unannounced], [Selection: in-depth monitoring; malicious user testing; penetration testing; red team exercises; [Assignment: organization-defined other forms of security assessment]] to ensure compliance with all vulnerability mitigation procedures.' WHERE [Requirement_Id] = 5331
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization develops, disseminates, and reviews/updates {{Assignment: organization-defined frequency}}:a.    A formal, documented configuration management policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; andb.    Formal, documented procedures to facilitate the implementation of the configuration management policy and associated configuration management controls.' WHERE [Requirement_Id] = 5332
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization reviews and updates the baseline configuration of the information system: (a)   {{Assignment: organization-defined frequency}}; (b) When required due to [Assignment organization-defined circumstances]; and (c) As an integral part of information system component installations and upgrades.The organization retains older versions of baseline configurations as deemed necessary to support rollback.' WHERE [Requirement_Id] = 5334
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization:a.    Determines the types of changes to the information system that are configuration controlled;b.    Approves configuration-controlled changes to the system with explicit consideration for security impact analyses;c.    Documents approved configuration-controlled changes to the system;d.    Retains and reviews records of configuration-controlled changes to the system;e.    Audits activities associated with configuration-controlled changes to the system; andf.    Coordinates and provides oversight for configuration change control activities through [Assignment: organization-defined configuration change control element (e.g., committee, board)] that convenes [Selection: (one or more): {{Assignment: organization-defined frequency}}; [Assignment: organization-defined configuration change conditions]].The organization tests, validates, and documents changes to the information system before implementing the changes on the operational system.' WHERE [Requirement_Id] = 5337
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization conducts audits of information system changes {{Assignment: organization-defined frequency}} and when indications so warrant to determine whether unauthorized changes have occurred.' WHERE [Requirement_Id] = 5345
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization enforces a two-person rule for changes to [Assignment: organization-defined information system components and system-level information].The organization:(a)   Limits information system developer/integrator privileges to change hardware, software, and firmware components and system information directly within a production environment; and(b)   Reviews and reevaluates information system developer/integrator privileges {{Assignment: organization-defined frequency}}.The organization limits privileges to change software resident within software libraries (including privileged programs).The information system automatically implements [Assignment: organization-defined safeguards and countermeasures] if security functions (or mechanisms) are changed inappropriately.' WHERE [Requirement_Id] = 5347
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization reviews the information system {{Assignment: organization-defined frequency}} to identify and eliminate unnecessary functions, ports, protocols, and/or services.' WHERE [Requirement_Id] = 5354
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization employs automated mechanisms to help maintain an up-to-date, complete, accurate, and readily available inventory of information system components.The organization:(a)   Employs automated mechanisms {{Assignment: organization-defined frequency}} to detect the addition of unauthorized components/devices into the information system; and(b)   Disables network access by such components/devices or notifies designated organizational officials.The organization includes in property accountability information for information system components, a means for identifying by [Selection (one or more): name; position; role] individuals responsible for administering those components.' WHERE [Requirement_Id] = 5359
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization develops, disseminates, and reviews/updates {{Assignment: organization-defined frequency}}:a.    A formal, documented contingency planning policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; andb.    Formal, documented procedures to facilitate the implementation of the contingency planning policy and associated contingency planning controls.' WHERE [Requirement_Id] = 5363
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization:a.    Develops a contingency plan for the information system that:-  Identifies essential missions and business functions and associated contingency requirements;-  Provides recovery objectives, restoration priorities, and metrics;-  Addresses contingency roles, responsibilities, assigned individuals with contact information;-  Addresses maintaining essential missions and business functions despite an information system disruption, compromise, or failure;-  Addresses eventual, full information system restoration without deterioration of the security measures originally planned and implemented; and-  Is reviewed and approved by designated officials within the organization;b.    Distributes copies of the contingency plan to [Assignment: organization-defined list of key contingency personnel (identified by name and/or by role) and organizational elements];c.    Coordinates contingency planning activities with incident handling activities;d.    Reviews the contingency plan for the information system {{Assignment: organization-defined frequency}};e.    Revises the contingency plan to address changes to the organization, information system, or environment of operation and problems encountered during contingency plan implementation, execution, or testing; andf.    Communicates contingency plan changes to [Assignment: organization-defined list of key contingency personnel (identified by name and/or by role) and organizational elements].' WHERE [Requirement_Id] = 5364
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization trains personnel in their contingency roles and responsibilities with respect to the information system and provides refresher training {{Assignment: organization-defined frequency}}.' WHERE [Requirement_Id] = 5368
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization:a.    Tests and/or exercises the contingency plan for the information system {{Assignment: organization-defined frequency}} using [Assignment: organization-defined tests and/or exercises] to determine the plan’s effectiveness and the organization’s readiness to execute the plan; andb.    Reviews the contingency plan test/exercise results and initiates corrective actions.' WHERE [Requirement_Id] = 5371
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization develops, disseminates, and reviews/updates {{Assignment: organization-defined frequency}}:a.    A formal, documented identification and authentication policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; andb.    Formal, documented procedures to facilitate the implementation of the identification and authentication policy and associated identification and authentication controls.' WHERE [Requirement_Id] = 5390
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization develops, disseminates, and reviews/updates {{Assignment: organization-defined frequency}}:a.    A formal, documented incident response policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; andb.    Formal, documented procedures to facilitate the implementation of the incident response policy and associated incident response controls.' WHERE [Requirement_Id] = 5407
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization:a.    Trains personnel in their incident response roles and responsibilities with respect to the information system; andb.    Provides refresher training {{Assignment: organization-defined frequency}}.' WHERE [Requirement_Id] = 5408
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization tests and/or exercises the incident response capability for the information system {{Assignment: organization-defined frequency}} using [Assignment: organization-defined tests and/or exercises] to determine the incident response effectiveness and documents the results.' WHERE [Requirement_Id] = 5410
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization:a.    Develops an incident response plan that:-  Provides the organization with a roadmap for implementing its incident response capability;-  Describes the structure and organization of the incident response capability;-  Provides a high-level approach for how the incident response capability fits into the overall organization;-  Meets the unique requirements of the organization, which relate to mission, size, structure, and functions;-  Defines reportable incidents;-  Provides metrics for measuring the incident response capability within the organization.-  Defines the resources and management support needed to effectively maintain and mature an incident response capability; and-  Is reviewed and approved by designated officials within the organization;b.    Distributes copies of the incident response plan to [Assignment: organization-defined list of incident response personnel (identified by name and/or by role) and organizational elements];c.    Reviews the incident response plan {{Assignment: organization-defined frequency}};d.    Revises the incident response plan to address system/organizational changes or problems encountered during plan implementation, execution, or testing; ande.    Communicates incident response plan changes to [Assignment: organization-defined list of incident response personnel (identified by name and/or by role) and organizational elements].' WHERE [Requirement_Id] = 5423
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization develops, disseminates, and reviews/updates {{Assignment: organization-defined frequency}}:a.    A formal, documented information system maintenance policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; andb.    Formal, documented procedures to facilitate the implementation of the information system maintenance policy and associated system maintenance controls.' WHERE [Requirement_Id] = 5424
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization develops, disseminates, and reviews/updates {{Assignment: organization-defined frequency}}:a.    A formal, documented media protection policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; andb.    Formal, documented procedures to facilitate the implementation of the media protection policy and associated media protection controls.' WHERE [Requirement_Id] = 5438
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization tracks, documents, and verifies media sanitization and disposal actions.The organization tests sanitization equipment and procedures to verify correct performance {{Assignment: organization-defined frequency}}.The organization sanitizes portable, removable storage devices prior to connecting such devices to the information system under the following circumstances: [Assignment: organization-defined list of circumstances requiring sanitization of portable, removable storage devices].' WHERE [Requirement_Id] = 5449
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization develops, disseminates, and reviews/updates {{Assignment: organization-defined frequency}}:a.    A formal, documented physical and environmental protection policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; andb.    Formal, documented procedures to facilitate the implementation of the physical and environmental protection policy and associated physical and environmental protection controls.' WHERE [Requirement_Id] = 5451
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization:a.    Develops and keeps current a list of personnel with authorized access to the facility where the information system resides (except for those areas within the facility officially designated as publicly accessible);b.    Issues authorization credentials;c.    Reviews and approves the access list and authorization credentials {{Assignment: organization-defined frequency}}, removing from the access list personnel no longer requiring access.' WHERE [Requirement_Id] = 5452
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization performs security checks at the physical boundary of the facility or information system for unauthorized exfiltration of information or information system components.The organization guards, alarms, and monitors every physical access point to the facility where the information system resides 24 hours per day, 7 days per week.The organization uses lockable physical casings to protect [Assignment: organization-defined information system components] from unauthorized physical access.The information system detects/prevents physical tampering or alteration of hardware components within the system.The organization employs a penetration testing process that includes {{Assignment: organization-defined frequency}}, unannounced attempts to bypass or circumvent security controls associated with physical access points to the facility.' WHERE [Requirement_Id] = 5456
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization:a.    Monitors physical access to the information system to detect and respond to physical security incidents;b.    Reviews physical access logs {{Assignment: organization-defined frequency}}; andc.    Coordinates results of reviews and investigations with the organization’s incident response capability.' WHERE [Requirement_Id] = 5459
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization: a. Maintains visitor access records to the facility where the information system resides (except  for those areas within the facility officially designated as publicly accessible); and b. Reviews visitor access records {{Assignment: organization-defined frequency}}.' WHERE [Requirement_Id] = 5465
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization develops, disseminates, and reviews/updates {{Assignment: organization-defined frequency}}:a.    A formal, documented security planning policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; andb.    Formal, documented procedures to facilitate the implementation of the security planning policy and associated security planning controls.' WHERE [Requirement_Id] = 5487
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization: a. Develops a security plan for the information system that: - Is consistent with the organization’s enterprise architecture; - Explicitly defines the authorization boundary for the system; - Describes the operational context of the information system in terms of missions and business processes; - Provides the security category and impact level of the information system including supporting rationale; - Describes the operational environment for the information system; - Describes relationships with or connections to other information systems; - Provides an overview of the security requirements for the system; - Describes the security controls in place or planned for meeting those requirements including a rationale for the tailoring and supplementation decisions; and - Is reviewed and approved by the authorizing official or designated representative prior to plan implementation; b. Reviews the security plan for the information system {{Assignment: organization-defined frequency}}; and c. Updates the plan to address changes to the information system/environment of operation or problems identified during plan implementation or security control assessments.' WHERE [Requirement_Id] = 5488
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization: (a) Develops a security Concept of Operations (CONOPS) for the information system containing, at a minimum: (i) the purpose of the system; (ii) a description of the system architecture; (iii) the security authorization schedule; and (iv) the security categorization and associated factors considered in determining the categorization; and (b) Reviews and updates the CONOPS {{Assignment: organization-defined frequency}}.The organization develops a functional architecture for the information system that identifies and maintains: (a) External interfaces, the information being exchanged across the interfaces, and the protection mechanisms associated with each interface; (b) User roles and the access privileges assigned to each role; (c) Unique security requirements; (d) Types of information processed, stored, or transmitted by the information system and any specific protection needs in accordance with applicable federal laws, executive Orders, directives, policies, regulations, standards, and guidance; and (e) Restoration priority of information or information system services.' WHERE [Requirement_Id] = 5489
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization:a.    Develops and disseminates an organization-wide information security program plan that:-  Provides an overview of the requirements for the security program and a description of the security program management controls and common controls in place or planned for meeting those requirements;-  Provides sufficient information about the program management controls and common controls (including specification of parameters for any assignment and selection operations either explicitly or by reference) to enable an implementation that is unambiguously compliant with the intent of the plan and a determination of the risk to be incurred if the plan is implemented as intended;-  Includes roles, responsibilities, management commitment, coordination among organizational entities, and compliance;-  Is approved by a senior official with responsibility and accountability for the risk being incurred to organizational operations (including mission, functions, image, and reputation), organizational assets, individuals, other organizations, and the Nation;b.    Reviews the organization-wide information security program plan {{Assignment: organization-defined frequency}}; andc.    Revises the plan to address organizational changes and problems identified during plan implementation or security control assessments.' WHERE [Requirement_Id] = 5494
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization develops, disseminates, and reviews/updates {{Assignment: organization-defined frequency}}:a.    A formal, documented personnel security policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; andb.    Formal, documented procedures to facilitate the implementation of the personnel security policy and associated personnel security controls.' WHERE [Requirement_Id] = 5505
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization:a.    Assigns a risk designation to all positions;b.    Establishes screening criteria for individuals filling those positions; andc.    Reviews and revises position risk designations {{Assignment: organization-defined frequency}}.' WHERE [Requirement_Id] = 5506
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization:a.    Ensures that individuals requiring access to organizational information and information systems sign appropriate access agreements prior to being granted access; andb.    Reviews/updates the access agreements {{Assignment: organization-defined frequency}}.' WHERE [Requirement_Id] = 5511
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization develops, disseminates, and reviews/updates {{Assignment: organization-defined frequency}}:a.    A formal, documented risk assessment policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; andb.    Formal, documented procedures to facilitate the implementation of the risk assessment policy and associated risk assessment controls.' WHERE [Requirement_Id] = 5515
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization:a.    Conducts an assessment of risk, including the likelihood and magnitude of harm, from the unauthorized access, use, disclosure, disruption, modification, or destruction of the information system and the information it processes, stores, or transmits;b.    Documents risk assessment results in [Selection: security plan; risk assessment report; [Assignment: organization-defined document]];c.    Reviews risk assessment results {{Assignment: organization-defined frequency}}; andd.    Updates the risk assessment {{Assignment: organization-defined frequency}} or whenever there are significant changes to the information system or environment of operation (including the identification of new threats and vulnerabilities), or other conditions that may impact the security state of the system.' WHERE [Requirement_Id] = 5517
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization updates the list of information system vulnerabilities scanned {{Assignment: organization-defined frequency}} or when new vulnerabilities are identified and reported.The organization employs vulnerability scanning procedures that can demonstrate the breadth and depth of coverage (i.e., information system components scanned and vulnerabilities checked).The organization attempts to discern what information about the information system is discoverable by adversaries.The organization includes privileged access authorization to [Assignment: organization-identified information system components] for selected vulnerability scanning activities to facilitate more thorough scanning.The organization employs automated mechanisms {{Assignment: organization-defined frequency}} to detect the presence of unauthorized software on organizational information systems and notify designated organizational officials.' WHERE [Requirement_Id] = 5520
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization develops, disseminates, and reviews/updates {{Assignment: organization-defined frequency}}:a.    A formal, documented system and services acquisition policy that includes information security considerations and that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; andb.    Formal, documented procedures to facilitate the implementation of the system and services acquisition policy and associated system and services acquisition controls.' WHERE [Requirement_Id] = 5522
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization develops, disseminates, and reviews/updates {{Assignment: organization-defined frequency}}:a.    A formal, documented system and communications protection policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; andb.    Formal, documented procedures to facilitate the implementation of the system and communications protection policy and associated system and communications protection controls.' WHERE [Requirement_Id] = 5547
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization limits the number of access points to the information system to allow for more comprehensive monitoring of inbound and outbound communications and network traffic.The organization:(a)   Implements a managed interface for each external telecommunication service; (b)   Establishes a traffic flow policy for each managed interface;(c)   Employs security controls as needed to protect the confidentiality and integrity of the information being transmitted;(d)   Documents each exception to the traffic flow policy with a supporting mission/business need and duration of that need;(e)   Reviews exceptions to the traffic flow policy {{Assignment: organization-defined frequency}}; and(f)    Removes traffic flow policy exceptions that are no longer supported by an explicit mission/business need.The information system at managed interfaces, denies network traffic by default and allows network traffic by exception (i.e., deny all, permit by exception).The information system prevents remote devices that have established a non-remote connection with the system from communicating outside of that communications path with resources in external networks.' WHERE [Requirement_Id] = 5559
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization employs virtualization techniques to present information system components as other types of components, or components with differing configurations.The organization employs virtualization techniques to support the deployment of a diversity of operating systems and applications that are changed {{Assignment: organization-defined frequency}}.The organization employs randomness in the implementation of the virtualization techniques.' WHERE [Requirement_Id] = 5597
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization develops, disseminates, and reviews/updates {{Assignment: organization-defined frequency}}:a.    A formal, documented system and information integrity policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; andb.    Formal, documented procedures to facilitate the implementation of the system and information integrity policy and associated system and information integrity controls.' WHERE [Requirement_Id] = 5602
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization:a.    Employs malicious code protection mechanisms at information system entry and exit points and at workstations, servers, or mobile computing devices on the network to detect and eradicate malicious code:-  Transported by electronic mail, electronic mail attachments, web accesses, removable media, or other common means; or-  Inserted through the exploitation of information system vulnerabilities;b.    Updates malicious code protection mechanisms (including signature definitions) whenever new releases are available in accordance with organizational configuration management policy and procedures;c.    Configures malicious code protection mechanisms to:-  Perform periodic scans of the information system {{Assignment: organization-defined frequency}} and real-time scans of files from external sources as the files are downloaded, opened, or executed in accordance with organizational security policy; and-  [Selection (one or more): block malicious code; quarantine malicious code;  send alert to administrator; [Assignment: organization-defined action]] in response to malicious code detection; andd.    Addresses the receipt of false positives during malicious code detection and eradication and the resulting potential impact on the availability of the information system.' WHERE [Requirement_Id] = 5607
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The information system updates malicious code protection mechanisms only when directed by a privileged user.The organization does not allow users to introduce removable media into the information system. The organization tests malicious code protection mechanisms {{Assignment: organization-defined frequency}} by introducing a known benign, non-spreading test case into the information system and subsequently verifying that both detection of the test case and associated incident reporting occur, as required.' WHERE [Requirement_Id] = 5611
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization takes the information system component out of service by transferring component responsibilities to a substitute component no later than [Assignment: organization-defined fraction or percentage] of mean time to failure.The organization does not allow a process to execute without supervision for more than [Assignment: organization-defined time period].The organization manually initiates a transfer between active and standby information system components at least once per {{Assignment: organization-defined frequency}} if the mean time to failure exceeds [Assignment: organization-defined time period].The organization, if an information system component failure is detected:(a)   Ensures that the standby information system component successfully and transparently assumes its role within [Assignment: organization-defined time period]; and(b)   [Selection (one or more): activates [Assignment: organization-defined alarm]; automatically shuts down the information system].' WHERE [Requirement_Id] = 5632
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} developed, disseminated, and [annually] reviews and updates a formal, documented, “critical digital asset” (CDA) access control policy which addresses the purpose, scope, roles, responsibilities, management commitments, and internal coordination of such policy.{{Licensee/Applicant}} has also developed formal, documented procedures to facilitate the implementation of the access control policy and associated access security controls.' WHERE [Requirement_Id] = 5633
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- managing and documenting CDA accounts, including authorizing, establishing, activating, modifying, reviewing, disabling, and removing accounts,- reviewing CDA accounts in a manner consistent with the access control list provided in the [design control package, access control program, cyber security procedures] and initiating required actions on CDA accounts [no less frequently than once every 30 days],- requiring access rights to be job function based,- conducting reviews when as individuals job function changes to ensure that rights remain limited to the individuals job function,- reviewing and documenting CDA accounts at a maximum interval consistent with the most recent version of Nuclear Energy Institute (NEI) 03-12, “Security Plan, Training and Qualification Plan, and Safeguards Contingency Plan,” endorsed by the U.S. Nuclear Regulatory Commission (NRC), and- employing automated mechanisms that support CDA account management functions and enable CDA to automatically:- - - terminate temporary, guest, and emergency accounts [no less frequently than once every 30 days]- - - disable inactive accounts [no less frequently than once every 30 days]- - - create and protect audit records for account creation, deletion, and modification- - - document and notify system administrators of all account creation, deletion, and modification activities so that system administrators are aware of any account modifications and can investigate potential cyber-attacks in a timely manner.' WHERE [Requirement_Id] = 5635
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- enforcing assigned authorizations for controlling access to CDAs in accordance with established policies and procedures,- assigning all user rights and privileges on the CDA consistent with the user authorizations,- defining and documenting privileged functions and security-relevant information for the CDAs,- authorizing personnel access to privileged functions and security-relevant information consistent with established policies and procedures,- restricting access to privileged functions (deployed in hardware, software, and firmware) and security-relevant information to authorized personnel (e.g., security administrators),- defining and documenting privileged functions for CDAs,- requiring dual authorization for critical privileged functions and the creation of any privileged access for users, and- ensuring and documenting that access enforcement mechanisms do not adversely impact the operational performance of CDAs and employing alternate compensating security controls when access enforcement cannot be used.' WHERE [Requirement_Id] = 5636
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- enforcing and documenting assigned authorizations for controlling the flow of information, in near-real time, within CDAs and between interconnected systems in accordance with the established defensive strategy,- maintaining documentation that demonstrates that {{Licensee/Applicant}} has analyzed and addressed the types of permissible and impermissible flow of information between CDAs, security boundary devices, and boundaries and the required level of authorization to allow information flow as defined in the defensive strategy,- implementing and documenting information flow control enforcement using protected processing level (e.g., domain type-enforcement) as a basis for flow control decisions,- implementing near-real time capabilities to detect, deter, prevent, and respond to illegal or unauthorized information flows,- preventing encrypted data from bypassing content-checking mechanisms,- implementing one-way data flows using hardware mechanisms,- implementing dynamic information flow control based on policy that allows or disallows information flows based on changing conditions or operational considerations, and- configuring CDAs such that user credentials are not transmitted in clear text and documenting this requirement in the access control policy.' WHERE [Requirement_Id] = 5637
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- establishing and documenting divisions of responsibility and separating functions as needed to eliminate conflicts of interest and to ensure independence in the responsibilities and functions of individuals,- enforcing separation of CDA functions through assigned access authorizations,- implementing alternative controls and documenting the justification for alternative controls and countermeasures for increased auditing for those situations in which a CDA cannot support the differentiation of roles and a single individual must perform all roles within the CDA, and- restricts security functions to the least amount of users necessary to ensure the security of CDAs.' WHERE [Requirement_Id] = 5638
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- assigning the most restrictive set of rights and privileges or access needed by users for the performance of specified tasks,- configuring CDAs to enforce the most restrictive set of rights and privileges or access needed by users, and- implementing alternative controls and documenting the justification for alternative controls and countermeasures for increased auditing for situations in which a CDA cannot support the differentiation of privileges within the CDA and an individual must perform all roles within the CDA.' WHERE [Requirement_Id] = 5639
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} ensures the following:- Security controls are implemented to limit the number of invalid access attempts by a user. The access control policy documents this requirement. The number of failed login attempts in a specified time period may vary by CDA. For example, more than three invalid attempts within a 1-hour time period will automatically lock out the account. The {{Licensee/Applicant}} system enforces the lock out mode automatically.- The access control policy includes a requirement that only authorized individuals, who are not the user, can unlock accounts once the maximum number of unsuccessful login attempts has been exceeded. Alternatively, other verification techniques or mechanisms that incorporate identity challenges are used.- The access control policy documents the justification and details for alternative controls or countermeasures for those instances in which a CDA cannot support account/node locking or delayed login attempts. If a CDA cannot perform account/node locking or delayed logins because of significant adverse impact on performance, safety, or reliability, the{{Licensee/Applicant}} employs alternative controls or countermeasures that include the following:- - - real-time logging and recording of unsuccessful login attempts, and- - - real-time alerting of designated personnel with the security expertise for the CDA through alarms when the number of defined consecutive invalid access attempts is exceeded.' WHERE [Requirement_Id] = 5640
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} ensures the following:- A “system use notification” message is displayed before granting system access informing potential users of the following:- - - The user is accessing a restricted system.- - - System usage is monitored, recorded, and subject to audit.- - - Unauthorized use of CDA is prohibited and subject to criminal and civil penalties. Theuse of CDAs indicates consent to monitoring and recording.- The CDA system use notification message provides privacy and security notices.- The CDA system use notification message is approved before its use.- The CDA system use notification message remains on the screen until the user takes explicit actions to log on to the CDA.- Physical notices are installed in those instances in which a CDA cannot support system use notifications.' WHERE [Requirement_Id] = 5641
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- upon successful logon, configuring CDA to display the date and time of the last logon and the number of unsuccessful logon attempts since the last successful logon, and- requiring all end users to report any suspicious activity to the Cyber Security Program manager.' WHERE [Requirement_Id] = 5642
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} configures CDAs to do the following:- initiate a session lock after [within 30 minutes of inactivity],- provide the capability for users to directly initiate session lock mechanisms,- maintain the session lock on a CDA until the user reestablishes access using identification and authentication procedures, and- implement alternative controls and document the justification for alternative controls or countermeasures for those instances in which a CDA cannot support session locks and:- - - physically restrict access to the CDA,- - - monitor and record physical access to the CDA to detect and respond to intrusions in a timely manner,- - - use auditing or validation measures (e.g., security guard rounds, periodic monitoring of tamper seals) to detect unauthorized access and modifications to the CDAs,- - - ensure that individuals who have access to the CDA are qualified, and- - - ensure that those individuals are trustworthy and reliable, in accordance with 10 CFR 73.56.' WHERE [Requirement_Id] = 5643
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- documenting, supervising, and reviewing the activities of users with respect to the enforcement and usage of access controls, and- employing automated mechanisms within CDAs to support and facilitate the review of user activities.' WHERE [Requirement_Id] = 5644
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- identifying and documenting specific user actions that can be performed on CDAs during normal and emergency conditions without identification or authentication, and- permitting actions to be performed without identification and authentication only to the extent necessary to accomplish mission objectives, without adversely affecting safety, security, and emergency preparedness (SSEP) functions, and in a manner consistent with NRC regulations.' WHERE [Requirement_Id] = 5645
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- identifying and implementing standard naming conventions for identification of special dissemination, handling, or distribution instructions in compliance with a policy and set of procedures to ensure that sensitive information is protected from inadvertent disclosure and 10 CFR 73.21, “Protection of Safeguards Information: Performance Requirements,” and- ensuring that CDAs are configured to mark hard and soft copy output using standard naming conventions to identify any special dissemination, handling, or distribution instructions (e.g., Security Related Information).' WHERE [Requirement_Id] = 5646
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} labels hard and soft copy information in storage, in process, and in transmission.' WHERE [Requirement_Id] = 5647
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} employs and documents mitigation techniques to secure CDAs through [media access control address locking, physical or electrical isolation, static tables, encryption, or monitoring].' WHERE [Requirement_Id] = 5648
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- documenting and implementing additional precautions to protect networks and bus communications from unauthorized access when protocols lack security controls,- prohibiting the protocols from initiating commands except within the same boundary, and- prohibiting these protocols from initiating commands that could change the state of the CDA from a more secured posture to a less secured posture.' WHERE [Requirement_Id] = 5649
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- only allowing wireless access through a boundary security control device and treating wireless connections as outside of the security boundary,- prohibiting the use of wireless technologies for CDAs associated with safety-related and important-to-safety functions,- disabling wireless capabilities when not utilized,- establishing usage restrictions and implementation guidance for wireless technologies,- documenting, justifying, authorizing, monitoring, and controlling wireless access to CDAs and ensuring that the wireless access restrictions are consistent with defensive strategies and defensive models, as articulated in RG 5.71, and- conducting scans [no less frequently than once every week] for unauthorized wireless access points, in accordance with this document, and disabling access points if unauthorized access points are discovered.' WHERE [Requirement_Id] = 5650
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} verifies that, during deployment of CDAs, when changes or modifications have been made to CDAs, and [no less frequently than once every month], CDAs are free of insecure and rogue connections such as vendor connections and modems.' WHERE [Requirement_Id] = 5651
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- establishing and documenting usage restrictions and implementation guidance for controlled portable and mobile devices,- authorizing, monitoring, and controlling device access to CDAs,- enforcing and documenting that mobile device security and integrity are maintained at a level consistent with the CDA they support, and- enforcing and documenting that mobile devices are only used in one security level and that mobile devices are not moved between security levels.' WHERE [Requirement_Id] = 5652
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} ensures that, when proprietary protocols that create a lack of visibility are used (e.g., systems cannot detect attacks because the protocol is proprietary), alternative controls or countermeasures are implemented to protect the CDAs from cyber-attack up to and including the design-basis threat (DBT).' WHERE [Requirement_Id] = 5653
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} ensures that for situations in which (1) third-party security solutions are not allowed because of vendor license and service agreements and (2) loss of service support would occur if third-party applications were to be installed without vendor acknowledgement or approval, alternative controls or countermeasures are implemented to mitigate vulnerabilities created by the lack of security functions provided by third-party products.' WHERE [Requirement_Id] = 5654
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- ensuring that external systems cannot be accessed from higher levels, such as Levels 4 and 3,- prohibiting external systems from accessing CDAs in Levels 3 and 4, and- prohibiting users from using an external system to access CDAs or to process, store, or transmit organization-controlled information except in situations in which {{Licensee/Applicant}} verifies the implementation of equivalent security measures on the external system.' WHERE [Requirement_Id] = 5655
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- designates individuals authorized to post information onto a {{Licensee/Applicant}} system that is publicly accessible;- trains authorized individuals to ensure that publicly accessible information does not contain information that could cause an adverse impact on SSEP functions or could assist an adversary in carrying out an attack;- ensuring that information that could cause an adverse impact on SSEP functions or could assist an adversary in carrying out an attack is not released to the public,' WHERE [Requirement_Id] = 5656
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} developed, disseminated, and [annually] reviews and updates the following while using an independent party for the audit reviews:- a formal, documented audit and accountability policy that addresses the purpose, scope, roles, responsibilities, management commitments, and internal coordination of the policy, and- formal, documented procedures that facilitate the implementation of the audit and accountability policy and associated audit and accountability security controls.' WHERE [Requirement_Id] = 5657
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- determining and documenting with SSEP functions those CDAs related events that require auditing,- defining the list of auditable events and frequency of auditing for each identified auditable event,- at a minimum, auditing all CDA connections, user login/logouts, configuration/software/firmware changes, audit setting changes, privileged access, privileged commands, and any modifications of the security functions of CDAs.- implementing alternative controls and documenting the justification for alternative controls and countermeasures for situations in which a CDA cannot support the use of automated mechanisms to generate audit records and employs non-automated mechanisms and procedures.' WHERE [Requirement_Id] = 5658
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- reviewing and updating the list of defined auditable events [no less frequently than once a year],- including execution of privileged functions in the list of events to be audited by the CDAs,- preventing CDAs from purging audit event records on restart,- coordinating security audit functions within the facility to enhance mutual support and to help guide the selection of auditable events,- configuring all CDAs so that auditable events are adequate to support after-the-fact investigations of security incidents, and- adjusting the events to be audited within the CDAs based on current threat information and effectiveness analysis described in Section 4.1.2 of Appendix A to RG 5.71.' WHERE [Requirement_Id] = 5659
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- ensuring that CDAs produce audit records that contain sufficient information to establish what events occurred, when the events occurred, where the events occurred, the sources of the events, and the outcomes of the events;- ensuring that CDAs provide the capability to include additional, more detailed information in the audit records for audit events identified by type, location, or subject; and- implementing architecture that provides the capability to centrally manage the content of audit records generated by individual components throughout CDAs, and to prevent CDAs from altering or destroying audit records.' WHERE [Requirement_Id] = 5660
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} allocates audit record storage capacity, meets NRC record retention requirements, and configures auditing to reduce the likelihood of such capacity being exceeded.' WHERE [Requirement_Id] = 5661
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} ensures the following:- CDAs provide a warning when allocated audit record storage volume reaches a defined percentage of maximum audit record storage capacity, which is based on [the function of how quickly storage capacity is consumed and what the organization''s resources and response times are] and documented.- Justification and details for alternate compensating security controls are documented for those instances in which a CDA cannot respond to audit processing failures.- Responses to audit failures by the {{Licensee/Applicant}} include the use of an external system to provide these capabilities.- If audit processing capabilities fail for a CDA or security boundary device, the following occurs:- - - Alerts are sent to designated {{Licensee/Applicant}} officials in the event of an audit processing failure.- - - Auditing failures are treated as a failure of the CDA or security boundary device and {{Licensee/Applicant}} will take action in accordance with the technical specification.- - - CDAs with auditing failures take the following additional actions:- - --- Shut down the CDA.- - --- Failover to a redundant CDA where necessary to prevent adverse impact to safety, security, or emergency preparedness functions.- - --- Overwrite only the oldest audit records.- - --- Stop generating audit records.' WHERE [Requirement_Id] = 5662
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- reviewing and analyzing the CDA audit records [no less frequently than once every 30 days] for indications of inappropriate or unusual activity and reporting findings to designated {{Licensee/Applicant}} official,- adjusting the level of audit review, analysis, and reporting within the CDAs when there is a change in threat or risk to {{Licensee/Applicant}} safety, security, and emergency preparedness functions based on credible sources of information as designated by {{Licensee/Applicant}} or the NRC, and- employing automated mechanisms on CDAs to integrate audit review, analysis, and reporting into {{Licensee/Applicant}} processes for investigation and response to suspicious activities.' WHERE [Requirement_Id] = 5663
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} has configured and deployed all CDA to do the following:- provide CDA audit reduction and report generation capability, and- provide the capability to automatically process audit records for events of interest based upon selectable event criteria.{{Licensee/Applicant}} documents the justification and details for alternate compensating security controls for situations in which a CDA cannot support auditing reduction and report generation by providing this capability through a separate system.' WHERE [Requirement_Id] = 5664
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} CDAs use a time source protected at an equal or greater level than the CDAs or an internal system clocks to generate time stamps for audit records, and {{Licensee/Applicant}} synchronizes the time on all CDAs.{{Licensee/Applicant}} synchronizes the time of all CDAs from a dedicated source protected at an equal or greater level than the CDA existing on the security network, attached directly to the CDA or via SNTP and a trusted key management process.{{Licensee/Applicant}} implements only methods of time synchronization that do not introduce a vulnerability to cyber-attack and/or common-mode failure and implements alternative controls to manage potential cyber security risks when time synchronization cannot be used for a CDA.' WHERE [Requirement_Id] = 5665
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- protecting audit information and audit tools from unauthorized access, modification, and deletion in a manner consistent with the CDA sources, and- ensuring that all audit information is protected at the same level as the device sources.' WHERE [Requirement_Id] = 5666
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} protects CDAs and audit records against an individual falsely denying they performed a particular action.' WHERE [Requirement_Id] = 5667
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} retains audit records consistent with the recordkeeping requirements for the access authorization program to provide support for after-the-fact investigations of security incidents and to meet regulatory and {{Licensee/Applicant}} record retention requirements.' WHERE [Requirement_Id] = 5668
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} security architecture provides the following:- audit record generation capability for the auditable events on CDAs,- audit record generation capability and the capability for authorized users to select which auditable events are to be audited by specific components of CDAs,- audit records for the selected list of auditable events on CDAs, and- the capability to compile audit records from multiple components within CDAs into a site wide (logical or physical) audit trail that is time correlated to within {{Licensee/Applicant}} defined level of tolerance for the relationship between time stamps of individual records in the audit trail.' WHERE [Requirement_Id] = 5669
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} developed, disseminated, and [annually] reviews and updates the following:- a formal, documented CDA system and communications protection policy that addresses the purpose, scope, roles, responsibilities, management commitments, and internal coordination of the system, and- formal, documented procedures that facilitate the implementation of the CDA system and communications protection policy and associated CDA system and communications protection security controls.' WHERE [Requirement_Id] = 5670
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- configuring CDAs to separate applications into user functionality (including user interface services) and CDA management functionality,- configuring CDAs to isolate security functions from non-security functions, which is accomplished through [partitions, domains, etc.], including control of access to and integrity of the hardware, software, and firmware that perform these security functions,- configuring CDAs to employ underlying hardware separation mechanisms to facilitate security function isolation,- configuring CDAs to isolate critical security functions (i.e., functions enforcing access and information flow control) from both non-security functions and other security functions,- configuring CDAs to minimize the number of non-security functions included within the isolation boundary containing security functions,- configuring CDA security functions as independent modules that avoid unnecessary interactions between modules,- configuring CDA security functions as a layered structure minimizing interactions between levels of the design and avoiding any dependence by lower levels on the functionality or correctness of higher levels, and- implementing alternative controls and documenting the justification for alternative controls or countermeasures for situations in which a CDA cannot support security function isolation and taking all of the following actions:- - - physically restrict access to the CDA,- - - monitor and record physical access to the CDA to detect and respond to intrusions in a timely manner,- - - use auditing/validation measures (e.g., security guard rounds, periodic monitoring of tamper seals) to detect unauthorized access and modifications to the CDAs,- - - ensure that individuals who have access to the CDAs are qualified, and- - - ensure that those individuals are trustworthy and reliable in accordance with 10 CFR 73.56.' WHERE [Requirement_Id] = 5671
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- configuring CDAs to prevent unauthorized and unintended information transfer via shared system resources, and- using physically separate network devices to create and maintain logical separation of Levels 3 and 4 from each other and from all other levels.' WHERE [Requirement_Id] = 5672
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- configuring CDAs to protect against or limit the effects of denial of service attacks,- configuring CDAs to restrict the ability of users to launch denial of service attacks against other CDAs or networks, and- configuring CDAs to manage excess capacity, bandwidth, or other redundancy to limit the effects of information-flooding and saturation types of denial-of-service attacks.' WHERE [Requirement_Id] = 5673
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} configures CDAs to limit the use of resources by priority by preventing lower priority processes from delaying or interfering with the servicing of any higher priority process.' WHERE [Requirement_Id] = 5674
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- configuring CDAs to protect the integrity of transmitted information,- employing cryptographic mechanisms to recognize changes to information during transmission and upon receipt, unless otherwise protected by alternative physical measures,- implementing mechanisms to prevent “man-in-the-middle” (MITM) attacks via the following methods:- - - Media Access Control Address Locking—{{Licensee/Applicant}} locks devices and ports via address locking to prevent MITM attacks and rogue devices from being added to the network- - - Network Access Control—{{Licensee/Applicant}} implements network access control to prevent MITM attacks and rogue devices from being added to the network,- implementing monitoring to detect MITM and address resolution protocol poisoning, and- implementing alternative controls and documenting the justification for alternative controls or countermeasures for situations in which a CDA cannot support transmission integrity andimplements all of the following:- - - physically restricts access to the CDA,- - - monitors and records physical access to the CDA to detect and respond to intrusions in a timely manner,- - - uses auditing/validation measures (e.g., security guard rounds, periodic monitoring of tamper seals) to detect unauthorized access and modifications to the CDAs,- - - ensures that individuals who have access to the CDA are qualified, and- - - ensures that those individuals are trustworthy and reliable in accordance with 10 CFR 73.56.' WHERE [Requirement_Id] = 5675
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- configuring the CDAs to protect the confidentiality of transmitted information,- employing cryptographic mechanisms to prevent unauthorized disclosure of information during transmission and receipt unless otherwise protected by alternative physical measures, and- implementing alternative controls and documenting the justification for alternative controls or countermeasures for situations in which a CDA cannot internally support transmission confidentiality capabilities, including virtual private networks, or implements all of the following:- - - physically restricts access to the CDA,- - - monitors and records physical access to the CDA to detect and respond to intrusions in a timely manner,- - - uses auditing/validation measures (e.g., security guard rounds, periodic monitoring of tamper seals) to detect unauthorized access and modifications to the CDAs,- - - ensures that individuals who have access to the CDA are qualified, and- - - ensures that those individuals are trustworthy and reliable in accordance with 10 CFR 73.56.' WHERE [Requirement_Id] = 5676
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} configures CDAs to use trusted communication paths between the user and the security functions of the CDAs, which includes authentication and re-authentication, at a minimum.' WHERE [Requirement_Id] = 5677
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} manages cryptographic keys using automated mechanisms with supporting procedures or manual procedures when cryptography is required and employed within the CDAs in accordance with [Federal Information Processing Standards (FIPS)140-2 Security Requirements for Cryptographic Modules].' WHERE [Requirement_Id] = 5678
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} configures CDAs to implement cryptographic mechanisms that comply with [Federal Information Processing Standards (FIPS)140-2 Security Requirements for Cryptographic Modules].' WHERE [Requirement_Id] = 5679
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- configuring CDAs to prohibit remote activation of collaborative computing mechanisms and providing an explicit indication of use to the local user, and- configuring CDAs to provide physical disconnection of cameras and microphones in a manner that supports ease of use, except when these technologies are used to control and monitor the CDA for security purposes.' WHERE [Requirement_Id] = 5680
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} configures CDAs to associate security parameters with information exchanged between CDAs.' WHERE [Requirement_Id] = 5681
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} issues public key certificates under a certificate policy or obtains public key certificates under a certificate policy from a provider approved by {{Licensee/Applicant}}.' WHERE [Requirement_Id] = 5682
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- establishing usage restrictions and implementation guidance for mobile code technologies based on their potential to cause damage to CDAs if used maliciously, and- authorizing, monitoring, and controlling the use of mobile code within the CDAs.' WHERE [Requirement_Id] = 5683
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- configuring systems that provide name/address resolution to supply additional data origin and integrity artifacts along with the authoritative data returned in response to resolution queries, and- configuring systems that provide name/address resolution to CDAs, when operating as part of a distributed, hierarchical namespace, to provide the means to indicate the security status of child subspaces and, if the child supports secure resolution services, enabled verification of a chain of trust among parent and child domains.' WHERE [Requirement_Id] = 5684
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- configuring the systems that serve name/address resolution service for CDAs to perform data origin authentication and data integrity verification on the resolution response they receive from authoritative sources, and- configuring CDAs so that, upon receipt of data, they perform data origin authentication and data integrity verification on resolution responses whether or not the CDAs explicitly request this service.' WHERE [Requirement_Id] = 5685
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} configures the systems that collectively provide name/address resolution service for a logical organization to be fault tolerant and segregate services (i.e., implement role separation).' WHERE [Requirement_Id] = 5686
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} configures CDAs to provide mechanisms to protect the authenticity of communications sessions.' WHERE [Requirement_Id] = 5687
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} configures CDAs and consoles to employ processing components that have minimal functionality and data storage.' WHERE [Requirement_Id] = 5688
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} configures CDAs to protect the confidentiality of information at rest.' WHERE [Requirement_Id] = 5689
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} employs diverse technologies in the implementation of CDAs.' WHERE [Requirement_Id] = 5690
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- CDAs fail in a known-state to ensure that SSEP functions are not adversely impacted by the CDAs failure, and- to prevent a loss of confidentiality, integrity, or availability in the event of a failure of the CDA or a component of the CDA.' WHERE [Requirement_Id] = 5691
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} developed, disseminated, and [annually] reviews and updates the following:- a formal, documented identification and authentication policy, which addresses purpose, scope, roles, responsibilities, management commitments, and internal coordination, to positively identify potential network users, hosts, applications, services, and resources using a combination of identification factors or credentials, and- formal, documented procedures that facilitate the implementation of the identification and authentication policy and associated identification and authentication controls.' WHERE [Requirement_Id] = 5692
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- implementing identification and authentication technology to uniquely identify and authenticate individuals and processes acting on behalf of users interacting with CDA and ensuring that CDAs, security boundary devices, physical controls of the operating environment, and individuals interacting with CDAs, are uniquely identified and authenticated and that all processes acting on behalf of users are equally authenticated and identified,- ensuring that the authentication technology employs strong multifactor authentication using protected processing levels,- implementing alternative controls and documenting the justification for alternative controls or countermeasures for situations in which a CDA cannot support user identification and authentication and implementing all of the following:- - - physically restricting access to the CDA,- - - monitoring and recording physical access to the CDA to detect and respond to intrusions in a timely manner,- - - using auditing/validation measures (e.g., security guard rounds, periodic monitoring of tamper seals) to detect unauthorized access and modifications to the CDAs,- - - ensuring that individuals who have access to the CDA are qualified, and- - - ensuring that those individuals are trustworthy and reliable in accordance with 10 CFR 73.56,' WHERE [Requirement_Id] = 5694
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- implementing secure domain-based authentication, as well as the following:- - - maintaining domain controllers within the given security level they are meant to service,- - - physically and logically securing domain controllers to prevent unauthorized access and manipulation,- - - prohibiting domain trust relationships between domains that exist at different security levels,- - - prohibiting domain authentication protocols from being passed between boundaries, and- - - implementing role-based access control where possible to restrict user privileges to only those required to perform the task, and- where domain-based authentication is not used, {{Licensee/Applicant}} is responsible for the following:- - - documenting and justifying the reason for not implementing secure domain-based authentication,- - - implementing localized authentication when feasible,- - - implementing the strongest possible challenge-response authentication mechanism within a scenario, as supported by the application, and- - - implementing role-based access control where possible to restrict user privileges to only those required to perform the task.' WHERE [Requirement_Id] = 5695
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} ensures that, where used, passwords meet the following requirements:- The length, strength, and complexity of passwords balance security and operational ease of access within the capabilities of the CDA.- Passwords have length and complexity commensurate with the required security.- Passwords are changed every [describe the periods for each class of system, for example 30 days for workstations, 3 months for CDAs in the vital area, etc. 90 days].- Passwords cannot be found in a dictionary and do not contain predictable sequences of numbers or letters.- Copies of master passwords are stored in a secure location with limited access.- Authority to change master passwords is limited to authorized personnel.' WHERE [Requirement_Id] = 5696
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- ensuring that, for those situations in which a human machine interaction (HMI) for a CDA cannot support authentication because of operational requirements, adequate physical security controls exist that require that operators are both authorized and properly identified and are monitored so that operator actions are audited and recorded,- controlling access to non-authenticated human machine interactions (NHMI) so as to not hamper HMI while maintaining security of the NHMI and ensuring that access to the NHMI is limited to only authorized personnel,- verifying that SSEP functions are not adversely affected by authentication, session lock, or session termination controls, and- implementing auditing capability on NHMIs to ensure that all operator activity is recorded and monitored by authorized and qualified personnel and maintaining historical records to provide for auditing requirements.' WHERE [Requirement_Id] = 5697
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- implementing and documenting technology that identifies and authenticates devices (i.e., tester) before those devices establish connections to CDAs, and- implementing alternative controls and documenting the justification for alternative controls or countermeasures for situations in which a CDA cannot support device identification and authentication (e.g., serial devices) and implementing all of the following:- - - physically restricting access to the CDA,- - - monitoring and recording physical access to the CDA to detect and respond to intrusions in a timely manner,- - - using auditing/validation measures (e.g., security guard rounds, periodic monitoring of tamper seals) to detect unauthorized access and modifications to the CDA,- - - ensuring that individuals who have access to the CDA are qualified, and- - - ensuring that those individuals are trustworthy and reliable in accordance with 10 CFR 73.56.' WHERE [Requirement_Id] = 5698
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} manages and documents user identifiers by performing all of the following:- uniquely identifying each user,- verifying the identity of each user,- receiving authorization to issue a user identifier from an organization official,- issuing the user identifier to the intended party,- disabling the user identifier after a maximum of [30 days] of inactivity, and- archiving user identifiers consistent with records retention for the access authorization program.' WHERE [Requirement_Id] = 5699
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} manages CDA authenticators by performing all of the following:- defining initial authenticator content, such as defining password length and composition, tokens, keys, and other means of authenticating,- establishing administrative procedures for initial authenticator distribution; lost, compromised, or damaged authenticators; and revoking authenticators,- changing default authenticators upon CDA installation, and- changing/refreshing authenticators [annually].' WHERE [Requirement_Id] = 5700
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- ensuring that CDAs obscure feedback of authentication information during the authentication process to protect the information from possible exploitation or use by unauthorized individuals, and- ensuring that CDAs and feedback from CDA do not provide information that would allow an unauthorized user to compromise the authentication mechanism.' WHERE [Requirement_Id] = 5701
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} ensures that CDAs authenticate cryptographic modules in accordance with [Federal Information Processing Standards (FIPS)140-2 Security Requirements for Cryptographic Modules].' WHERE [Requirement_Id] = 5702
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} documents all required applications, utilities, system services, scripts, configuration files, databases, and other software and the appropriate configurations, including revisions or patch levels, for each of the computer systems associated with the CDAs.{{Licensee/Applicant}} maintains a list of services required for CDAs. The listing includes all necessary ports and services required for normal and emergency operations. The listing also includes an explanation or cross reference to justify why each service is necessary for operation. Only those services and programs that are necessary for operation are allowed.{{Licensee/Applicant}} verifies and documents that all CDAs are patched or mitigated in accordance with the Flaw Remediation security controls in C 3.2.{{Licensee/Applicant}} documents the remediation period appropriate for software and service updates or workarounds to mitigate all vulnerabilities associated with the product and to maintain the established level of security.{{Licensee/Applicant}} documents the operating system and software patches as CDAs evolve to allow traceability and verifies that no extra services are reinstalled or reactivated.{{Licensee/Applicant}} removes or disables software components that are not required for the operation and maintenance of the CDA before incorporating the CDA into the production environment.{{Licensee/Applicant}} documents components that were removed or disabled. The software removed or disabled includes, but is not limited to the following:- device drivers for network devices not delivered,- device drivers for unused peripherals,- messaging services (e.g., MSN, AOL IM),- servers or clients for unused services,- software compilers in all user workstations and servers except for development workstations and servers,- software compilers for languages that are not used in the control system,- unused networking and communications protocols,- unused administrative utilities, diagnostics, network management, and system management functions,- backups of files, databases, and programs used only during system development,- all unused data and configuration files,- sample programs and scripts,- unused document processing utilities (e.g., Microsoft Word, Excel, Power Point, Adobe Acrobat, OpenOffice),- unused removable media support, and- games.' WHERE [Requirement_Id] = 5703
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} establishes, implements, and documents the following requirements:- Configure the host intrusion detection system (HIDS) to include attributes, such as static file names, dynamic file name patterns, system and user accounts, execution of unauthorized code, host utilization, and process permissions, to enable the system to detect cyber-attacks up to and including the DBT.- Configure HIDS to log system and user account connections in such a way that the user or security personnel are alerted if an abnormal situation occurs.- Configure the HIDS in a manner that does not adversely impact the CDA safety, security, and emergency preparedness functions.- Configure security logging storage devices as “append only” to prevent alteration of records on those storage devices.- Perform rules updates and patches to the HIDS as security issues are identified to maintain the established level of system security.{{Licensee/Applicant}} secures HIDS configuration documents to ensure that only authorized personnel may access them.' WHERE [Requirement_Id] = 5704
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} establishes, implements, and documents the following requirements:- Configure CDAs with the lowest privilege, data, commands, file, and account access.- Configure the system services to execute at the lowest privilege level possible for that service and document the configuration.- Document the changing or disabling of access to files and functions.- Validate that baseline permission and security settings are not altered after modifications or upgrades.' WHERE [Requirement_Id] = 5705
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} establishes, implements, and documents the following requirements:- Disable, through software or physical disconnection, unneeded networks, wireless and communication ports and removable media drives or provided engineered barriers.- Password protect the BIOS from unauthorized changes.- Document mitigation measures in cases for which password protection of the BIOS is not technically feasible.- Document the hardware configuration.- Use network devices to limit access to and from specific locations, where appropriate.- Allow system administrators the ability to re-enable devices if the devices are disabled by software and document the configuration.- Verify that replacement devices are configured in a manner that is equal to or better than the original.' WHERE [Requirement_Id] = 5706
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} establishes, implements, and documents the following:- the patch management program, update process, and individuals responsible for installation,- notification of vulnerabilities affecting CDAs to be conducted [within 4 hours of receipt of the vulnerability information],- notification to authorized personnel of patches affecting cyber security,- the authorization of updates or workarounds to the baseline before implementation,- the patch management process for the CDA after installation, including policies, procedures, and programs relating to mitigation strategies for instances in which the vendor of the CDA informs {{Licensee/Applicant}} not to apply released patches, and- the level of support for testing patch releases.{{Licensee/Applicant}} establishes, implements, and tests the following:- received cyber security updates on a nonproduction system/device for testing and validation before installing on production systems, and- all updates for security impact.{{Licensee/Applicant}} ensures that the nonproduction system/device accurately replicate the production CDA.' WHERE [Requirement_Id] = 5707
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} developed, disseminated, and [annually] reviews and updates the following:- a formal, documented media protection policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among [Site/Licensee/Applicant] entities, and compliance for each information category, as defined by the site policies, and ensures that any media which can provide information to assist an adversary is marked at a minimum to identify the sensitive nature of the media, and- a formal, documented procedure to facilitate the implementation of the media protection policy and all associated media protection controls, including the methodology that defines the purpose, scope, roles, responsibilities, and management commitments in the areas of media receipt, storage, handling, sanitization, removal, reuse, and disposal necessary to provide high assurance that the risk of unauthorized disclosure of information that could be used in a cyber-attack to adversely impact the “safety, security, and emergency preparedness” (SSEP) functions of the nuclear facility is prevented.' WHERE [Requirement_Id] = 5708
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} documents and restricts access to “critical digital asset” (CDA) media to authorized individuals only. CDA media includes both digital media (e.g., diskettes, magnetic tapes, external or removable hard drives, flash/thumb drives, compact disks, and digital video disks) and non-digital media (e.g., paper, microfilm).{{Licensee/Applicant}} restricts access to any security information on mobile computing and communications devices with information storage capability (e.g., notebook computers, personal digital assistants, cellular telephones) to authorized individuals only.{{Licensee/Applicant}} employs automated mechanisms to restrict access to media storage areas and audits access attempts and accesses granted.' WHERE [Requirement_Id] = 5709
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} marks removable CDA media and CDA output according to information categories indicating the distribution limitations and handling caveats. Output on external media, including video display devices, is marked in accordance with the identified set of special dissemination, handling, or distribution instructions that apply to system output using human readable, standard naming conventions for media labels.' WHERE [Requirement_Id] = 5710
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} physically protects and securely stores CDA media to a level commensurate with the sensitivity of the data.' WHERE [Requirement_Id] = 5711
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} physically protects and stores CDA media in transport in a manner commensurate with the sensitivity of the data.{{Licensee/Applicant}} protects and controls CDA media during transport outside of controlled areas and restricts the activities associated with transport of such media to authorized personnel only.{{Licensee/Applicant}} protects digital and non-digital media during transport outside of controlled areas using {{Licensee/Applicant}}-defined security measures (e.g., locked containers, transport by security officer, cryptography).{{Licensee/Applicant}} documents activities associated with the transport of CDA media using {{Licensee/Applicant}}-defined system of records.{{Licensee/Applicant}} uses an identified custodian at all times during transport of CDA media.' WHERE [Requirement_Id] = 5712
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} sanitizes CDA media, both digital and non-digital, before disposal or release for reuse. {{Licensee/Applicant}} [follows the guidance in NIST SP 800-88] to sanitize CDA media. The information is destroyed by a method that precludes reconstruction by means available to the DBT adversaries.{{Licensee/Applicant}} identifies CDA media requiring sanitization and the appropriate techniques and procedures to be used in the process; sanitizes identified CDA media, both paper and digital, before disposal or release for reuse; and implements this control so that media sanitization is consistent. {{Licensee/Applicant}} tracks, documents, and verifies media sanitization and disposal actions and performs [quarterly] tests on sanitized data to ensure that equipment and procedures are functioning properly.' WHERE [Requirement_Id] = 5713
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}}’s reviewing official grants unescorted access authorization to those individuals who have access, extensive knowledge, or administrative control of CDAs or communication systems that can adversely impact CDAs or safety, security, and emergency preparedness functions before they gain access to those systems, in accordance with Title 10 of the Code of Federal Regulations (10 CFR) 73.56, “Personnel Access Authorization Requirements for Nuclear Power Plants.”' WHERE [Requirement_Id] = 5714
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}}, upon termination or transfer of an individual’s employment, follows the access authorization program established under 10 CFR 73.56 and promptly performs the following actions:- terminates all CDA and system access,- conducts exit interviews,- informs appropriate personnel of status change or termination,- retrieves all security-related organizational property, and- retains access to organizational information and CDAs formerly controlled by terminated individual.' WHERE [Requirement_Id] = 5715
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} developed, disseminated, and [annually] reviews and updates the following:- a formal, documented system and information integrity policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among {{Licensee/Applicant}} entities, and compliance, and- formal, documented procedures to facilitate the implementation of CDAs and an information integrity policy and associated system and information integrity controls.' WHERE [Requirement_Id] = 5716
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}}’s system and information integrity procedures contain the following attributes:- detects malicious or suspicious access control or networking anomalies occurring at established defensive level boundaries and within security levels,- alerts appropriate staff to the detected malicious or suspicious activity using a secure communications mechanism that is protected from the network being monitored,- isolates and contains malicious activity,- neutralizes malicious activity,- centralizes logging of cyber security events to support correlations,- provides for secure monitoring and management of security mechanisms,- provides time synchronization for all security-related devices, and- provides high assurance that the physical and logical security of the monitoring network (or systems/CDAs) matches or exceeds, and differs from, the systems/CDAs or networks being monitored.' WHERE [Requirement_Id] = 5717
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} established, implemented, and documented procedures for the following purposes:- identifying the security alerts and vulnerability assessment process,- communicating vulnerability information,- correcting the flaw expeditiously utilizing the configuration management process,- correcting security flaws in CDAs, and- performing vulnerability scans and assessments of the CDA to validate that the flaw has been eliminated before the CDA is put into production. Before implementing corrections, {{Licensee/Applicant}} documents and tests software updates related to flaw remediation to determine the effectiveness and potential side effects on CDAs.The {{Licensee/Applicant}} captures flaw remediation information in its Corrective Action Program.' WHERE [Requirement_Id] = 5718
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} established, deployed, and documents real-time malicious code protection mechanisms at security boundary device entry and exit points, CDAs (if applicable), workstations, servers, and mobile computing devices (i.e., calibrators) on the network to detect and eradicate malicious code resulting from the following:- data communication between systems, CDAs, removable media, or other common means, and- exploitation of CDA vulnerabilities.{{Licensee/Applicant}} documents and updates malicious code protection mechanisms (including signature definitions) whenever new releases are available in accordance with the {{Licensee/Applicant}}’s configuration management policy and procedures.{{Licensee/Applicant}} documents and configures malicious code protection mechanisms to ensure the following:- Scans are performed of security boundary devices, CDAs (if applicable), workstations, servers, and mobile computing devices weekly and real-time scans of files from external sources are performed as the files are downloaded, opened, or executed.- Infected files are disinfected and quarantined.{{Licensee/Applicant}} documents and employs malicious code protection software products from multiple vendors as part of a defense-in-depth strategy and addresses the receipt of false positives during malicious code detection and eradication and the resulting potential impact on the availability of the CDA.{{Licensee/Applicant}} centrally manages malicious code protection mechanisms to achieve the following:- The CDAs prevent users from circumventing malicious code protection capabilities.- The CDAs update malicious code protection mechanisms only when directed by a privileged user.{{Licensee/Applicant}} does not allow users to introduce unauthorized removable media into the CDAs.{{Licensee/Applicant}} disables all media interfaces (e.g., USB ports) that are not required for the operation of the CDA.{{Licensee/Applicant}} documents and implements malicious code protection mechanisms to identify data containing malicious code and responds accordingly when CDAs encounter data not explicitly allowed by the security policy.' WHERE [Requirement_Id] = 5719
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- monitoring events on the CDAs,- detecting CDAs attacks,- detecting and blocking unauthorized connections,- retaining event logs in accordance with information retention requirements,- identifying unauthorized use of the CDAs, and- monitoring devices that are deployed to provide visibility across CDAs for the following capabilities:- - - to collect information to detect attacks, unauthorized behavior and access, and authorized access, and- - - to track specific types of transactions of interest to {{Licensee/Applicant}}.{{Licensee/Applicant}} heightens the level of monitoring activity whenever {{Licensee/Applicant}} or the U.S. Nuclear Regulatory Commission (NRC) determines that there is an indication of increased risk to the safety, security, or emergency operations of the site.{{Licensee/Applicant}} documents, interconnects, and configures individual intrusion detection tools into a plant-wide intrusion detection system using common protocols.{{Licensee/Applicant}} tests cyber intrusion detection and prevention systems consistent with the timeframe defined in Nuclear Energy Institute (NEI) 03-12, Section 20.1, for intrusion detection systems, and before being placed back in service after each repair or inoperative state.{{Licensee/Applicant}} documents and employs automated tools to support near-real-time analysis of events.{{Licensee/Applicant}} documents and employs automated tools to integrate intrusion detection tools into access control and flow control mechanisms for rapid response to attacks by enabling reconfiguration of these mechanisms in support of attack isolation and elimination.{{Licensee/Applicant}} monitors, logs, and documents inbound and outbound communications for unusual or unauthorized activities or conditions. Monitoring capabilities provide real-time alerts when indications of compromise or potential compromise occur.{{Licensee/Applicant}} prevents users from circumventing intrusion detection and prevention capabilities.{{Licensee/Applicant}} notifies and documents incident response personnel of suspicious events and takes the least-disruptive actions to SSEP functions to investigate and terminate suspicious events.{{Licensee/Applicant}} documents and protects information obtained from intrusion monitoring tools from unauthorized access, modification, and deletion.{{Licensee/Applicant}} uses competent cyber security personnel to randomly test and document intrusion monitoring tools.{{Licensee/Applicant}} documents and makes provisions to ensure that encrypted traffic is visible to monitoring tools.{{Licensee/Applicant}} analyzes and documents outbound communications traffic at the external boundary of CDAs (i.e., system perimeter) and, at selected interior points within the CDAs infrastructure to discover anomalies.{{Licensee/Applicant}} ensures and documents that the use of monitoring tools and techniques does not adversely impact the functional performance of CDAs and that, where monitoring tools and techniques cannot be used, adequate alternate controls are in place to compensate.' WHERE [Requirement_Id] = 5720
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- receiving timely security alerts, bulletins, advisories, and directives from credible external organizations as designated by the NRC and the {{Licensee/Applicant}} on an ongoing basis, such as third-party security alert notification services and vendor security alert lists, and maintaining a copy of these documents,- independently evaluating and determining the need, severity, methods, and timeframes for implementing security directives consistent with the security controls for the CDA (Section 3.1 of Appendix A to Regulatory Guide (RG) 5.71), and- within established timeframes set by the licensee or as directed by the NRC, {{Licensee/Applicant}}:- - - generates and documents internal security alerts, advisories, and directives as necessary,- - - disseminates and documents security alerts, advisories, and directives to designated personnel for action and tracks their status and completion,- - - implements and documents security directives in accordance with established timeframes or implements an alternate security measure,- - - implements and documents any required mitigation measures in accordance with the [configuration management process], and- - - employs automated or other mechanisms (e.g., e-mail lists) to make security alert and advisory information available to [Site], as needed.' WHERE [Requirement_Id] = 5721
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} verifies and documents the correct operation of security functions of CDAs. This occurs, where possible, upon startup and restart, upon command by a user with appropriate privilege, [weekly], and when anomalies are discovered. When technically feasible, CDAs provide notification of failed security tests and {{Licensee/Applicant}} documents these cases.' WHERE [Requirement_Id] = 5722
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'If technically feasible, CDAs provide automated support for the management of distributed security testing and {{Licensee/Applicant}} documents the results of this testing.{{Licensee/Applicant}} documents the justification for employing alternative (compensating) controls for those situations in which a CDA cannot support the use of automated mechanisms for the management of distributed security testing. Non-automated mechanisms and procedures to test security functions include the use of the following:- qualified individuals,- trustworthy and reliable individuals in accordance with 10 CFR 73.56,- test procedures and results,- physically restricted access to the CDA,- monitored and recorded physical access to the CDA (for timely detection and response to intrusions), and- auditing and validation measures (e.g., security officer rounds, periodic monitoring of tamper seals).' WHERE [Requirement_Id] = 5723
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- detecting and documenting unauthorized changes to software and information,- employing hardware access controls (e.g., hardwired switches), where technically feasible, to prevent unauthorized software changes,- reassessing and documenting the integrity, operation, and functions of software and information by performing regular integrity, operation, and functional scans consistent with manufacturer or vendor recommendations, [quarterly] or as defined in NEI 03-12 or as required by NRC regulation, whichever is more frequent,- employing and documenting automated tools, where technically feasible, that provide notification to designated individuals upon discovering discrepancies during integrity verification,- employing and documenting centrally managed integrity verification tools,- requiring the use of physical tamper evident packaging or seals for system components,- requiring, when tamper evident packaging is used, that seals be inspected on a regular basis, and- ensuring and documenting that the use of integrity verification applications does not adversely impact the operational performance of the CDA and applying alternate controls when integrity verification applications cannot be used.' WHERE [Requirement_Id] = 5724
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for ensuring the following:- The capability to input information to CDAs is restricted to only authorized sources.- Information is checked automatically for accuracy, completeness, validity, and authenticity as close to the point of origin as possible. Rules for checking the valid syntax of CDA inputs (e.g., character set, length, numerical range, acceptable values) are documented and in place to verify that inputs match specified definitions for format and content. Inputs passed to interpreters are prescreened to prevent the content from being unintentionally interpreted as commands.' WHERE [Requirement_Id] = 5725
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} documents and implements controls for CDAs to ensure the following:- Error conditions are identified.- Generated error messages provide information necessary for corrective actions without revealing potentially harmful information that could be exploited by adversaries.- Error messages are revealed only to authorized personnel.- Inclusion of sensitive information, such as passwords, in error logs or associated administrative messages is prohibited.' WHERE [Requirement_Id] = 5726
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} retains output from CDAs to ensure that sensitive information is only disclosed to authorized personnel and is handled and disposed of to ensure that output is not disclosed to unauthorized personnel.' WHERE [Requirement_Id] = 5727
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} protects the availability of CDAs through compliance with technical specifications, preventive maintenance programs, maintenance rule programs, security plans, emergency plans, or the corrective action program. Where these programs do not apply, the availability of CDAs is provided by the following means:- substitution of components, when needed, and a mechanism to exchange active and standby roles of the components, and- consideration of the mean time to failure for components in specific environments of operation- having adequate inventory of essential spare parts.' WHERE [Requirement_Id] = 5728
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} developed, disseminated, and [annually] reviews the following:- a formal, documented CDA maintenance policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among {{Licensee/Applicant}} entities, associated CDA maintenance controls, and compliance,- formal, documented procedures to facilitate the implementation of the CDA maintenance policy and associated maintenance controls, and- the system maintenance policy and procedures which cover assets located in all security boundaries, including the following:- - - owner-controlled area: the outermost protected area boundary for a plant that is outside the plant’s security area,- - - protected area: an area within the boundaries of a nuclear facility that is encompassed by physical barriers and to which access is controlled (see 10 CFR 73.2, “Definitions”),- - - vital areas: areas containing any equipment, system, device, or material, the failure, destruction, or release of which could directly or indirectly endanger the public health and safety by exposure to radiation. Vital areas may also contain equipment or systems which would be required to function to protect public health and safety following such failure, destruction, or release, and- - - public access area: locations outside the physical control of the plant.' WHERE [Requirement_Id] = 5729
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- approving, monitoring, and documenting the use of CDA maintenance tools,- inspecting and documenting maintenance tools (e.g., diagnostic and test equipment and mobile devices, such as laptops) carried into a facility by maintenance personnel for obvious improper modifications,- checking and documenting all media and mobile devices, such as laptops, containing diagnostic, CDA, and system and test programs or software for malicious code before the media or mobile device is used in or on a CDA,- controlling, preventing and documenting the unauthorized removal of maintenance equipment by one of the following:- - - verifying that there is no {{Licensee/Applicant}} information contained on the equipment and validating the integrity of the device before reintroduction into the facility,- - - sanitizing or destroying the equipment,- - - retaining the equipment within the facility, and- - - obtaining approval from an authority explicitly authorizing removal of the equipment from the facility, and- employing [automated/manual] mechanisms to restrict the use of maintenance tools to authorized personnel only and employing manual mechanisms only when CDAs or support equipment (e.g., laptops) cannot support automated mechanisms.' WHERE [Requirement_Id] = 5730
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- maintaining and documenting a current list of authorized maintenance personnel consistent with its access authorization program and insider mitigation program, and- implementing and documenting [automated mechanism or non-automated mechanism] to detect unauthorized use or execution of commands by an escorted individual, ordesignating and documenting {{Licensee/Applicant}} personnel with required access authorization and knowledge necessary to supervise escorted personnel interacting with CDAs.' WHERE [Requirement_Id] = 5731
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'For those CDAs located outside of the [Site] protected area, {{Licensee/Applicant}} developed, implemented, and [annually] reviews and updates the following:- a formal, documented physical and environmental protection policy that addresses the following:- - - the purpose of the physical security program as it relates to protecting the CDAs,- - - the scope of the physical security program as it applies to the organization’s staff and third-party contractors, and- - - the roles, responsibilities, and management accountability structure of the physical security program to ensure compliance with the {{Licensee/Applicant}} security policy and other regulatory commitments, and- formal, documented procedures to facilitate the implementation of the physical and environmental protection policy and associated physical and operational environmental protection security controls.' WHERE [Requirement_Id] = 5732
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- screening, enforcing, and documenting security controls for third-party personnel (including service contractors and other organizations providing control system operation and maintenance, development, information technology services, outsourced applications, and network and security management) and monitoring service provider behavior and compliance, and- explicitly including personnel security controls in acquisition-related contract and agreement documents.' WHERE [Requirement_Id] = 5733
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} secures and documents physical access to CDAs. Physical security controls (e.g., physical, locked, drivers) are employed to limit access to CDAs and to prevent degradation of the operational environment which could impact the correct performance of CDAs (e.g., temperature, humidity, dust, vibration and electromagnetic or radiofrequency interference).' WHERE [Requirement_Id] = 5734
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- developing and maintaining a list of, and issuing authorization credentials (e.g., badges, identification cards, smart cards) to, personnel with authorized access to facilities containing CDAs and security boundary systems, and- designating officials within the organization to review and approve the above access lists and authorization credentials, consistent with the access authorization program.' WHERE [Requirement_Id] = 5735
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- controlling all physical access points (including designated entry and exit points) to locations where CDAs reside and verifying individual access authorization before granting access to these areas,- approving individual access privileges and enforcing physical and logical access restrictions associated with changes to CDAs,- controlling logical access through the use of electronic devices and software,- generating, retaining, and reviewing records pertaining to access restrictions,- ensuring that only qualified and authorized individuals obtain access to CDAs, and- controlling physical access to the CDAs independent of the physical access controls for the facility.' WHERE [Requirement_Id] = 5736
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} controls and documents physical access to CDA communication paths.' WHERE [Requirement_Id] = 5737
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} controls and documents physical access to CDAs that display information that may assist an adversary and prevents unauthorized individuals from observing the display output.' WHERE [Requirement_Id] = 5738
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- monitoring and documenting physical access to CDAs and security boundaries to detect and respond to physical security incidents,- reviewing physical access logs,- coordinating results of reviews and investigations with {{Licensee/Applicant}}’s incident response personnel,- monitoring real-time physical intrusion alarms and surveillance equipment,- employing automated mechanisms to assess and recognize potential intrusions and initiates appropriate response actions, and- providing adequate lighting for access monitoring devices (e.g., cameras).' WHERE [Requirement_Id] = 5739
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- controlling and documenting visitor physical access to CDAs by verifying the identity and confirming access authorization of these individuals prior to entry, and- escorting visitors and monitoring visitor activity to prevent adverse impact to SSEP functions.' WHERE [Requirement_Id] = 5740
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} implements and documents its defensive strategy that identifies the protective controls associated within each security level.{{Licensee/Applicant}} implements and documents a defensive model that identifies the logical boundaries for data transfer and associated communication protocols. The model defines the level of connectivity permitted between levels and individual CDAs. The elements of the defensive strategy are incorporated into CDAs. Security controls are applied commensurate with the risk associated to perform the function required to meet design specifications and operational requirements.' WHERE [Requirement_Id] = 5741
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} implements and documents a defensive strategy, as well as the following:- allocates the highest degree (i.e., Level 4) of cyber security protection to CDAs that carry out safety, important to safety, and security functions and protects those CDAs from lower defensive levels,- prevents remote access to CDAs located in the highest defensive level,- prevents spoofing of addresses from one security level to another,- only one-way data flow is allowed from Level 4 to Level 3 and from Level 3 to Level 2,- initiation of communications from digital assets at lower security levels to digital assets at higher security levels is prohibited,- bi-directional (2-way) communication between CDAs in Level 4 is only conducted within a security Level 4,- any non-safety system that has bi-directional communication to a safety system is afforded the same level of protection as the safety system,- provides intrusion prevention and detection capabilities within and at the boundaries between security levels,- ensures for defense-in-depth levels using bi-directional (2 way) communication that data flow from one level to other levels occurs only through a device that enforces the security policy between each level and detects, prevents, delays, mitigates, and recovers from a cyber-attack coming from the lower security level, and- moves data, software, firmware, and devices from lower levels of security to higher levels of security using a documented validation process or procedure which is trustworthy at or above the trust level of the device on which the data, code, information, or device will be installed or connected with to ensure that the data, software, firmware, or devices are free from known malicious code, Trojan viruses, worms, and other passive attacks.CDAs that provide safety, important-to-safety, security, or control functions are allocated defensive Level 4 protection.CDAs that provide data acquisition functions are allocated at least defensive Level 3 protection.' WHERE [Requirement_Id] = 5742
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} implements and documents security boundary control devices between higher security levels and lower security levels that include the following elements:- physically and logically secures and hardens CDAs to prevent unauthorized access or manipulation,- employs secure management communications and encryption in accordance with Appendix B to RG 5.71,- provides logging and alert capabilities,- provides intrusion detection and prevention capabilities,- detects and prevents malware from moving between boundaries,- possesses the ability to perform more than stateful inspection with respect to the protocols used in communication across the boundary, such as through a bastion host or application proxy.' WHERE [Requirement_Id] = 5743
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} implements and documents security boundary control devices between higher security levels and lower security levels that except in the case of data diodes, contains a rule set that at a minimum:- - - is configured to deny traffic, except that which is explicitly authorized,- - - provides protocol, source, and destination filtering such as IP addresses, MAC addresses, TCP ports, and UDP ports,- - - bases blocking on source and destination address pairs, services, and ports where the protocol supports this,- - - does not permit either incoming or outgoing traffic by default,- - - is managed either through a direct connection to the firewall from a management device, such as a laptop, or through a dedicated interface connected to a site-centric security network,- - - does not permit direct communication to the firewall from any of the managed interfaces,- - - records information relative to accepted and rejected connections, traffic monitoring, analysis, and intrusion detection,- - - forwards logs to a centralized logging server,- - - enforces destination authorization and restricts users by allowing them to reach only the CDAs necessary for their function,- - - records information flow for traffic monitoring, analysis, and intrusion detection,- - - is deployed and maintained by authorized personnel adequately trained in the technologies used,- - - documents and designs with minimal connections that permit acquisition and control networks to be severed from corporate networks, should that decision be made, in times of serious cyber incidents or when directed by authorized personnel who are designated to do so,- - - is evaluated, analyzed, and tested before deployment and routinely upon modification of the rule set and updates to the operational software and firmware required to operate the firewall,- - - receives time synchronization from a trusted and dedicated source existing on the security network, attached directly to the CDA or via SNTP and a trusted key management process,- - - synchronizes time with CDAs to provide for event correlation,- - - is capable of forwarding logging information in a standard format to a secure logging server or uses an external device to provide this logging (as in the case of a data diode),- - - routinely reviews logs by personnel that are appropriately trained in such analysis to detect malicious or anomalous activity,- - - is updated [quarterly],- - - uses only physically and logically secured and hardened computing devices and flow control to prevent unauthorized access or manipulation of data streams,- - - allows no information of any kind, including handshaking protocols, to be transferred directly from networks, systems, or CDAs existing at a lower security level to networks, systems, or CDAs existing at Level 4, and- - - employs measures to prevent viruses or other malicious or unwanted programs from propagating information between security levels. CDAs that provide safety, important-to-safety, security, or control functions are allocated defensive Level 4 protection. CDAs that provide data acquisition functions are allocated at least defensive Level 3 protection. The defensive model defines data transmission.' WHERE [Requirement_Id] = 5744
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'Measures necessary to deny, deter, and detect cyber-attacks are implemented by [system, CDA, network protective devices] and align with the {{Licensee/Applicant}} defensive strategy.{{Licensee/Applicant}} establishes, implements, and documents security controls to deny, deter, and detect adverse threats and conditions to CDAs that may be susceptible to cyber-attacks. Security controls employed counteract postulated threats. {{Licensee/Applicant}} establishes, implements, and documents the methods used to respond to incidents and to escalate cyber security events to the [Site/Licensee]’s incident response personnel, appropriate law enforcement authorities, or the NRC.The {{Licensee/Applicant}}’s Corrective Action Program evaluates, tracks, manages, provides corrective action and documents cyber-attacks.{{Licensee/Applicant}} procedures that govern response to cyber events direct timely identification, detection, and response to cyber-attacks. When there is a reasonable suspicion of a cyber-attack, response instructions direct notification to the [shift superintendent operations, site security superintendent, manager nuclear information technology, cyber security incident response team] and other emergency response actions.{{Licensee/Applicant}} procedures direct containment activities. These measures include (but are not limited to) activities necessary for the following:- assist operations in conducting an operability determination,- isolate the affected CDA with approval by [shift superintendent operations], if possible, and- verify that surrounding or interconnected CDAs, networks, and support systems are not contaminated, degraded, or compromised. Eradication activities identify the attack and the compromised pathway. {{Licensee/Applicant}} patches, cleans, reimages, or replaces the CDA using disaster recovery procedures.{{Licensee/Applicant}} governing procedures direct measures necessary to mitigate the consequences of cyber-attacks. Recovery activities include, but are not limited to, functional recovery tests, security function and requirements tests, restoration to an operational state, verification of operability, and return to active service. Systems, networks, or equipment affected by cyber-attacks are restored and returned to operation as directed by {{Licensee/Applicant}} procedures. {{Licensee/Applicant}} conducts post incident analysis in accordance with its Corrective Action Program. {{Licensee/Applicant}} reports cyber-attacks to the NRC as directed by {{Licensee/Applicant}} procedures, in accordance with the requirements of Appendix G, “Reportable Safeguards Events,” to 10 CFR Part 73 and as further described in Regulatory Position C.8.6.' WHERE [Requirement_Id] = 5745
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} developed, disseminated, and [annually] reviews and updates the following:- a formal, documented incident response policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among {{Licensee/Applicant}} entities, and compliance,- formal, documented procedures to facilitate the implementation of the incident response policy and associated incident response controls that establish procedures for the following:- - - notifying staff and operators,- - - determining whether unexpected indications or fault conditions could be the result of a cyber-attack in progress,- - - in the event that the cyber-attack was the result of previous activities that have lain dormant within a CDA, using the Corrective Action Program to perform an analysis to identify entry mechanisms and take steps to close down the vulnerability, and- - - establishing a disaster recovery plan that specifically permits rapid recovery from a cyber-attack, including system backups which allow rapid reconstruction of the CDA, and- recovery plans that are exercised to ensure that they are effective and that personnel are sufficiently familiar with how to employ them in accordance with [disaster recovery plans, business continuity or emergency plans] and that changes made are based on lessons learned from exercises and drills and actual incidents and events.{{Licensee/Applicant}} includes stakeholders in the development of incident response policies, procedures, and plans, including the following groups:- physical security,- cyber security team,- operations,- engineering,- information technology,- human resources,- system support vendors,- management, and- legal.' WHERE [Requirement_Id] = 5746
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- training personnel in their incident response roles and responsibilities with respect to the CDAs and providing refresher training [at least annually],- incorporating simulated events into incident response training to facilitate effective response by personnel in crisis situations, and- documenting incident response training exercises and acknowledgements that personnel are qualified and trained.' WHERE [Requirement_Id] = 5747
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- testing and conducting drills of the incident response capability for CDAs [at least annually],- using {{Licensee/Applicant}}-defined tests or drills or both to update the incident response capability to maintain its effectiveness,- documenting the results of testing and drills,- providing incident response testing and drills procedures,- employing automated mechanisms to thoroughly and effectively test or drill the incident response capability, and- performing and documenting announced and unannounced tests and drills.' WHERE [Requirement_Id] = 5748
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- implementing and documenting an ongoing incident handling capability for security incidents that includes preparation, detection and analysis, containment, eradication, and recovery [rolled into existing incident handling program],- incorporating lessons learned from ongoing incident handling activities into incident response procedures and implementing the procedures accordingly,- forming an integrated cyber security incident response team (CSIRT),- in the event of an unplanned incident that reduces the number of required cyber security personnel, compensating, by using other trained and qualified onsite cyber security personnel or calling in off-duty personnel within 2 hours from the time of discovery,- providing the team with the technical skills and authority to effectively respond to a potential cyber security event,- developing and documenting processes, procedures, and controls that the team will employ upon the discovery or identification of a potential or actual cyber security attack, and- documenting and defining response to the following:- - - identification of what constitutes a cyber security incident,- - - identification of threat level classification for incidents,- - - description of actions to be taken for each component of the Incident Response &Recovery (IR&R) process,- - - description of individual postulated classes or categories of incidents or attacks, as analyzed during attack vector analysis, and indicators and potential or planned methods of mitigation,- - - identification of defensive strategies that would assist in identifying and containing a cyber-attack,- - - description of the CSIRT incident notification process,- - - description of incident documentation requirements,- - - establishment of coordinated and secure communication methods to be used between local and remote CSIRT members and outside agencies, and- - - description of response escalation requirements.Incident data collected includes the following:- incident title,- date of incident,- reliability of report,- type of incident (e.g., accident, virus),- entry point (e.g., Internet, wireless, modem),- perpetrator,- type of system, hardware and software impacted,- brief description of incident,- impact on organization,- measures to prevent recurrence, and- references.' WHERE [Requirement_Id] = 5749
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The {{Licensee/Applicant}} CSIRT consists of individuals with knowledge and experience in the following areas:- Information and digital system technology—This covers the areas of cyber security, software development and application, computer system administration, and computer networking. In particular, knowledge is required of the digital systems involved in plant operations, including digital instrumentation and control systems, and those involved in plant business systems. In the plant operations area, this includes programmable logic controllers, control systems, and distributed control systems. In the business area, this includes computer systems and databases containing information used to design, operate, and maintain CDAs. In the networking arena, knowledge is required of both plant- and corporate-wide networks. An experienced and highly skilled cyber security staff member might have expertise in all of these areas.- Nuclear facility operations, engineering, and safety—This includes knowledge of overall facility operations and plant technical specifications. Staff representing this technical area must be able to trace the impact of a vulnerability or series of vulnerabilities in a CDA (or connected digital asset) outward through plant subsystems and systems so that the overall impact on safety, security, and emergency preparedness of the plant can be evaluated.- Physical and operational security—This includes in-depth knowledge of the plant’s physical and operational security program. In addition to the above requirements, specialized in-depth cyber security skills are required to perform the electronic validation testing and optional scanning activities.- {{Licensee/Applicant}} may not have onsite personnel trained and experienced in all arenas. If this expertise is not available on site, corporate-level cyber security personnel, an independent cyber security organization, or other sources of the necessary validation expertise are considered.In addition, individuals with the following roles join the CSIRT on an as-needed basis (depending on the incident):- site security (physical),- senior plant management,- corporate public relations, and- corporate legal.' WHERE [Requirement_Id] = 5750
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} tracks and documents security incidents on an ongoing basis using automated mechanisms to assist in the tracking of security incidents and in the collection and analysis of incident information.' WHERE [Requirement_Id] = 5751
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} provides competent and trained incident response support personnel who are available year round, 24 hours per day to offer advice and assistance to users of CDAs in response to and reporting of cyber security incidents. The support resource is an integral part of {{Licensee/Applicant}}’s incident response capability. {{Licensee/Applicant}} employs mechanisms to increase the availability of incident response-related information and support.' WHERE [Requirement_Id] = 5753
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} developed an incident response plan that:- describes the structure and organization of the cyber incident response capability,- provides a high-level approach for how the cyber incident response capability fits into the overall organization,- defines reportable cyber incidents consistent with Regulatory Position C.8.6,- provides metrics for measuring the cyber incident response capability within the organization,- defines the resources and management support needed to effectively maintain and mature an incident response capability, and- is reviewed and approved by the Cyber Security Program Sponsor.{{Licensee/Applicant}} distributes copies of the incident response plan plant personnel including incident response personnel, reviews the incident response plan [annually], revises the incident response plan to address changes or problems encountered during plan implementation, execution, or testing, and communicates incident response plan changes to plant personnel including incident response personnel.' WHERE [Requirement_Id] = 5754
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} developed, disseminated, and [annually] reviews and updates the following:- a formal, documented contingency planning policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among {{Licensee/Applicant}} entities, and compliance, and- formal, documented procedures to facilitate the implementation of the contingency planning policy and associated contingency planning controls.{{Licensee/Applicant}} updates contingency planning policy and procedures and, where necessary, related policies and procedures for other programs when {{Licensee/Applicant}} review indicates updates are required.' WHERE [Requirement_Id] = 5755
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}}’s contingency plan includes the following:- required response to events or conditions of varying duration and severity that would activate the recovery plan,- procedures for operating the CDAs in manual mode with external electronic connections severed until secure conditions can be restored,- roles and responsibilities of responders,- processes and procedures for the backup and secure storage of information,- complete and up-to-date logical diagrams depicting network connectivity,- current configuration information for components,- personnel list (according to title or function or both) for authorized physical and cyber access to the CDA,- communication procedure and list of personnel (according to title or function or both) to contact in the case of an emergency, and- documented requirements for the replacement of components.' WHERE [Requirement_Id] = 5756
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- implementing a cyber security contingency plan to maintain the SSEP functions by developing and disseminating roles, responsibilities, assigned individuals with contact information, and activities associated with determining the effects of CDAs after a compromise, disruption or failure and restoring those CDAs,- coordinating contingency plan development with {{Licensee/Applicant}} organizations responsible for related plans (e.g., emergency plan, physical security plan) and requirements (e.g., technical specifications),- maintaining the necessary resources and capacity to ensure that necessary information processing, telecommunications, and environmental support exist during crisis situations,- documenting the resources needed to ensure that the capacity necessary for information processing, telecommunications, and environmental support exists during crisis situations, and- deploying CDAs such that, in the event of a loss of processing within a CDA or a loss of communication with operational facilities, CDAs will execute predetermined actions.' WHERE [Requirement_Id] = 5757
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for taking the following actions:- tests and/or exercises and documents the contingency plan [at least annually] to verify its effectiveness and the organization’s readiness to execute this plan,- reviews the contingency plan test and exercise results and initiates appropriate corrective actions,- coordinates contingency plan testing and/or exercises with {{Licensee/Applicant}} elements responsible for related plans,- tests and/or exercises and documents the contingency plan at emergency and/or backup sites to familiarize contingency personnel with these facilities and their available resources and to evaluate the [Site''s] capabilities to support contingency operations,- employs automated mechanisms to thoroughly and effectively test/exercise the contingency plan by providing a more complete coverage of contingency issues and selecting more realistic test/exercise scenarios and environments,- includes recovery and reconstitution of CDAs as part of contingency plan testing,- establishes and documents alternate controls when the contingency plan cannot be tested or exercised on production CDAs because of the potential for a significant adverse impact on safety, security, performance, or reliability of the site or CDA, and- uses scheduled and unscheduled system maintenance activities, including responding to CDA component and system failures, as an opportunity to test or exercise the contingency plan.' WHERE [Requirement_Id] = 5758
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- training personnel in their contingency roles and responsibilities with respect to the CDAs and providing refresher training [at least annually] or consistent with the [Licensee/Applicant''s] overall contingency program, whichever period is shorter,- maintaining training procedures and documenting training records of individuals,- including training drills to familiarize contingency personnel with the facility, CDAs, and available resources and evaluating the site’s capabilities to support contingency operations,- employing automated mechanisms to thoroughly and effectively test/drill the contingency plan by providing more complete coverage of contingency issues, and- selecting realistic test/drill scenarios and environments, effectively stressing the CDAs.' WHERE [Requirement_Id] = 5759
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} identifies and documents alternate storage locations and initiates necessary agreements to permit the storage of CDA backup information. The frequency of CDA backups and the transfer rate of backup information to the alternate storage locations are consistent with {{Licensee/Applicant}}’s recovery time objectives and recovery plan objectives.{{Licensee/Applicant}} is responsible for the following:- identifying an alternate storage location that is geographically separated from the primary storage location so as not to be susceptible to a common hazard,- configuring the alternate storage location to facilitate recovery of operation, and- identifying and documenting potential accessibility problems to the alternate storage location in the event of a wide area disruption or disaster and implementing explicit mitigation actions.' WHERE [Requirement_Id] = 5760
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- conducting backups of user-level and system-level information,- backing up CDAs at an interval identified for the CDA or based on trigger events,- protecting backup information at the storage location,- testing and documenting backup information [monthly] to verify media reliability and information integrity,- using backup information in the restoration of CDA functions as part of contingency plan testing,- protecting system backup information from unauthorized modification,- storing backup copies of the operating system and other critical CDA software in a separate facility or in a fire-rated container that is not collocated with the operational software, and- establishing and documenting the timeframe in which data or the CDA must be restored and the frequency at which critical data and configurations are changing.' WHERE [Requirement_Id] = 5761
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} employs mechanisms with supporting procedures that allow CDAs to be recovered and reconstituted to a known secure state following a disruption or failure and only when initiated by authorized personnel. {{Licensee/Applicant}} performs regression testing before returning to normal operations to ensure that CDA are performing correctly.' WHERE [Requirement_Id] = 5762
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} establishes, implements, and documents the training requirements necessary for licensee/applicant personnel and contractors to perform their assigned duties and responsibilities in implementing the requirements of the program.{{Licensee/Applicant}} individuals are trained to a level of cyber security knowledge appropriate to their assigned responsibilities in order to provide high assurance that these individuals are able to perform their job functions properly.' WHERE [Requirement_Id] = 5763
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}}’s cyber security awareness training is designed to increase an individual’s sensitivity to cyber threats and vulnerabilities and their recognition of the need to protect data and information. Policy-level awareness training provides employees and contractors with the ability to understand security policies so that the program is effectively implemented. Individual users must understand their responsibility for adherence to applicable policies and standards. {{Licensee/Applicant}} establishes, implements, and documents requirements for the following:- Training programs provide basic cyber security awareness training for facility personnel. Refresher or continuous training provides updates on new threats and technology.- Cyber security awareness is provided by displaying posters, offering security-messaged items, generating e-mail advisories and notices, and displaying logon screen messages.- Training includes practical exercises to simulate actual cyber incidents, recovery plans, response plans and adversary attacks.{{Licensee/Applicant}} develops and documents the content of cyber security training based on the following:- assigned roles and responsibilities,- specific requirements identified by the defensive strategy, and- CDAs to which personnel have authorized access.' WHERE [Requirement_Id] = 5764
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} establishes, implements, and documents requirements for training to provide the following:- cyber security awareness training for {{Licensee/Applicant}} employees and contractors which addresses the following:- - - the site-specific objectives, management expectations, programmatic authority, roles and responsibilities, policies, procedures, and consequences for noncompliance with the cyber security program,- - - general attack methodologies, including social engineering techniques and appropriate and inappropriate cyber security practices,- - - attack indicators, such as the following:- - --- unusually heavy network traffic,- - --- out of disk space or significantly reduced free disk space,- - --- unusually high CPU usage,- - --- creation of new user accounts,- - --- attempted or actual use of administrator-level accounts,- - --- locked-out accounts,- - --- account in-use when the user is not at work,- - --- cleared log files,- - --- full log files with unusually large number of events,- - --- antivirus or IDS alerts,- - --- disabled antivirus software and other security controls,- - --- unexpected patch changes,- - --- machines connecting to outside IP addresses,- - --- requests for information about the system (social engineering attempts),- - --- unexpected changes in configuration settings,- - --- unexpected system shutdown,- - --- unusual activity from control devices,- - --- loss of signal from control devices, and- - --- unusual equipment in secure areas,- - - organizational contacts to whom to report suspicious activity, incidents, and violations of cyber security policies, procedures, or practices,- - - an explanation as to why access and control methods are required,- - - measures users can employ to reduce risks, and- - - the impact on the organization if the control methods are not incorporated.' WHERE [Requirement_Id] = 5765
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} establishes, implements, and documents training programs for personnel performing, verifying, or managing activities within the scope of the program to ensure that suitable proficiency is achieved and maintained. {{Licensee/Applicant}} individuals that have cyber security responsibilities related to programs, processes, procedures, or individuals that are involved in the design, modification, and maintenance of CDAs, will receive technical training.{{Licensee/Applicant}} establishes, implements, and documents requirements to do the following:- provide cyber security-related technical training to individuals:- - - before authorizing access to CDAs or performing assigned duties,- - - when required by policy or procedure changes and plant modifications, and- - - annually or at an interval as defined by the {{Licensee/Applicant}}, whichever is shorter, to mitigate risk and to ensure personnel maintain competency, and- provide cyber security-related technical training on applicable cyber security concepts and practices to those individuals whose roles and responsibilities involve designing, installing, operating, maintaining, or administering (e.g., serving as a system administrator) CDAs or associated networks which addresses the following:- - - knowledge of specific cyber security and engineering procedures, practices, and technologies, including implementation methods and design requirements, which apply to the assets they may encounter as part of their job and- - - general information on cyber vulnerabilities, potential consequences to CDAs and networks of successful cyber-attacks, and cyber security risk reduction methods{{Licensee/Applicant}} provides system managers, cyber security specialists, system owners, network administrators, and other personnel having access to system-level software with security-related technical training to perform their assigned duties.' WHERE [Requirement_Id] = 5766
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} individuals who have programmatic and procedural cyber security authority and require the necessary skills and knowledge to execute capabilities expected of a cyber security specialist receive specialized cyber security training in order to design, execute, and manage the cyber defensive strategy effectively.{{Licensee/Applicant}} establishes, implements, and documents requirements for advanced training for individuals who are designated security experts or specialists, including the cyber security specialists with roles and responsibilities for cyber security, incident response, and the execution and management of defense-in-depth protective strategies. Advanced training addresses the following:- achievement and maintenance of the necessary up-to-date skills and knowledge in core competencies of data security, operation system security, application security, network security, security controls, intrusion analysis, incident management and response, digital forensics, penetration testing, and plant system functionality and operations,- competency in the use of tools and techniques to physically and logically harden CDAs and networks to reduce vulnerabilities to cyber-attack,- the provision of cyber security guidance, assistance, and training for other staff members,- the review of programmatic and system-specific cyber security plans and practices,- assessment of CDAs, networks, and assets for compliance with cyber security policies, and- design, acquisition, installation, operation, maintenance, or administration of security controls.' WHERE [Requirement_Id] = 5767
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} develops, implements, and documents a cross-functional cyber security team (CST).{{Licensee/Applicant}} develops, implements, and documents a program to share expertise and varied domain knowledge between members of the CST.{{Licensee/Applicant}}’s CST includes, at a minimum, a member of the organization’s information technology staff, an instrumentation and control system engineer, a control system operator, a subject matter expert in cyber security, and a member of the management staff.{{Licensee/Applicant}}’s cyber security subject matter experts’ skills include network architecture and design, security processes and practices, and secure infrastructure design and operation.{{Licensee/Applicant}}’s CST also includes the control system vendor or system integrator, as needed.{{Licensee/Applicant}}’s CST reports [directly to organizational structure how and who].' WHERE [Requirement_Id] = 5768
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} security training describes the physical processes being controlled, as well as the associated CDAs and security controls.' WHERE [Requirement_Id] = 5769
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} establishes, implements, and documents a feedback process for personnel and contractors to refine the cyber security program and address identified training gaps.' WHERE [Requirement_Id] = 5770
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} documents and monitors individual cyber security training.' WHERE [Requirement_Id] = 5771
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} maintains contact with selected security groups to remain informed of newly recommended security practices, techniques, and technologies and to share current security-related information including threats, vulnerabilities, and incidents.' WHERE [Requirement_Id] = 5772
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} creates, documents, and staffs the following positions (roles) with appropriately qualified personnel:Role: Cyber Security SponsorRequirements: member of senior site managementResponsibilities:- overall responsibility and accountability for the cyber security program, and- provides resources required for the development, implementation and sustenance of the cyber security program.Role: Cyber Security Program ManagerResponsibilities:- provides oversight of the plant cyber security operations,- functions as a single point of contact for issues related to site cyber security,- provides oversight and direction on issues regarding nuclear plant cyber security,- initiates and coordinates CSIRT functions as required,- coordinates with the NRC as required during cyber security events,- oversees and approves the development and implementation of a cyber security plan,- ensures and approves the development and operation of the cyber security education, awareness, and training program, and- oversees and approves the development and implementation of cyber security policies and procedures.Role: Cyber Security SpecialistResponsibilities:- protects CDAs from cyber threat,- understands the cyber security implications surrounding the overall architecture of plant networks, control systems, safety systems, operating systems, hardware platforms, plant specific applications, and the services and protocols upon which those applications rely,- performs cyber security evaluations of digital plant systems,- conducts security audits, network scans, and penetration tests against CDAs as necessary,- conducts cyber security investigations involving compromise of CDAs,- preserves evidence collected during cyber security investigations to prevent loss of evidentiary value, and- maintains expert skill and knowledge level in the area of cyber security.Role: Cyber Security Incident Response TeamRequirements:- personnel have knowledge of cyber forensics and- functions in accordance with the incident response planResponsibilities:- initiates emergency action when required to safeguard CDAs from compromise and to assist with the eventual recovery of compromised systems,- contains and mitigates incidents involving critical and other support systems, and- restores compromised CDAs.' WHERE [Requirement_Id] = 5773
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} establishes, implements, and documents configuration management security controls for CDAs consistent with the process described in Section 4.2.1 of [this Plan (Appendix A)].' WHERE [Requirement_Id] = 5774
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} develops, disseminates, and [annually] reviews and updates a formal, documented configuration management policy and implementing procedures that address the purpose, scope, roles, responsibilities, management commitment, [coordination among {{Licensee/Applicant}} entities], associated configuration management controls, and compliance.{{Licensee/Applicant}} documents its configuration management policy as a part of the [Site] configuration management plan and includes hardware configurations, software configurations, and access permissions. Changes to hardware or software are documented and accessed in accordance with these policies and implementing procedures. The structured configuration management process evaluates and controls changes to CDAs to ensure that CDAs remains secure. Before any change is implemented, {{Licensee/Applicant}} confirms that new vulnerabilities are not introduced.' WHERE [Requirement_Id] = 5775
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} develops, documents, and maintains a current baseline configuration of CDAs and their connections including the interface characteristics, security requirements, and the nature of the information communicated. As a part of the configuration management process, {{Licensee/Applicant}} employs [manual/automated] mechanisms to maintain an up-to-date, complete, accurate, and readily available baseline configuration of each CDA.{{Licensee/Applicant}} documents the up-to-date baseline configurations and audits the configurations [quarterly]. Baseline configurations include [but are not limited to] a current list of all components (e.g., hardware, software), configuration of peripherals, version releases of current software, and switch settings of machine components. For each CDA, {{Licensee/Applicant}} maintains a log of configuration changes made, the name of the person who implemented the change, the date of the change, the purpose of the change, and any observations made during the course of the change.{{Licensee/Applicant}} documents and maintains baseline configurations for development and test environments that are managed separately from the operational/production baseline configuration.{{Licensee/Applicant}} employs a “deny-all, permit-by-exception” authorization policy to identify and authorize software permitted on {{Licensee/Applicant}} CDAs (i.e., white lists of authorized software). After authorized changes are implemented, {{Licensee/Applicant}} verifies that security features still function properly and that adequate cyber security levels are maintained. Individuals authorized to modify CDA configurations are properly trained and qualified to perform the modifications. {{Licensee/Applicant}} defines the minimum physical and logical access for the modifications.{{Licensee/Applicant}} reviews log records [no less frequently than once a quarter] in compliance with the physical security plan.' WHERE [Requirement_Id] = 5776
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} employs electronic means to monitor CDA access to ensure that only authorized systems and services are used. Furthermore, {{Licensee/Applicant}} documents the justification for the use of alternate (compensating) security controls for instances in which monitoring cannot be done electronically, including the following:- physically restricting access,- monitoring and recording physical access to enable timely detection and response to intrusions,- employing auditing and validation measures (e.g., security officer rounds, periodic monitoring of tamper seals),- ensuring authorized individuals are trustworthy and reliable in accordance with 10 CFR 73.56,- ensuring that authorized individuals are operating under established work management controls, and- conducting post maintenance testing to validate that changes are implemented correctly.' WHERE [Requirement_Id] = 5777
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} is responsible for the following:- authorizing and documenting changes to CDAs- retaining and reviewing records of CDA configuration changes and audit activities associated with CDA configuration changes and employing [manual/automated] mechanisms to:- - - document changes to CDAs,- - - notify designated approval authorities, and- - - prohibit implementation of changes until designated approvals are received and documented.' WHERE [Requirement_Id] = 5778
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The {{Licensee/Applicant}}’s CST performs a security impact assessment before making changes to CDAs consistent with [Section 4.2.2 of Appendix A to RG 5.71] to manage the cyber risk resulting from the changes. The CST evaluates, documents, and incorporates into the security impact analysis any identified safety and security interdependencies. The {{Licensee/Applicant}} performs and documents the security impact assessment as part of the change approval process.' WHERE [Requirement_Id] = 5779
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} defines, documents, approves, and enforces physical and logical access restrictions associated with changes to CDAs and generates, retains, and audits the record [quarterly] and when there are indications that unauthorized changes may have occurred.{{Licensee/Applicant}} implements its configuration management program to address discovered deviations.' WHERE [Requirement_Id] = 5780
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} employs automated mechanisms to detect unauthorized changes, to enforce access restrictions and to support subsequent audits of enforcement actions.{{Licensee/Applicant}} documents the justification and details for alternate (compensating) security controls for situations in which a CDA cannot support the use of automated mechanisms to enforce access restrictions and to support subsequent audits of enforcement actions, including all of the following:- physically restricting access,- monitoring and recording physical access to enable timely detection and response to intrusions,- employing auditing and validation measures (e.g., security officer rounds, periodic monitoring of tamper seals),- ensuring authorized individuals are trustworthy and reliable in accordance with 10 CFR 73.56,- ensuring that authorized individuals are operating under established work management controls, and- conducting post maintenance testing to validate that changes are implemented correctly.' WHERE [Requirement_Id] = 5781
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} applies configuration settings for CDAs by- documenting the most restrictive mode,- evaluating operational requirements, and - enforcing and documenting the most restrictive operational configuration settings based upon explicit operational requirements.This is achieved by the following:- establishing and documenting configuration settings for CDAs that reflect the most restrictive mode,- documenting and approving any exceptions from the most restrictive mode configuration settings for individual components within CDAs based upon explicit operational requirements,- enforcing the configuration settings in CDAs and monitoring and controlling changes to the configuration settings in accordance with {{Licensee/Applicant}} policies and procedures,-' WHERE [Requirement_Id] = 5782
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} applies configuration settings for CDAs by- documenting and employing automated mechanisms to [centrally] manage, apply, and verify configuration settings,- documenting and employing [automated mechanisms/manual mechanisms] to respond to unauthorized changes to {{Licensee/Applicant}}-defined configuration settings, and- documenting the justification for alternate (compensating) security controls for situations in which a CDA cannot support the use of automated mechanisms to [centrally] manage, apply, and verify configuration settings, including all of the following:- - - physically restricting access,- - - monitoring and recording physical access to enable timely detection and response to intrusions,- - - employing auditing/validation measures (e.g., security officer rounds, periodic monitoring of tamper seals),- - - ensuring authorized individuals are trustworthy and reliable in accordance with 10 CFR 73.56,- - - ensuring that authorized individuals are operating under established work management controls, and- - - conducting post maintenance testing to validate that changes are implemented correctly.' WHERE [Requirement_Id] = 5783
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} configures and documents CDA configuration settings to provide only essential capabilities and specifically prohibits, protects, and restricts the use of insecure functions, ports, protocols and services. {{Licensee/Applicant}} reviews CDAs [monthly] to identify and eliminate unnecessary functions, ports, protocols, and services.  {{Licensee/Applicant}} documents and employs automated mechanisms to prevent program execution.{{Licensee/Applicant}} uses [white-lists, black-lists, gray-lists] application control technologies.' WHERE [Requirement_Id] = 5784
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} develops, documents, and maintains an inventory of the components of CDAs that has the following attributes:- accurately reflects the current system configuration,- ensures that the location (logical and physical) of each component is consistent with the authorized boundary of the CDA,- provides the proper level of granularity deemed necessary for tracking and reporting and for effective property accountability,- updates the inventory of system components as an integral part of component installations and system updates,- employs automated mechanisms to maintain an up-to-date, complete, accurate, and readily available inventory of system components,- employs automated mechanisms to detect the addition of unauthorized components or devices into the environment and disables access by such components or devices or notifies designated {{Licensee/Applicant}} officials, and- documents the [names or roles] of the individuals responsible for administering those components.' WHERE [Requirement_Id] = 5785
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} develops, disseminates, and [annually] reviews and updates a formal, documented system and services acquisition policy that addresses purpose, scope, roles, responsibilities, management commitment, [coordination among {{Licensee/Applicant}} entities], associated system and service acquisition controls, and compliance.{{Licensee/Applicant}} develops, disseminates, and [annually] reviews and updates formal, documented procedures to facilitate the implementation of the system and services acquisition policy and associated system and services acquisition controls.' WHERE [Requirement_Id] = 5786
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} protects against supply chain threats and vulnerability by employing the following list of measures to protect against supply chain threats to maintain the integrity of the CDAs that are acquired:- establishment of trusted distribution paths,- validation of vendors, and- requiring tamper proof products or tamper evident seals on acquired products.{{Licensee/Applicant}} performs an analysis for each product acquisition to determine that the product provides the security requirements necessary to address the security controls inAppendixes B and C to RG 5.71.{{Licensee/Applicant}} uses heterogeneity to mitigate vulnerabilities associated with the use of a single vendor’s product.' WHERE [Requirement_Id] = 5787
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} requires that software developers employ software quality and validation methods to minimize flawed or malformed software.{{Licensee/Applicant}} establishes, implements, and documents requirements to require all tools used to perform cyber security tasks or SSEP functions to undergo a commercial qualification process similar to that for software engineering tools that are used to develop digital instrumentation and control systems.' WHERE [Requirement_Id] = 5788
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} documents and implements a program to ensure that new acquisitions contain security design information, capabilities or both to implement security controls in Appendix B to RG 5.71. Such security capabilities include the following:- being cognizant of evolving cyber security threats and vulnerabilities,- being cognizant of advancements in cyber security protective strategies and security controls,- conducting analyses of the effects that each advancement could have on the security, safety, and operation of critical assets, systems, CDAs, and networks and implementing these advancements in a timely manner, and- replacing legacy systems as they reach end of life with systems that incorporate security capabilities.{{Licensee/Applicant}} establishes timeframes to minimize the time it takes to deploy new and more effective protective strategies and security controls.' WHERE [Requirement_Id] = 5789
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} documents and requires that system developers and integrators of acquired CDAs create, implement, and document a security test and evaluation plan to ensure that the acquired products meet all specified security requirements that the products are free from known, testable vulnerabilities and malicious code by identifying and eliminating these following vulnerabilities and other vulnerabilities that may change with new technology:- weak, unproven, or nonstandard cryptographic modules,- insecure network protocols for sensitive communications,- known insecure software components or libraries,- known vulnerabilities,- insecure configuration files or options that act to control features of the application,- inadequate or inappropriate use of access control mechanisms to control access to system resources,- inappropriate privileges being granted to users, processes, or applications,- weak authentication mechanisms,- improperly or failing to validate input and output data,- insecure or inadequate logging of system errors or security-related information,- inadequately bounded buffers,- format string vulnerabilities,- privilege escalation vulnerabilities,- unsafe database transactions,- unsafe use of native function calls,- hidden functions and vulnerable features embedded in the code,- implemented security features do not themselves act to increase the risk of security vulnerabilities, increase susceptibility to cyber-attack, or reduce the reliability of design-basis functions.- use of unsupported or undocumented methods or functions, and- use of undocumented code or malicious functions that might allow either unauthorized access or use of the system or the system to behave beyond the system requirements.' WHERE [Requirement_Id] = 5790
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} documents and requires that system developers and integrators of acquired CDAs create, implement, and document a security test and evaluation plan to ensure that the acquired products meet all specified security requirements and developers cyber security program maintains the integrity of the acquired system until the product is delivered to the {{Licensee/Applicant}} by implementing equivalent security controls as described in RG 5.71 to prevent tampering and to provide high assurance that the integrity of the developed CDA is maintained until delivered to the licensee.{{Licensee/Applicant}} requires that CDA developers/integrators:- perform configuration management during CDA design, development, implementation, and operation,- manage and control changes to the CDA,- implement only {{Licensee/Applicant}} approved changes,- document approved changes to the CDA, and- track security flaws and flaw resolution.' WHERE [Requirement_Id] = 5791
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} requires the developer to perform and document that security requirements are verified and validated and that security controls implemented in the product and used to meet the requirements of this plan are tested to ensure they are effective per section A.4.1.2.{{Licensee/Applicant}} requires documentation of all of the following activities:- system design transformed into code, database structures, and related machine executable representations,- hardware and software configuration and setup,- software coding practices and testing,- communication configuration and setup (including the incorporation of reused software and commercial off-the-shelf products),- The results of unit tests performed to ensure that the code was developed correctly and accurately and completely reflects the security design configuration transformations from the requirements,- details of the implementation of each required security feature within the developed code base. The listing includes reference the coded functions and modules within the code base that were developed to implement the security features,- security configurations implemented to meet security design features specified in the requirements,- operating system security configurations implemented to meet security design features specified in the requirements are documented,- For programming languages that support static analysis source code scanners, results of the following are documented:- - - the static source code vulnerability analysis performed to inspect the developed code for potential security defects, poor programming practices, hidden functions, and vulnerable features within the code during the implementation of the code base and methods applied to eliminate these vulnerabilities,- - - the security defect tracking metrics used to capture and track the identification, type, classification, cause, and remediation of security defects found within the code, and- - - the defects encountered during the translation of the design features specified in the requirements into code.- For all programming languages, the results of the following are documented:- - - a dynamic source code vulnerability analysis performed to inspect the developed code for potential security defects, poor programming practices, hidden functions, and vulnerable features within the code during the implementation of the code base and methods applied to eliminate these vulnerabilities,- - - the security defect tracking metrics used to capture and track the identification, type, classification, cause, and remediation of security defects found within the code, and- - - the defects encountered during the translation of the design features specified in the requirements into code.' WHERE [Requirement_Id] = 5792
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} verifies and validates the results of the developer’s security testing in conducted in accordance with Section 12.5 above.{{Licensee/Applicant}} is responsible for the following:- testing CDA (e.g., offline on a comparable CDA) security devices, security controls, and software to ensure that they do not compromise the CDA or the operation of an interconnected CDA operation before installation,- testing to ensure that CDAs do not provide a pathway to compromise the CDA or other CDAs,- implementation of the security controls in Appendices B and C to RG 5.71 in accordance with the process described in Section 3.1.6 of Appendix A to RG 5.71- testing of the security controls for effectiveness, as described in Section 4.1.2 of Appendix A to RG 5.71,- performance of vulnerability scans, in accordance with Section 4.1.3 of Appendix A to RG 5.71 and Section 13.1 of this plan, against the CDA in its integrated state and correction, elimination, or discussion of discovered vulnerabilities,- installation and testing of the CDA in the target environment, and- performance of an acceptance review and test of the CDA security features.{{Licensee/Applicant}} documents the following:- Security controls implemented in accordance with Appendix B of RG 5.71.- Verification of the effectiveness of the security controls implemented in accordance with Appendix C.- Security design features developed to address the identified security requirements for the CDA (if any), in addition to the security controls implemented in accordance with Appendix B to 5.7.1. For each security feature or configuration to be implemented, the documentation includes a description of the feature, its method of implementation, and any configurable options associated with the feature are provided. Each security feature designed into the system is traceable to its corresponding security requirement. The security reviews of the implemented design by the cyber security organization responsible for the protection of the critical assets/systems/networks are documented. The review ensures that the security design configuration item transformations from the requirements implemented are correct, accurate, and complete.{{Licensee/Applicant}} requires [annual] audits of CDAs to verify the following:- The security controls present during testing remain in place and are functioning correctly in the production system.- CDAs are free from known vulnerabilities and security compromises and continue to provide information on the nature and extent of compromises, should they occur.- The change management [process/program] is functioning effectively and is recording configuration changes appropriately.' WHERE [Requirement_Id] = 5793
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} does the following:- Perform assessments and scans for vulnerabilities in CDAs [no less frequently than once a quarter] and at random intervals in accordance with Section 4.1.3 of Appendix A to RG 5.71 and when new potential CDA vulnerabilities are reported or identified.- Employ vulnerability scanning tools and techniques that promote interoperability among tools and automating parts of the vulnerability management process by:- - - enumerating platforms, software flaws, and improper configurations,- - - formatting and making transparent checklists and test procedures, and- - - measuring vulnerability impacts.- Analyze vulnerability scan reports and remediate vulnerabilities within a time period that will provide high assurance that CDAs are protected from cyber-attacks up to and including the DBT.- Eliminate similar vulnerabilities in other CDAs.- Employ vulnerability scanning tools that include the capability to update the list of cyber vulnerabilities scanned and update the list of CDA vulnerabilities scanned [monthly] and when new vulnerabilities are identified and reported.- Employ vulnerability scanning procedures that maximize the breadth and depth of coverage (i.e., CDA components scanned and vulnerabilities checked).- Discern and document what information associated with the CDA is discoverable by adversaries.- Perform security testing to determine the level of difficulty in circumventing the security controls of the CDA. [Testing methods include penetration testing, malicious user testing, and independent verification and validation].- Include privileged access authorization to CDAs for selected vulnerability scanning activities to facilitate more thorough scanning.- Employ automated mechanisms to compare the results of vulnerability scans over time to determine trends in CDA vulnerabilities and mitigation/flaw remediation activities.- Employ automated mechanisms to detect and notify authorized personnel of the presence of unauthorized software on CDAs.- Ensure that SSEP functions are not adversely impacted by the scanning process. Where this may occur, CDAs are removed from service or replicated (to the extent feasible) before scanning is conducted or be scheduled to occur during planned CDA outages whenever possible. Where {{Licensee/Applicant}} cannot conduct vulnerability scanning on a production CDA because of the potential for an adverse impact on SSEP functions, alternate controls (e.g., providing a replicated system or CDA to conduct scanning) are employed.The {{Licensee/Applicant}} reviews historic audit logs to determine if a vulnerability identified in the CDA has been previously exploited.' WHERE [Requirement_Id] = 5794
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'Protection and mitigation of risk are achieved by implementing - the defense-in-depth strategies discussed in Section 3.2 of RG 5.71, - the security controls described in Appendices B and C to RG 5.71, and - digital equipment and software cyber-attack detection, prevention, and recovery techniques and tools to the systems, structures, and components within the scope of the rule and - Section 4 of Appendix A of RG 5.71. {{Licensee/Applicant}} has the detailed information on how these requirements are implemented to achieve the high assurance objectives of security controls specified in this plan. The detailed information is available for NRC inspections and audits.' WHERE [Requirement_Id] = 5795
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'{{Licensee/Applicant}} established, implemented, and documented the criteria consistent with RG 5.71 for adverse conditions and the requirements for corrective action. The adverse impact resulting from a cyber security incident is evaluated, tracked, and adjusted in accordance with the {{Licensee/Applicant}} Corrective Action Program and in a manner consistent with RG 5.71.' WHERE [Requirement_Id] = 5796
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization monitors for unauthorized remote connections to the information system {{Assignment: organization-defined frequency}}, and takes appropriate action if an unauthorized connection is discovered.' WHERE [Requirement_Id] = 6980
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The information system supports and maintains the binding of {{Assignment: organization-defined security attributes}} to information in storage, in process, and in transmission.' WHERE [Requirement_Id] = 7568
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization provides employees with initial and {{Assignment: organization-defined frequency}} training in the employment and operation of environmental controls.' WHERE [Requirement_Id] = 7592
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization monitors open source information for evidence of unauthorized exfiltration or disclosure of organizational information {{Assignment: organization-defined frequency}}.' WHERE [Requirement_Id] = 7623
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization includes as part of security control assessments, {{Assignment: organization-defined frequency}}, [Selection: announced; unannounced], [Selection: in-depth monitoring; malicious user testing; penetration testing; red team exercises; [Assignment: organization-defined other forms of security testing]].' WHERE [Requirement_Id] = 7628
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization:a.    Determines the types of changes to the information system that are configuration controlled;b.    Approves configuration-controlled changes to the system with explicit consideration for security impact analyses;c.    Documents approved configuration-controlled changes to the system;d.    Retains and reviews records of configuration-controlled changes to the system;e.    Audits activities associated with configuration-controlled changes to the system; andf.    Coordinates and provides oversight for configuration change control activities through a Configuration Control Board that convenes [Selection: (one or more): {{Assignment: organization-defined frequency}}; [Assignment: organization-defined configuration change conditions]].' WHERE [Requirement_Id] = 7641
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization:(a)   Employs automated mechanisms {{Assignment: organization-defined frequency}} to detect the addition of unauthorized components/devices into the information system; and(b)   Disables network access by such components/devices or notifies designated organizational officials.' WHERE [Requirement_Id] = 7660
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization:a.    Enforces physical access authorizations for all physical access points (including designated entry/exit points) to the facility where the information system resides (excluding those areas within the facility officially designated as publicly accessible);b.    Verifies individual access authorizations before granting access to the facility;c.    Controls entry to the facility containing the information system using physical access devices and/or guards;d.    Controls access to areas officially designated as publicly accessible in accordance with the organization’s assessment of risk;e.    Secures keys, combinations, and other physical access devices;f.    Inventories physical access devices {{Assignment: organization-defined frequency}}; andg.    Changes combinations and keys {{Assignment: organization-defined frequency}} and when keys are lost, combinations are compromised, or individuals are transferred or terminated.' WHERE [Requirement_Id] = 7752
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization employs a penetration testing process that includes {{Assignment: organization-defined frequency}}, unannounced attempts to bypass or circumvent security controls associated with physical access points to the facility.' WHERE [Requirement_Id] = 7756
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization ensures that the facility undergoes {{Assignment: organization-defined frequency}} fire marshal inspections and promptly resolves identified deficiencies.' WHERE [Requirement_Id] = 7777
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization: a. Maintains temperature and humidity levels within the facility where the information system resides at [Assignment: organization-defined acceptable levels]; and b. Monitors temperature and humidity levels {{Assignment: organization-defined frequency}}.' WHERE [Requirement_Id] = 7778
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization employs automated mechanisms {{Assignment: organization-defined frequency}} to detect the presence of unauthorized software on organizational information systems and notify designated organizational officials.' WHERE [Requirement_Id] = 7815
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization tests malicious code protection mechanisms {{Assignment: organization-defined frequency}} by introducing a known benign, non-spreading test case into the information system and subsequently verifying that both detection of the test case and associated incident reporting occur, as required.' WHERE [Requirement_Id] = 7909
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization reassesses the integrity of software and information by performing {{Assignment: organization-defined frequency}} integrity scans of the information system.' WHERE [Requirement_Id] = 7918
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization manually initiates a transfer between active and standby information system components at least once per {{Assignment: organization-defined frequency}} if the mean time to failure exceeds [Assignment: organization-defined time period].' WHERE [Requirement_Id] = 7927
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The information system provides separate processing domains to enable finer-grained allocation of user privileges.The organization prohibits privileged access to the information system by non-organizational users.The organization:(a) Reviews {{Assignment: organization-defined frequency}} the privileges assigned to [Assignment: organization-defined roles or classes of users] to validate the need for such privileges; and(b) Reassigns or removes privileges, if necessary, to correctly reflect organizational mission/business needs.The information system ensures that [Assignment: organization-defined software] does not execute at higher privilege levels than users executing the software.' WHERE [Requirement_Id] = 8538
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization:a. Provides the means to associate [Assignment: organization-defined types of security attributes] having [Assignment: organization-defined security attribute values] with information in storage, in process, and/or in transmission;b. Ensures that the security attribute associations are made and retained with the information;c. Establishes the permitted {{Assignment: organization-defined security attributes}} for [Assignment: organization-defined information systems]; andd. Determines the permitted [Assignment: organization-defined values or ranges] for each of the established security attributes.The information system dynamically associates security attributes with {{Assignment: organization-defined subjects and objects}} in accordance with [Assignment: organization-defined security policies] as information is created and combined.The information system provides authorized individuals the capability to define or change the value of associated security attributes.The information system maintains the association and integrity of {{Assignment: organization-defined security attributes}} to {{Assignment: organization-defined subjects and objects}}.The information system supports the association of {{Assignment: organization-defined security attributes}} with {{Assignment: organization-defined subjects and objects}} by authorized individuals.The information system displays security attributes in human-readable form on each object that the system transmits to output devices to identify [Assignment: organization-identified special dissemination, handling, or distribution instructions] using [Assignment: organization-identified human-readable, standard naming conventions].The organization allows personnel to associate, and maintain the association of {{Assignment: organization-defined security attributes}} with {{Assignment: organization-defined subjects and objects}} in accordance with [Assignment: organization-defined security policies].The organization provides a consistent interpretation of security attributes transmitted between distributed information system components.The information system implements [Assignment: organization-defined techniques or technologies] with [Assignment: organization-defined level of assurance] in associating security attributes to information.The organization ensures that security attributes associated with information are only reassigned via re-grading mechanisms validated using [Assignment: organization-defined techniques or procedures].The information system provides authorized individuals the capability to define or change the type and value of security attributes available for association with subjects and objects.' WHERE [Requirement_Id] = 8546
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The information system protects against an individual falsely denying having performed [Assignment: organization-defined actions to be covered by non-repudiation].The information system:(a) Binds the identity of the information producer with the information to [Assignment: organization-defined strength of binding]; and(b) Provides the means for authorized individuals to determine the identity of the producer of the information.The information system:(a) Validates the binding of the information producer identity to the information at {{Assignment: organization-defined frequency}}; and(b) Performs [Assignment: organization-defined actions] in the event of a validation error.The information system maintains reviewer/releaser identity and credentials within the established chain of custody for all information reviewed or released. The information system:(a) Validates the binding of the information reviewer identity to the information at the transfer or release points prior to release/transfer between [Assignment: organization-defined security domains]; and(b) Performs [Assignment: organization-defined actions] in the event of a validation error.' WHERE [Requirement_Id] = 8597
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization updates the inventory of information system components as an integral part of component installations, removals, and information system updates.The organization:(a) Employs automated mechanisms {{Assignment: organization-defined frequency}} to detect the presence of unauthorized hardware, software, and firmware components within the information system; and(b) Takes the following actions when unauthorized components are detected: [Selection (one or more): disables network access by such components; isolates the components; notifies [Assignment: organization-defined personnel or roles]].The organization verifies that all components within the authorization boundary of the information system are either inventoried as a part of the system or recognized by another system as a component within that system.' WHERE [Requirement_Id] = 8645
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization obtains alternate telecommunications service providers that are separated from primary service providers to reduce susceptibility to the same threats.The organization:(a) Requires primary and alternate telecommunications service providers to have contingency plans;(b) Reviews provider contingency plans to ensure that the plans meet organizational contingency requirements; and(c) Obtains evidence of contingency testing/training by providers {{Assignment: organization-defined frequency}}.' WHERE [Requirement_Id] = 8672
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization performs security checks at the physical boundary of the facility or information system for unauthorized exfiltration of information or information system components.The organization employs guards and/or alarms to monitor every physical access point to the facility where the information system resides 24 hours per day, 7 days per week.The organization uses lockable physical casings to protect [Assignment: organization-defined information system components] from unauthorized physical access.The organization employs [Assignment: organization-defined security safeguards] to [Selection (one or more): detect; prevent] physical tampering or alteration of [Assignment: organization-defined hardware components] within the information system.The organization employs a penetration testing process that includes {{Assignment: organization-defined frequency}}, unannounced attempts to bypass or circumvent security controls associated with physical access points to the facility.' WHERE [Requirement_Id] = 8759
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The information system dynamically associates security attributes with {{Assignment: organization-defined subjects and objects}} in accordance with [Assignment: organization-defined security policies] as information is created and combined.' WHERE [Requirement_Id] = 8961
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The information system maintains the association and integrity of {{Assignment: organization-defined security attributes}} to {{Assignment: organization-defined subjects and objects}}.' WHERE [Requirement_Id] = 8963
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The information system supports the association of {{Assignment: organization-defined security attributes}} with {{Assignment: organization-defined subjects and objects}} by authorized individuals.' WHERE [Requirement_Id] = 8964
