UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The information system:a. Verifies the correct operation of [Assignment: organization-defined security functions];b. Performs this verification [Selection (one or more): [Assignment: organization-defined system transitional states]; upon command by user with appropriate privilege; {{Assignment: organization-defined frequency}}];c. Notifies minimally notifies system/security administrator of failed security verification tests; andd. [Selection (one or more): shuts the information system down; restarts the information system; [Assignment: organization-defined alternative action(s)]] when anomalies are discovered.' WHERE [Requirement_Id] = 14810
