UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization: a. Develops and documents access agreements for organizational information systems; b. Reviews and updates the access agreements {{Assignment: organization-defined frequency}}; and c. Ensures that individuals requiring access to organizational information and information systems:  1. Sign appropriate access agreements prior to being granted access; and 2. Re-sign access agreements to maintain access to organizational information systems when access agreements have been updated or {{Assignment: organization-defined frequency}}.' WHERE [Requirement_Id] = 16464
