UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization: (a) Implements a managed interface for each external telecommunication service; (b) Establishes a traffic flow policy for each managed interface; (c) Protects the confidentiality and integrity of the information being transmitted across each interface; (d) Documents each exception to the traffic flow policy with a supporting mission/business need and duration of that need; and (e) Reviews exceptions to the traffic flow policy {{Assignment: organization-defined frequency}} and removes exceptions that are no longer supported by an explicit mission/business need.' WHERE [Requirement_Id] = 16581
