UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'The organization employs virtualization techniques to support the deployment of a diversity of operating systems and applications that are changed {{Assignment: organization-defined frequency}}.' WHERE [Requirement_Id] = 16642
