UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'Include as part of security and privacy control assessments, {{Assignment: organization-defined frequency}}, [Selection: announced; unannounced], [Selection (one or more): in-depth monitoring; vulnerability scanning; malicious user testing; insider threat assessment; performance and load testing; [Assignment: organization-defined other forms of assessment]].' WHERE [Requirement_Id] = 28896
