UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'Require the developer of the system, system component, or system service, at all post-design phases of the system development life cycle, to: a. Create and implement a security and privacy assessment plan; b. Perform [Selection (one or more): unit; integration; system; regression] testing/evaluation {{Assignment: organization-defined frequency}} at [Assignment: organization-defined depth and coverage]; c. Produce evidence of the execution of the assessment plan and the results of the testing and evaluation; d. Implement a verifiable flaw remediation process; and e. Correct flaws identified during testing and evaluation.' WHERE [Requirement_Id] = 29338
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'Review the supply chain-related risks associated with suppliers or contractors and the system, system component, or system service they provide {{Assignment: organization-defined frequency}}.' WHERE [Requirement_Id] = 29349
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'a. Require the developer of the system, system component, or system service to follow a documented development process that: 1. Explicitly addresses security requirements; 2. Identifies the standards and tools used in the development process; 3. Documents the specific tool options and tool configurations used in the development process; and 4. Documents, manages, and ensures the integrity of changes to the process and/or tools used in development; and b. Review the development process, standards, tools, tool options, and tool configurations {{Assignment: organization-defined frequency}} to determine if the process, standards, tools, tool options and tool configurations selected and employed can satisfy [Assignment: organization-defined security and privacy requirements].' WHERE [Requirement_Id] = 29360
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'(a) Define quality metrics at the beginning of the development process; and (b) Provide evidence of meeting the quality metrics [Selection (one or more): {{Assignment: organization-defined frequency}}; [Assignment: organization-defined program review milestones]; upon delivery].' WHERE [Requirement_Id] = 29361
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'Inspect [Assignment: organization-defined systems or system components] [Selection (one or more): at random; at {{Assignment: organization-defined frequency}}, upon [Assignment: organization-defined indications of need for inspection]] to detect tampering.' WHERE [Requirement_Id] = 29381
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'Scan for counterfeit system components {{Assignment: organization-defined frequency}}.' WHERE [Requirement_Id] = 29386
