UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'Test intrusion-monitoring tools and mechanisms {{Assignment: organization-defined frequency}}.' WHERE [Requirement_Id] = 29531
UPDATE [dbo].[NEW_REQUIREMENT] SET [Requirement_Text]=N'a. Verify the correct operation of [Assignment: organization-defined security and privacy functions]; b. Perform this verification [Selection (one or more): [Assignment: organization-defined system transitional states]; upon command by user with appropriate privilege; {{Assignment: organization-defined frequency}}]; c. Notify [Assignment: organization-defined personnel or roles] of failed security and privacy verification tests; and d. [Selection (one or more): Shut the system down; Restart the system; [Assignment: organization-defined alternative action(s)]] when anomalies are discovered.' WHERE [Requirement_Id] = 29550
