INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (702, N'CRE.CORE.3', N'', N'<div class="sub-header-1">Question Intent</div><div>To determine if access to assets is granted based on their protection requirements.
<ul>
<li>Protection requirements describe how an assets exposure to sources of disruption and to the exploitation of vulnerabilities must be minimized.</li>
<li>Access requests should be granted in accordance with the protection requirements that have been established for the asset.</li>
<li>Access privileges are assigned and approved by asset owners based on the role of the person, object, or entity that is requesting access.</li>
</ul>
</div>', NULL, NULL, 1, 2, 22, NULL, NULL, 591, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, N'Is access (including identities and credentials) to assets granted based on their protection requirements?', N'<div class="sub-header-2">Implemented</div>
