INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (810, N'CRE.OP.21', N'', N'<div class="sub-header-1">Question Intent</div><div>To determine if protection and sustainment requirements of the critical service are considered during the selection of facilities.
<ul>
<li>The confidentiality, integrity, and availability requirements of the service are used to derive the collective protection and sustainment requirements of associated facility assets.</li>
<li>Protection requirements describe how an asset’s exposure to sources of disruption and to the exploitation of vulnerabilities must be minimized.</li>
<li>Sustainment requirements describe how assets must be kept operating when faced with disruptive events.</li>
<li>Protecting facility assets from vulnerabilities, threats, and risks requires the organization to develop appropriate protection and sustainment requirements.</li>
</ul>
</div>', NULL, NULL, 1, 20, 23, NULL, NULL, 715, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, N'Are protection and sustainment requirements of the critical service considered during the selection of facilities?', N'<div class="sub-header-2">Implemented</div>
