INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (812, N'CRE.OP.23', N'', N'<div class="sub-header-1">Question Intent</div><div>To determine if controls satisfy the established control objectives.
<ul>
<li>Controls analysis establishes a baseline from which the organization can begin to assess control effectiveness on a scheduled basis.</li>
</ul>
Controls analysis should identify gaps where a control objective is not adequately satisfied.
<ul>
<li>Analysis may include: (1) a design review of the control and its ability to meet the control objective (2) development and execution of tests that demonstrate the control''s capability.</li>
</ul>
</div>', NULL, NULL, 1, 22, 23, NULL, NULL, 717, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, N'Are control designs analyzed to identify gaps where control objectives are not adequately satisfied?', N'<div class="sub-header-2">Implemented</div>
