INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (843, N'CRE.OP.54', N'', N'<div class="sub-header-1">Question Intent</div><div>To determine if a link between the incident management process and other related processes is established.
<ul>
<li>Problem management is the process that an organization uses to identify recurring problems, examine root causes, and develop solutions for these problems to prevent future, similar incidents.</li>
<li>Formal linkages to other processes (risk management, change and configuration management, vulnerability management, etc.) that may impact an incident should be established.</li>
<li>Formal linkages strengthen the organization''s overall ability to prevent incidents.</li>
</ul>
</div>', NULL, NULL, 1, 53, 23, NULL, NULL, 728, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, N'Is there a link between the incident management process and other related processes (problem management, risk management, change management, etc.)?', N'<div class="sub-header-2">Implemented</div>
