INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (946, N'CRE.OP.157', N'', N'<div class="sub-header-1">Question Intent</div><div>To determine if a vulnerability analysis and resolution strategy has been developed.
<ul>
<li>The strategy for addressing vulnerability analysis and resolution should be documented, communicated to relevant stakeholders, and implemented.</li>
<li>The strategy may be a stand-alone document, embedded in a more comprehensive document, or be distributed across multiple documents.</li>
</ul>
</div>', NULL, NULL, 1, 156, 23, NULL, NULL, 759, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, N'Has a vulnerability analysis and resolution strategy been developed?', N'<div class="sub-header-2">Implemented</div>
