INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1029, N'CRE.MIL.70', N'', N'<div class="sub-header-1">Question Intent</div><div>To determine if management oversight exists. The intent of oversight is to ensure the direct, day-to-day monitoring of the service continuity management activities.
<ul>
<li>Management consists of the immediate level of managers that govern the day-to-day operation of the service continuity management activities.</li>
<li>Oversight provides visibility into the service continuity management activities so that issues can be identified and appropriate corrective actions can be taken when necessary.</li>
<li>Oversight activities could include regular meetings, written or oral status updates, auditing, or spot checks.</li>
</ul>
</div>', NULL, NULL, 1, 69, 24, NULL, NULL, 801, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, N'Is there management oversight of the performance of the service continuity activities?', N'<div class="sub-header-2">Implemented</div>
