INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1031, N'CRE.MIL.72', N'', N'<div class="sub-header-1">Question Intent</div><div>To determine if adequate funding has been provided. The intent of the question is to evaluate the completeness of the funding, not the completeness of the plan.
