INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1048, N'CRE.MIL.89', N'', N'<div class="sub-header-1">Question Intent</div><div>To determine if the performance of situational awareness is communicated to higher level managers to provide visibility and facilitate the resolution of issues.
<ul>
<li>Higher level managers include those in the organization above the immediate level of management responsible for the situational awareness process.</li>
<li>Communications are expected to be performed periodically (as defined by the organization) and may be event-driven when escalation is needed.</li>
</ul>
</div>', NULL, NULL, 1, 88, 24, NULL, NULL, 806, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, N'Is higher-level management aware of issues related to situational awareness?', N'<div class="sub-header-2">Implemented</div>
