INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1495, N'AC-17', N'<h5>Determine If</h5><p>The organization enforces boundary protections, deep packet inspection, and egress controls to monitor and block unauthorized data exfiltration from OT/ICS environments.</p><h5>Then Ask</h5><p>How do you monitor, detect, and block unauthorized attempts to exfiltrate data from ICS/OT systems, including deep packet inspection and boundary egress controls?</p>', NULL, NULL, NULL, 1, 0, 25, NULL, NULL, 680, NULL, N'Data‑Exfiltration Protection', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1496, N'AC-17', N'<h5>Determine If</h5><p>The organization monitors OT communications at system boundaries and key internal points, with tools(e.g., IDS) and logs to detect anomalies, ensuring security across IT-OT interfaces and vendor connections.</p><h5>Then Ask</h5><p>How do you monitor OT communications at system boundaries and internal points to detect anomalies and ensure security?</p>', NULL, NULL, NULL, 1, 1, 25, NULL, NULL, 680, NULL, N'Data‑Exfiltration Protection', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1497, N'AC-17(1)', N'<h5>Determine If</h5><p>The organization has a strategy to ensure encrypted traffic does not circumvent established flow control or content inspection.</p><h5>Then Ask</h5><p>How do you ensure that encrypted data streams do not bypass your information flow controls or inspections?</p>', NULL, NULL, NULL, 1, 2, 25, NULL, NULL, 680, NULL, N'Encrypted‑Flow Inspection Strategy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1498, N'AC-17(2)', N'<h5>Determine If</h5><p>The organization enforces approved information flow control policies within and between OT and IT systems by restricting unauthorized lateral movement, enforcing protocol-specific controls, and inspecting message content to prevent malicious or unauthorized communication. Information flow enforcement is implemented through protocol filtering, deep packet inspection, one-way or bidirectional flow restrictions, and segmentation strategies that ensure OT data remains secure.</p><h5>Then Ask</h5><p>How do you control and restrict data movement within OT networks and between OT and IT systems to prevent unauthorized access or lateral movement?</p>', NULL, NULL, NULL, 1, 3, 25, NULL, NULL, 680, NULL, N'Information‑Flow Enforcement', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1499, N'AC-17(3)', N'<h5>Determine If</h5><p>The organization prevents OT devices from direct Internet connectivity, with boundary controls and monitoring to mitigate external threat risks, ensuring secure isolation.</p><h5>Then Ask</h5><p>How do you prevent OT devices from direct Internet connectivity, and how do you monitor and mitigate external threat risks?</p>', NULL, NULL, NULL, 1, 4, 25, NULL, NULL, 680, NULL, N'Internet Isolation Controls', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1500, N'AC-17(4)', N'<h5>Determine If</h5><p>The organization routes OT Internet traffic through authenticated proxy servers at boundary devices, with logs, authentication checks, and monitoring to ensure secure egress, addressing OT-specific performance and vendor access needs.</p><h5>Then Ask</h5><p>How do you ensure OT traffic to the Internet is routed through authenticated proxy servers, and how do you monitor and secure this process?</p>', NULL, NULL, NULL, 1, 5, 25, NULL, NULL, 680, NULL, N'Proxy‑Based Egress Control', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1501, N'AC-17(9)', N'<h5>Determine If</h5><p>The organization isolates OT VLANs to block unauthorized communication, with monitoring and enforcement to ensure separation, maintaining network integrity.</p><h5>Then Ask</h5><p>How do you isolate OT VLANs to block unauthorized communication, and how do you monitor and enforce this separation?</p>', NULL, NULL, NULL, 1, 6, 25, NULL, NULL, 680, NULL, N'VLAN Isolation', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1502, N'AC-18', N'<h5>Determine If</h5><p>organization restricts external system usage to authorized personnel, ensuring security controls or formal agreements are in place.</p><h5>Then Ask</h5><p>What security measures or agreements must be verified before external systems can process or access organizational data, including OT data?</p>', NULL, NULL, NULL, 1, 0, 25, NULL, NULL, 681, NULL, N'External‑System Security Agreements', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1503, N'AC-18(1)', N'<h5>Determine If</h5><p>The organization prohibits non-organizational devices(e.g., personal phones, laptops, tablets, integrator devices) from connecting to networks, enforcing this through policies, technical controls(e.g., NAC), and monitoring, while defining exception processes for operational needs.</p><h5>Then Ask</h5><p>How do you prevent employees, vendors, integrators, and other third parties from connecting non-organizational devices to IT and OT networks?</p>', NULL, NULL, NULL, 1, 1, 25, NULL, NULL, 681, NULL, N'Non‑Org Device Prohibition', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1504, N'AC-18(3)', N'<h5>Determine If</h5><p>The organization ensures portable devices in OT environments have encrypted storage to protect data at rest, with procedures to verify compliance and address devices lacking native encryption capabilities.</p><h5>Then Ask</h5><p>How do you ensure portable devices in OT environments have encrypted storage to protect data at rest, and what measures address devices with limited encryption support?</p>', NULL, NULL, NULL, 1, 2, 25, NULL, NULL, 681, NULL, N'Portable‑Device Encryption', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1505, N'AC-18(5)', N'<h5>Determine If</h5><p>organization limits or controls how portable storage devices are used on external systems, mitigating risks of cross-contamination(especially for OT).</p><h5>Then Ask</h5><p>What policies govern portable storage device usage on external systems, and how do you prevent malware introduction to OT or IT systems?</p>', NULL, NULL, NULL, 1, 3, 25, NULL, NULL, 681, NULL, N'Portable‑Media on External Systems', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1506, N'AC-19', N'<h5>Determine If</h5><p>The organization enables secure sharing of sensitive OT information(e.g., configs, logs) with authorized parties, using encrypted channels or access controls, with verification processes to ensure recipients meet OT-specific security requirements.</p><h5>Then Ask</h5><p>How do you enable secure sharing of sensitive OT information with authorized parties, ensuring compliance with security and operational constraints?</p>', NULL, NULL, NULL, 1, 4, 25, NULL, NULL, 681, NULL, N'Sensitive‑Data Sharing Controls', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1507, N'AC-19(5)', N'<h5>Determine If</h5><p>The organization restricts transient device use in OT environments to pre-approved, hardened systems with minimal privileges and appropriate controls.</p><h5>Then Ask</h5><p>What controls govern the use of transient or mobile devices (e.g., tooling laptops, contractor systems) within ICS/OT environments?</p>', NULL, NULL, NULL, 1, 5, 25, NULL, NULL, 681, NULL, N'Transient‑Device Control', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1508, N'AC-20', N'<h5>Determine If</h5><p>The organization identifies all OT communication paths, enforces zoning and segmentation for internal traffic, applies encryption or secure tunnels (e.g., TLS, VPNs) for data leaving protected OT zones, and implements compensating controls (e.g., unidirectional gateways, firewall rules) for systems that cannot natively encrypt traffic. Protections are reviewed regularly to ensure controls match network exposure.</p><h5>Then Ask</h5><p>How does the organization protect OT communications from interception, especially when traffic crosses into IT, cloud, or external networks and  What compensating controls are in place for protocols or devices that do not support encryption? How often are these protections reviewed or audited?</p>', NULL, NULL, NULL, 1, 0, 25, NULL, NULL, 682, NULL, N'Communication‑Segmentation & Encryption', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1509, N'AC-20(1)', N'<h5>Determine If</h5><p>The organization enforces a default-deny network policy for OT traffic, allowing only explicitly approved exceptions, with monitoring and documentation to ensure compliance and detect unauthorized flows, especially for critical OT operations.</p><h5>Then Ask</h5><p>How do you enforce a default-deny policy for OT network traffic, document exceptions, and monitor compliance to prevent unauthorized access or data flows?</p>', NULL, NULL, NULL, 1, 1, 25, NULL, NULL, 682, NULL, N'Default‑Deny Network Policy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1510, N'AC-20(2)', N'<h5>Determine If</h5><p>The organization can promptly disconnect remote access and isolate the OT environment when necessary, without causing unacceptable disruption to operations.</p><h5>Then Ask</h5><p>In the event of an incident, do you have the capability to immediately isolate the OT environment and prohibit non-local traffic?</p>', NULL, NULL, NULL, 1, 0, 25, NULL, NULL, 683, NULL, N'Emergency Remote‑Access Isolation', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1511, N'AC-21', N'<h5>Determine If</h5><p>The organization utilizes encryption for remote access(e.g., SSH, TLS, IPsec)</p><h5>Then Ask</h5><p>Does the entity employ encryption for remote access?(e.g. SSH instead of Telnet, WinRM over 5986 instead of 5985, RDP using TLS, TeamViewer using AES-256. VNC by default is cleartext, FTP vs SFTP, SNMPv1/2, UDP 161,162).</p>', NULL, NULL, NULL, 1, 1, 25, NULL, NULL, 683, NULL, N'Encrypted Remote Access', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1512, N'AC-4', N'<h5>Determine If</h5><p>The organization requires internal system authentication for OT remote connections post-initial access, including transitions from IT to OT environments (e.g., OT DMZ jump hosts or OT bastion hosts), with monitoring and logs to ensure compliance. Compensating controls are applied where OT devices lack advanced authentication features.</p><h5>Then Ask</h5><p>How do you enforce re-authentication when transitioning from IT to OT systems or via remote access mechanisms (e.g., via jump hosts or bastion hosts), and what measures are in place for legacy OT devices that cannot support additional authentication?</p>', NULL, NULL, NULL, 1, 2, 25, NULL, NULL, 683, NULL, N'Layered Authentication', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1513, N'AC-4(4)', N'<h5>Determine If</h5><p>The organization enforces multi-factor authentication(MFA) for OT remote access, with policies, verification, and compensating controls(e.g., physical tokens) for devices unable to support standard MFA, ensuring secure access.</p><h5>Then Ask</h5><p>How do you enforce MFA for OT remote access, and what compensating controls address devices unable to support it?</p>', NULL, NULL, NULL, 1, 3, 25, NULL, NULL, 683, NULL, N'MFA for Remote OT Access', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1514, N'IA-11', N'<h5>Determine If</h5><p>The organization enforces remote access governance for OT systems, specifying approved methods(e.g., VPN, SSH), restrictions(e.g., IP whitelisting), and authentication(e.g., MFA), with monitoring and compensating controls for OT devices unable to support standard security protocols.</p><h5>Then Ask</h5><p>How do you govern, authorize, and monitor remote access methods for OT systems, including usage restrictions, authentication, and compensating measures for devices with limited capabilities?</p>', NULL, NULL, NULL, 1, 4, 25, NULL, NULL, 683, NULL, N'Remote Access Governance', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1515, N'IA-2(11)', N'<h5>Determine If</h5><p>The organization ensures remote access to OT networks requires robust authentication(e.g., MFA, tokens) before connection, addressing OT-specific latency/availability constraints</p><h5>Then Ask</h5><p>How do you ensure remote access to OT networks requires authentication before connection, and what controls address OT-specific constraints?</p>', NULL, NULL, NULL, 1, 5, 25, NULL, NULL, 683, NULL, N'Remote Access Management', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1516, N'SC-32', N'<h5>Determine If</h5><p>The organization authorizes and documents privileged remote access(e.g. firmware updates/software updates, remote shutdowns/restarts, safety parameter changes) to OT systems only when necessary and ensures all remote privileged actions generate assessable evidence(e.g., logs, session recordings, approvals).</p><h5>Then Ask</h5><p>How does your organization ensure that privileged commands executed remotely are logged and reviewed for security and compliance(e.g. firmware updates, shutdowns)?</p>', NULL, NULL, NULL, 1, 6, 25, NULL, NULL, 683, NULL, N'Remote Privileged‑Action Evidence', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1517, N'SC-7', N'<h5>Determine If</h5><p>The organization restricts network-based OT privileged commands to approved methods(e.g., secure consoles), with documentation and monitoring to ensure compliance, managing approvals for operational needs.</p><h5>Then Ask</h5><p>How do you restrict and document network-based OT privileged commands, and manage approvals for operational needs?</p>', NULL, NULL, NULL, 1, 7, 25, NULL, NULL, 683, NULL, N'Remote Privileged‑Command Control', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1518, N'SC-7', N'<h5>Determine If</h5><p>The organization restricts and monitors unauthorized remote management protocols in OT(e.g., Telnet), with controls and processes to manage vendor-related risks, ensuring secure access.</p><h5>Then Ask</h5><p>How do you restrict and monitor unauthorized remote management protocols in OT, and how do you manage vendor-related risks?</p>', NULL, NULL, NULL, 1, 8, 25, NULL, NULL, 683, NULL, N'Remote‑Protocol Restrictions', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1519, N'SC-7', N'<h5>Determine If</h5><p>The organizaiton manages wireless antenna placement and transmission power are managed to reduce external interception while ensuring operational coverage.</p><h5>Then Ask</h5><p>How do you manage antenna placement and power levels to balance security and operational needs?</p>', NULL, NULL, NULL, 1, 0, 25, NULL, NULL, 684, NULL, N'Antenna Placement & Power Mgmt', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1520, N'SC-7(5)', N'<h5>Determine If</h5><p>Unused wireless functions(Wi-Fi, Bluetooth, RF) are disabled on IT and OT devices before deployment, with compliance verification.</p><h5>Then Ask</h5><p>How do you ensure unused wireless capabilities are disabled on IT and OT devices before deployment?</p>', NULL, NULL, NULL, 1, 1, 25, NULL, NULL, 684, NULL, N'Disable Unused Wireless Functions', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1521, N'SC-7(8)', N'<h5>Determine If</h5><p>The organization establishes, enforces, and secures wireless access configurations, connection policies, and implementation guidance for all wireless technologies(Wi-Fi, Bluetooth, RFID, 5G, Zigbee, Microwave, Radio).</p><h5>Then Ask</h5><p>How do you restrict and secure wireless access in OT environments, and what compensating controls address devices with limited wireless capabilities?</p>', NULL, NULL, NULL, 1, 2, 25, NULL, NULL, 684, NULL, N'Wireless Access Hardening', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1522, N'SC-8', N'<h5>Determine If</h5><p>Wireless access is secured with authentication(users, devices) and encryption, or compensating controls are in place for OT systems lacking built-in security.</p><h5>Then Ask</h5><p>How do you authenticate and encrypt wireless connections, and what alternatives exist for OT devices without these capabilities?</p>', NULL, NULL, NULL, 1, 3, 25, NULL, NULL, 684, NULL, N'Wireless Auth & Encryption', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1523, N'CA-2', N'<h5>Determine If</h5><p>The organization establishes, approves, and updates an OT cybersecurity policy, with defined controls, roles, and resources, ensuring alignment with operational and security objectives.</p><h5>Then Ask</h5><p>How do you establish, approve, and update an OT cybersecurity policy with defined controls, roles, and resources?</p>', NULL, NULL, NULL, 1, 0, 25, NULL, NULL, 685, NULL, N'Cybersecurity Policy Governance', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1524, N'PL-1', N'<h5>Determine If</h5><p>The organization conducts independent OT cybersecurity assessments(e.g., third-party audits), with processes to address vendor and operational dependencies, ensuring objective evaluation.</p><h5>Then Ask</h5><p>How do you conduct independent OT cybersecurity assessments, and how do you address vendor and operational dependencies?</p>', NULL, NULL, NULL, 1, 1, 25, NULL, NULL, 685, NULL, N'Independent OT‑Security Assessments', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1525, N'PL-2', N'<h5>Determine If</h5><p>The organization has a cybersecurity program that effectively manages the unique operational, technical, and safety needs of its OT environment. The program supports continuity of critical functions, accounts for OT-specific risks, and includes input from engineering, operations, and vendors.</p><h5>Then Ask</h5><p>Does the organization have a cybersecurity program that specifically addresses OT? How is it tailored to support the continuity, safety, and reliability of critical OT functions, and who is involved in shaping or maintaining the program?</p>', NULL, NULL, NULL, 1, 2, 25, NULL, NULL, 685, NULL, N'OT Cybersecurity Program', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1526, N'PL-4', N'<h5>Determine If</h5><p>The organization defines, documents, and trains OT staff on security roles and responsibilities within the cybersecurity plan, ensuring clarity and accountability.</p><h5>Then Ask</h5><p>How do you define, document, and train OT staff on security roles and responsibilities within the cybersecurity plan?</p>', NULL, NULL, NULL, 1, 3, 25, NULL, NULL, 685, NULL, N'Role & Responsibility Definition', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1527, N'PL-7', N'<h5>Determine If</h5><p>The organization obtains signed acknowledgments from OT users confirming rules of behavior before access, with records and verification processes, integrating with physical security to ensure compliance and awareness.</p><h5>Then Ask</h5><p>How do you ensure OT users acknowledge rules of behavior before access, and how do you verify and integrate with physical security?</p>', NULL, NULL, NULL, 1, 4, 25, NULL, NULL, 685, NULL, N'Rules‑of‑Behavior Acknowledgment', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
