INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1529, N'PE-3', N'<h5>Determine If</h5><p>Physical entry logs or escort requirements are enforced for ICS areas, and environmental controls(e.g., tamper-evident seals) are in place to prevent sabotage or unauthorized modifications.</p><h5>Then Ask</h5><p>How do you monitor and log physical entry to ICS-critical areas, and what tamper-evident or environmental controls exist to detect unauthorized physical access?</p>', NULL, NULL, NULL, 1, 0, 25, NULL, NULL, 687, NULL, N'Entry Logging & Tamper Detection', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
