INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1532, N'PE-5', N'<h5>Determine If</h5><p>The organization maintains robust physical security for ICS environments(locked cabinets, restricted rooms, video surveillance), preventing unauthorized persons from accessing control systems directly.</p><h5>Then Ask</h5><p>What physical security controls(e.g., locks, guards, cameras) do you have in place to ensure only authorized personnel can access ICS equipment or networking closets?</p>', NULL, NULL, NULL, 1, 2, 25, NULL, NULL, 688, NULL, N'ICS Physical‑Security Controls', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
