INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1534, N'SA-2', N'<h5>Determine If</h5><p>The organization allocates resources(e.g., budget, staff) to the OT cybersecurity program, with planning and tracking to ensure alignment with strategy and compliance needs.</p><h5>Then Ask</h5><p>How do you allocate resources to the OT cybersecurity program to ensure alignment with strategy and compliance needs?</p>', NULL, NULL, NULL, 1, 0, 25, NULL, NULL, 689, NULL, N'OT‑Security Resource Allocation', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
