INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1541, N'SR-5(1)', N'<h5>Determine If</h5><p>The organization includes OT security requirements in acquisition contracts, with monitoring and verification to ensure vendor compliance, reducing supply chain risks.</p><h5>Then Ask</h5><p>How do you ensure OT security requirements are in acquisition contracts, and how do you monitor vendor compliance to reduce supply chain risks?</p>', NULL, NULL, NULL, 1, 2, 25, NULL, NULL, 691, NULL, N'Security Requirements in Contracts', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact], [Scope], [Recommend_Action], [Risk_Addressed], [Services], [Outcome], [Security_Practice], [Implementation_Guides]) VALUES (1542, N'SR-6', N'<h5>Determine If</h5><p>The organization monitors ICS/OT vendor and integrator accounts for unauthorized use, maintaining oversight after onboarding to prevent security gaps.</p><h5>Then Ask</h5><p>How do you monitor ICS/OT vendor and integrator accounts post-onboarding to prevent unauthorized use or drift from security baselines?</p>', NULL, NULL, NULL, 1, 3, 25, NULL, NULL, 691, NULL, N'Vendor‑Account Monitoring', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
