INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1408, 'OBS-DISCOVERY', N'The organization does not verify user identity before issuing OT credentials. HR and physical security coordination is informal or absent.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1408, 'OBS-IMPACT', N'Granting credentials to unauthorized individuals increases the risk of malicious actions within critical OT systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1408, 'OBS-RECOMMENDATION', N'Verify identity via HR and ID checks before issuing credentials; integrate with badge systems; audit user lists quarterly.')
