INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1409, 'OBS-IMPACT', N'Exposed credentials during distribution could result in early compromise before account controls are applied.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1409, 'OBS-RECOMMENDATION', N'Distribute via encrypted email or physical handoff; use manual verification for legacy systems; log all distributions.')
