INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1410, 'OBS-DISCOVERY', N'The organization does not have a defined process for responding to lost or compromised OT authenticators. Response efforts are delayed and may impact continuity.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1410, 'OBS-IMPACT', N'Delay in revocation allows adversaries continued access, risking critical process manipulation or shutdowns.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1410, 'OBS-RECOMMENDATION', N'Revoke and reissue within hours; pre-stage spare tokens; test response plans quarterly.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1410, 'OBS-RISK', N'Slow response to compromised authenticators could enable OT breaches, risking safety or uptime.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1410, 'OBS-RISK-STATEMENT', N'Slow response to compromised authenticators could enable OT breaches, risking safety or uptime.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1410, 'OBS-VULNERABILITY', N'Inefficient lost authenticator management processes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1411, 'OBS-DISCOVERY', N'The organization does not enforce strong password policies across OT systems. Devices may use default or weak credentials, and legacy systems lack compensating protections.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1411, 'OBS-IMPACT', N'Compromised passwords could allow attackers to gain unauthorized control over critical OT assets.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1411, 'OBS-RECOMMENDATION', N'Implement a 15-character minimum password policy with complexity requirements. Distribute credentials using encrypted methods and ensure timely revocation. For legacy systems, apply compensating controls such as access segmentation, multifactor jump hosts, or physical protections.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1411, 'OBS-RISK', N'Weak passwords could be easily cracked, compromising OT system access.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1411, 'OBS-RISK-STATEMENT', N'Default or weak OT credentials increase the risk of unauthorized access, lateral movement, or control manipulation by adversaries exploiting password-based attack vectors.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1411, 'OBS-VULNERABILITY', N'Inadequate enforcement of password complexity in OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1412, 'OBS-DISCOVERY', N'Some OT user accounts or devices share credentials, or lack unique identification. Multi-user access terminals and legacy systems do not link physical presence to logical access, reducing traceability and accountability. MFA is not in place, and no compensating controls have been implemented.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1412, 'OBS-IMPACT', N'Impersonated users could make unauthorized changes to OT processes or disrupt operations without detection.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1412, 'OBS-RECOMMENDATION', N'Assign unique IDs to each OT user and device.

Prevent reuse of IDs and disable unused accounts promptly.

Link logical access to physical security systems, such as badge readers or control room logs.

Where MFA is not practical, implement compensating controls like log correlation with badge access, jump hosts with strong authentication, or real-time session logging.

Regularly review access logs for anomalies and shared account usage.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1412, 'OBS-RISK', N'Reused or shared IDs reduce accountability and enable unauthorized access.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1412, 'OBS-RISK-STATEMENT', N'Without unique IDs and identity-linked access, users may impersonate others or access OT systems anonymously. This weakens accountability and increases the risk of unauthorized changes, particularly when access is shared or not tracked to individuals.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1412, 'OBS-VULNERABILITY', N'Lack of enforced unique identity and absence of layered or compensating authentication controls in OT.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1413, 'OBS-DISCOVERY', N'The organization does not enforce lockout thresholds or relies on unlimited login attempts, exposing OT systems to brute-force attacks')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1413, 'OBS-IMPACT', N'Unrestricted login attempts could allow attackers to compromise accounts, leading to unauthorized system access and potential manipulation of OT processes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1413, 'OBS-RECOMMENDATION', N'Set 5-attempt lockouts; use alerts for continuous systems; train operators on manual resets.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1413, 'OBS-RISK', N'Lack of invalid login attempt thresholds leaves OT systems vulnerable to brute-force attacks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1413, 'OBS-RISK-STATEMENT', N'Unrestricted login attempts could enable brute-force attacks and allow accounts to be compromised.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1413, 'OBS-VULNERABILITY', N'Absence of account lockout or invalid attempt thresholds for OT systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1414, 'OBS-DISCOVERY', N'Network-based privileged commands are not formally controlled, and no documentation exists to justify why certain users require remote privileged access. In OT, logging limitations further obscure accountability.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1414, 'OBS-IMPACT', N'Unauthorized privileged command execution over networks could modify critical OT processes, disable defenses, or introduce undetected backdoors.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1414, 'OBS-RECOMMENDATION', N'The organization should require formal approval and justification for network-based privileged command execution. All remote privileged access should be logged, monitored, and limited to specific operational needs. Where logging is not feasible, compensating controls such as session recording should be implemented.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1414, 'OBS-RISK', N'Network-based privileged commands are executed without formal justification or oversight.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1414, 'OBS-RISK-STATEMENT', N'Unauthorized remote privileged commands could be used to modify OT system configurations, disable security controls, or introduce malware. Without documentation, privileged access could be abused or misused without detection.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1414, 'OBS-VULNERABILITY', N'Lack of approval, monitoring, and control over remote privileged command execution in OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1415, 'OBS-DISCOVERY', N'Organization is not separating privileged vs. non-privileged tasks; staff regularly use privileged accounts for daily operation')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1415, 'OBS-IMPACT', N'Inappropriate use of privileged accounts could lead to undetected system changes, privilege escalation, or complete system compromise impacting operational integrity and safety.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1415, 'OBS-RECOMMENDATION', N'Restrict privileged accounts to security tasks only; assign non-privileged accounts for routine ops; log all usage; enforce role or group separation')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1415, 'OBS-RISK', N'Use of privileged accounts for daily tasks increases the risk of credential misuse or escalation by adversaries.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1415, 'OBS-RISK-STATEMENT', N'Privileged accounts used for routine tasks could be misused or compromised and raises the risk of unauthorized activities, heightens risk for adversaries to gain access.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1415, 'OBS-VULNERABILITY', N'Absence of role-based access enforcement for privileged OT accounts; failure to implement least privilege.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1416, 'OBS-DISCOVERY', N'There is no formal review process for privileged accounts, leading to privilege creep(users retaining access they no longer need). Privileged actions are not consistently logged, making it difficult to detect misuse or unauthorized changes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1416, 'OBS-IMPACT', N'Unreviewed privileged access could lead to unnoticed misuse of critical OT functions, delaying detection of insider threats or operational sabotage.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1416, 'OBS-RECOMMENDATION', N'The organization should implement a structured review process for privileged accounts(e.g., quarterly audits) to ensure privileges remain aligned with job roles. All privileged functions should be logged where possible, and compensating controls(e.g., monitoring privileged session activities) should be implemented for OT systems lacking robust logging.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1416, 'OBS-RISK', N'Privileged accounts are not reviewed or logged, increasing risk of unauthorized or excessive access.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1416, 'OBS-RISK-STATEMENT', N'Without privilege reviews, users may retain excessive access, increasing insider threat risks. Lack of logging for privileged functions prevents detection of unauthorized activity, potentially delaying incident response.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1416, 'OBS-VULNERABILITY', N'Lack of periodic privileged access review and logging in OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1417, 'OBS-DISCOVERY', N'Privileged access is not well-defined or enforced, allowing over-privileged users to perform unauthorized security functions(e.g., operators modifying security settings, engineers using admin accounts for daily tasks). Additionally, non-privileged users are not restricted from executing privileged commands, increasing security risks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1417, 'OBS-IMPACT', N'Over-privileged or misused accounts could disable logging, alter system configurations, or execute unauthorized commands, undermining OT system integrity and availability.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1417, 'OBS-RECOMMENDATION', N'The organization should implement strict RBAC(Role-Based Access Control) to restrict privileges, enforce separation between privileged and non-privileged activities, and audit privileged actions. Technical controls(e.g., group policies, access control lists) should block non-privileged users from executing privileged commands.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1417, 'OBS-RISK', N'Privileged accounts are not strictly controlled, allowing users to perform unauthorized security functions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1417, 'OBS-RISK-STATEMENT', N'Over-privileging could enable accidental or malicious changes to OT systems, risking downtime or safety incidents. Non-privileged users performing privileged actions could bypass security controls, leading to potential compromise or operational failures.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1417, 'OBS-VULNERABILITY', N'Lack of privileged role separation and enforcement in OT system access controls.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1418, 'OBS-DISCOVERY', N'Organization does not control or monitor access to ICS documentation repositories, leaving critical information exposed to unauthorized users.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1418, 'OBS-IMPACT', N'Loss or manipulation of ICS system information could lead to operational sabotage or unauthorized system changes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1418, 'OBS-RECOMMENDATION', N'Implement strong access control lists, encrypt sensitive documents, monitor access logs, and restrict repository access to authorized personnel only.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1418, 'OBS-RISK', N'Unauthorized access to sensitive OT data could aid reconnaissance or attack preparation.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1418, 'OBS-RISK-STATEMENT', N'Lack of restrictions or monitoring could allow an insider or external actor to access, alter, or steal sensitive ICS information, enabling targeted attacks or operational disruption.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1418, 'OBS-VULNERABILITY', N'Insufficient protection of ICS document repositories exposes critical operational information to malicious actors.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1419, 'OBS-DISCOVERY', N'The organization does not enforce least privilege access for OT systems, allowing users to retain excessive privileges beyond their job duties(e.g., operators with admin rights, engineers with full network access). Role-based access controls(RBAC) are not consistently applied or reviewed, increasing the risk of unauthorized modifications and security gaps.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1419, 'OBS-IMPACT', N'Users with excessive privileges could alter critical system settings, disable security protections, or disrupt operations, either unintentionally or maliciously.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1419, 'OBS-RECOMMENDATION', N'Enforce least privilege via RBAC; deploy monitoring tools with OT-compatible thresholds; conduct bi-annual privilege audits with operator input.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1419, 'OBS-RISK', N'Over-privileged OT system accounts increase the risk of unauthorized or accidental changes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1419, 'OBS-RISK-STATEMENT', N'Over-privileging could enable accidental or malicious changes to OT systems, risking downtime or safety incidents. Excessive access also increases the attack surface, where a compromised account could be leveraged to manipulate critical processes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1419, 'OBS-VULNERABILITY', N'Failure to enforce least privilege access principles in OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1420, 'OBS-DISCOVERY', N'The organization does not enforce separation of duties, allowing individuals to perform conflicting roles(e.g., creating and approving their own accounts, making unvalidated security changes). This increases the risk of unauthorized actions and weakens accountability.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1420, 'OBS-IMPACT', N'Without proper separation, malicious changes could be made to OT configurations, leading to sabotage, fraud, or critical system compromise.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1420, 'OBS-RECOMMENDATION', N'The organization should formally define and document critical duties that require separation and enforce those separations through system access controls. Role-based access control(RBAC) should be implemented to prevent conflicting privileges(e.g., access provisioning vs. access auditing) from being assigned to the same user. Regular audits should be conducted to validate compliance with separation of duties policies, ensuring no single individual has unchecked control over OT and IT-critical systems. If system-based enforcement is not feasible, manual compensating controls, such as dual authorization for critical actions, should be implemented.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1420, 'OBS-RISK', N'Lack of separation of duties could allow individuals to approve their own actions without oversight.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1420, 'OBS-RISK-STATEMENT', N'Without proper separation of duties, a single individual could bypass security controls, approve unauthorized changes, or manipulate OT systems without oversight, increasing the risk of fraud, insider threats, and security breaches.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1420, 'OBS-VULNERABILITY', N'Absence of enforced separation of duties policies and technical or manual controls for OT-critical actions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1421, 'OBS-DISCOVERY', N'The organization does not automatically terminate OT sessions under defined conditions or lacks procedures for systems requiring constant uptime, leaving sessions open indefinitely.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1421, 'OBS-IMPACT', N'Hijacked OT sessions could lead to unauthorized configuration changes, system disruptions, or bypassing of safety mechanisms, causing unplanned shutdowns or process instability.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1421, 'OBS-RECOMMENDATION', N'Enforce auto-termination after a specified timeout. Provide documented overrides with logging for systems needing continuous operation, and ensure all exceptions are justified and monitored.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1421, 'OBS-RISK', N'Persistent sessions could be hijacked, allowing attackers or unauthorized personnel to execute unauthorized actions on OT systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1421, 'OBS-RISK-STATEMENT', N'Persistent or abandoned sessions can be hijacked by unauthorized users, compromising OT security and potentially disrupting operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1421, 'OBS-VULNERABILITY', N'Lack of enforced session timeout mechanisms and session monitoring for OT operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1422, 'OBS-DISCOVERY', N'The organization does not enforce or monitor concurrent OT session limits, nor does it document exceptions for critical operations, allowing multiple sessions per account without oversight.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1422, 'OBS-IMPACT', N'Concurrent session misuse could delay detection of unauthorized changes, mask malicious activities, and undermine system reliability and safety tracking.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1422, 'OBS-RECOMMENDATION', N'Limit concurrent OT sessions by role or account type, document exceptions for critical needs, and log all concurrent usage to ensure accountability and resource availability.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1422, 'OBS-RISK', N'Multiple concurrent sessions without control could obscure accountability, enabling unauthorized actions or system misuse.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1422, 'OBS-RISK-STATEMENT', N'Unrestricted concurrent sessions can strain OT resources, obscure user accountability, and increase the risk of operational errors or malicious actions going undetected.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1422, 'OBS-VULNERABILITY', N'Lack of enforced concurrent session controls for OT accounts.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1423, 'OBS-DISCOVERY', N'The organization does not consistently lock OT devices after inactivity or obscure displays on systems that require constant visibility, leaving screens exposed and accessible.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1423, 'OBS-IMPACT', N'Unauthorized access to OT terminals could result in unapproved system changes, suppression of monitoring capabilities, or disruption of critical processes affecting safety and operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1423, 'OBS-RECOMMENDATION', N'Set 15-minute locks with screen blanking; use physical oversight for visibility needs; audit compliance.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1423, 'OBS-RISK', N'Unlocked OT terminals may expose critical functions or sensitive data to unauthorized users, increasing the risk of tampering or misuse.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1423, 'OBS-RISK-STATEMENT', N'Unlocked devices could expose sensitive OT data or controls, risking unauthorized use or tampering.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1423, 'OBS-VULNERABILITY', N'Lack of enforced device locking and inactivity timeout controls on OT operator workstations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1424, 'OBS-DISCOVERY', N'The organization does not display system-use banners or provide compensating notifications, leaving users unaware of monitoring, legal accountability, or acceptable use policies.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1424, 'OBS-IMPACT', N'Lack of user acknowledgment weakens enforcement of security policies, complicates response to misuse, and increases legal risk in the event of insider threats or regulatory investigations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1424, 'OBS-RECOMMENDATION', N'Implement system-use banners on all systems with login screens, requiring explicit acknowledgment before access. For devices without login screens (e.g., shared HMIs, engineering workstations), deploy physical signage, digital notices, or pre-access acknowledgment mechanisms. Include language clearly stating monitoring, authorization, and usage restrictions. Regularly review content with legal and compliance teams to ensure enforceability.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1424, 'OBS-RISK', N'Failure to present system-use banners reduces legal and security accountability for OT system use.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1424, 'OBS-RISK-STATEMENT', N'Without system-use notices, users may be unaware of monitoring and acceptable use policies, weakening legal enforcement and undermining deterrence. This reduces the ability to pursue disciplinary or legal action following unauthorized activity.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1424, 'OBS-VULNERABILITY', N'Absence of login banners or alternate notification mechanisms for OT systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1425, 'OBS-DISCOVERY', N'Organization has over-provisioned what can be done by non-authenticated users.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1425, 'OBS-IMPACT', N'Allowing unauthenticated access to critical functions could result in unauthorized system modifications, operational instability, or accidental or malicious exposure of sensitive system information.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1425, 'OBS-RECOMMENDATION', N'Overly permissive or undefined unauthenticated actions can lead to unauthorized changes or data exposure, compromising OT security.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1425, 'OBS-RISK', N'Overly permissive unauthenticated actions could enable unauthorized users to manipulate control settings or access sensitive OT data.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1425, 'OBS-RISK-STATEMENT', N'Overly permissive or undefined unauthenticated actions can lead to unauthorized changes or data exposure, compromising OT security.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1425, 'OBS-VULNERABILITY', N'Lack of authentication enforcement for critical OT functions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1426, 'OBS-DISCOVERY', N'Organization does not have well-documented, updated policies and procedures covering OT-specific access risks(e.g., vendor/maintenance access in unobserved areas), disseminated to staff, and periodically reviewed for relevance and compliance with operational needs')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1426, 'OBS-IMPACT', N'Unauthorized third-party access to OT systems could result in system compromise, operational downtime, leakage of sensitive configuration data, or loss of safety oversight.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1426, 'OBS-RECOMMENDATION', N'Develop and implement a comprehensive OT access control policy that defines authorized vendor and remote access scenarios, approval workflows, monitoring expectations, and periodic policy review schedules.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1426, 'OBS-RISK', N'Lack of defined OT access policies could allow unauthorized vendor access, resulting in unmonitored changes or operational disruptions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1426, 'OBS-RISK-STATEMENT', N'Lack of OT-specific policies may lead to unauthorized vendor access or inconsistent security practices, risking system compromise or operational disruption.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1426, 'OBS-VULNERABILITY', N'Absence of formal, updated OT-specific access control governance and third-party access monitoring.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1427, 'OBS-DISCOVERY', N'The organization does not maintain a complete audit trail of OT account events, hindering investigations and incident response.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1427, 'OBS-IMPACT', N'Missing account audit data could delay incident detection, hinder investigations, and allow malicious account actions to go unnoticed, endangering OT system security.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1427, 'OBS-RECOMMENDATION', N'Deploy SIEM for account event logging; use manual logs for legacy systems; review logs monthly for anomalies.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1427, 'OBS-RISK', N'Lack of complete audit trails obscures detection of unauthorized account activity.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1427, 'OBS-RISK-STATEMENT', N'Lack of audit trails could obscure unauthorized account changes, delaying incident detection in OT.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1427, 'OBS-VULNERABILITY', N'Absence of centralized audit capabilities for OT account activities.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1428, 'OBS-DISCOVERY', N'The organization does not have consistent lifecycle processes for OT accounts, or lacks coordination with HR and physical security, leaving outdated or orphaned accounts.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1428, 'OBS-IMPACT', N'Poorly managed accounts could be exploited by former employees or third parties to compromise OT operations, disrupt services, or steal critical system information.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1428, 'OBS-RECOMMENDATION', N'Implement automated account lifecycle tools where possible; integrate HR termination alerts with OT access revocation; use manual logs for legacy systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1428, 'OBS-RISK', N'Inconsistent account lifecycle management increases the risk of unauthorized access and insider threat.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1428, 'OBS-RISK-STATEMENT', N'Poor account management could leave active accounts for terminated staff or vendors, risking unauthorized OT access and operational sabotage.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1428, 'OBS-VULNERABILITY', N'Lack of automated or coordinated account management processes in OT.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1429, 'OBS-DISCOVERY', N'The organization does not track OT account anomalies or integrate physical security data, missing potential insider threats or compromised accounts.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1429, 'OBS-IMPACT', N'Undetected abnormal account behaviors could allow attackers or insiders to maintain long-term access, manipulate systems, or steal sensitive data unnoticed.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1429, 'OBS-RECOMMENDATION', N'Use SIEM for real-time anomaly detection; integrate with physical security logs; escalate suspicious activity immediately.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1429, 'OBS-RISK', N'Failure to track anomalous OT account activity allows insider threats or compromises to go undetected.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1429, 'OBS-RISK-STATEMENT', N'Untracked anomalies could miss insider threats or compromises, endangering OT operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1429, 'OBS-VULNERABILITY', N'Absence of continuous monitoring and anomaly escalation for OT account behavior.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1430, 'OBS-DISCOVERY', N'The organization lacks automated account management tools, relying on inconsistent manual processes for critical systems, which can delay updates or deactivations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1430, 'OBS-IMPACT', N'Delayed account updates could leave unnecessary accounts active, enabling unauthorized actions that compromise OT system integrity or safety.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1430, 'OBS-RECOMMENDATION', N'Deploy IAM tools for modern OT systems; maintain detailed manual logs for legacy devices; audit account changes quarterly.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1430, 'OBS-RISK', N'Reliance on manual OT account management leads to inconsistencies and delayed deactivations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1430, 'OBS-RISK-STATEMENT', N'Inconsistent account management due to lack of automation could delay updates, increasing insider threats.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1430, 'OBS-VULNERABILITY', N'Lack of automated enforcement for OT account provisioning and deprovisioning.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1431, 'OBS-DISCOVERY', N'High-risk individuals(e.g., terminated staff) retain access due to slow or uncoordinated disablement procedures.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1431, 'OBS-IMPACT', N'Failure to promptly disable high-risk accounts could allow malicious insiders to alter control system configurations, disable alarms, or disrupt physical processes, leading to operational downtime, data loss, and potential safety incidents.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1431, 'OBS-RECOMMENDATION', N'Link HR terminations to instant OT account disabling; revoke badges simultaneously; log and audit all actions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1431, 'OBS-RISK', N'Retained access by high-risk individuals may allow sabotage, insider attacks, or unauthorized changes to critical OT systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1431, 'OBS-RISK-STATEMENT', N'Delayed disabling of high-risk accounts could allow sabotage or data theft in critical systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1431, 'OBS-VULNERABILITY', N'Lack of rapid deprovisioning processes for high-risk personnel.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1432, 'OBS-DISCOVERY', N'Expired or inactive accounts remain enabled, providing unnecessary attack vectors.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1432, 'OBS-IMPACT', N'Unauthorized access through stale accounts could allow adversaries to alter operational parameters, disable safety systems, or cause process interruptions, resulting in unplanned downtime, system malfunctions, and operational losses.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1432, 'OBS-RECOMMENDATION', N'Enforce 30-day disablement policy; automate inactivity detection; integrate HR notifications with OT account systems. Ensure accounts are disabled and archived rather then deleted.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1432, 'OBS-RISK', N'Inactive or expired accounts may be exploited to gain unauthorized access to OT systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1432, 'OBS-RISK-STATEMENT', N'Inactive or expired accounts could remain exploitable, enabling unauthorized OT access and disruptions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1432, 'OBS-VULNERABILITY', N'Failure to disable stale accounts within a defined timeframe.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1433, 'OBS-DISCOVERY', N'The organization does not enforce strict need-to-know, or fails to periodically review who has OT access, risking unauthorized or excessive privileges.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1433, 'OBS-IMPACT', N'Unauthorized access could enable malicious insiders or vendors to alter control settings, disrupt critical processes, or expose sensitive operational data.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1433, 'OBS-RECOMMENDATION', N'Implement RBAC with strict need-to-know mappings; integrate badge systems with logical access; automate periodic access reviews with alerts for anomalies.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1433, 'OBS-RISK', N'Lack of strict need-to-know enforcement could allow unauthorized or excessive access to OT systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1433, 'OBS-RISK-STATEMENT', N'Unauthorized access due to poor need-to-know enforcement could allow malicious insiders or vendors to disrupt critical OT processes compromise critical systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1433, 'OBS-VULNERABILITY', N'Inadequate periodic access reviews and need-to-know enforcement.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1434, 'OBS-DISCOVERY', N'OT user privileges are not reviewed on a regular basis. Reviews are informal or misaligned with role and mission needs, and elevated access often persists after role changes or project completion.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1434, 'OBS-IMPACT', N'Accumulated privileges increase insider threat risk, facilitate lateral movement, and allow unauthorized changes that disrupt or degrade OT systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1434, 'OBS-RECOMMENDATION', N'Conduct formal privilege reviews at least quarterly. Compare access assignments to documented job roles or functions. Immediately revoke or adjust any access that exceeds current operational needs. Document review outcomes for auditability.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1434, 'OBS-RISK', N'Unreviewed OT access privileges enable unauthorized or unnecessary access over time.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1434, 'OBS-RISK-STATEMENT', N'Unreviewed privileges could accumulate, enabling OT misuse or errors.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1434, 'OBS-VULNERABILITY', N'Failure to conduct periodic privilege reviews aligned with job roles.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1435, 'OBS-DISCOVERY', N'The organization does not consistently update shared account passwords when membership changes occur, allowing former users ongoing access to OT systems. This increases the risk of unauthorized access.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1435, 'OBS-IMPACT', N'Outdated shared account passwords could enable former users to access OT environments unnoticed, allowing manipulation of operational systems or access to sensitive data.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1435, 'OBS-RECOMMENDATION', N'Implement policies and procedures to update passwords on turnover; log changes; sync with badge revocation, and employ automation where possible.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1435, 'OBS-RISK', N'Failure to update shared account credentials after membership changes leaves OT systems vulnerable to unauthorized use.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1435, 'OBS-RISK-STATEMENT', N'Outdated shared passwords could allow ex-staff access, risking OT sabotage.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1435, 'OBS-VULNERABILITY', N'Poor shared account lifecycle management practices in OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1436, 'OBS-DISCOVERY', N'The organization does not restrict shared OT account use or enforce strong authentication and audit logging. Shared credentials obscure accountability and complicate incident response.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1436, 'OBS-IMPACT', N'Difficulty attributing malicious activity increases the risk of delayed detection and recovery from insider threats.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1436, 'OBS-RECOMMENDATION', N'Minimize shared accounts; enforce strong auth(e.g., MFA); log all shared account activity.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1436, 'OBS-RISK', N'Unrestricted shared accounts could obscure user actions, complicating OT incident response.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1436, 'OBS-RISK-STATEMENT', N'Unrestricted shared accounts could obscure user actions, complicating OT incident response.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1436, 'OBS-VULNERABILITY', N'Lack of accountability controls for OT shared accounts.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1437, 'OBS-DISCOVERY', N'Temporary/Emergency accounts remain active beyond intended periods, creating potential backdoors and an avenue for unauthorized access.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1437, 'OBS-IMPACT', N'Attackers leveraging unexpired temporary accounts could bypass standard authentication, manipulate system settings, disrupt ongoing operations, or pivot deeper into the network, significantly increasing the risk of prolonged system compromise.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1437, 'OBS-RECOMMENDATION', N'Set 24-hour auto-expiration for temp accounts; assign operators to verify manual disables; log all temp account actions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1437, 'OBS-RISK', N'Lingering temporary accounts could be exploited to gain unauthorized access to OT networks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1437, 'OBS-RISK-STATEMENT', N'Lingering temporary accounts could be exploited post-use, risking OT system compromise or safety incidents.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1437, 'OBS-VULNERABILITY', N'Lack of expiration enforcement for temporary or emergency accounts.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1438, 'OBS-DISCOVERY', N'Usage conditions for OT accounts are undefined or unenforced, allowing off-hours or off-location logins without oversight.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1438, 'OBS-IMPACT', N'Unauthorized off-hours or off-site access could allow adversaries to bypass monitoring, manipulate operational systems, or disrupt critical processes without detection.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1438, 'OBS-RECOMMENDATION', N'Define usage conditions(e.g., shift-based access); enforce via ACLs; train staff on policies.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1438, 'OBS-RISK', N'Lack of defined usage conditions for OT accounts increases opportunities for unauthorized off-hours or remote access.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1438, 'OBS-RISK-STATEMENT', N'Unrestricted usage could enable off-hours or off-site access, increasing OT breach risks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1438, 'OBS-VULNERABILITY', N'Absence of access time/location-based restrictions for OT users.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1439, 'OBS-DISCOVERY', N'Accounts are not automatically logged out after a period of inactivity.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1439, 'OBS-IMPACT', N'Unattended OT sessions could be hijacked to execute unauthorized commands, manipulate operational states, or disable safety systems, risking process failure or unsafe conditions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1439, 'OBS-RECOMMENDATION', N'Set 15-minute inactivity timeouts; use physical monitoring for continuous ops; ensure logout doesn’t disrupt critical tasks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1439, 'OBS-RISK', N'Persistent unattended sessions increase the risk of session hijacking or misuse in OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1439, 'OBS-RISK-STATEMENT', N'Persistent sessions could allow unauthorized use if unattended, risking OT system misuse and does not provide proper attribution for actions on the system.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1439, 'OBS-VULNERABILITY', N'Lack of inactivity-based session logout enforcement in OT.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1440, 'OBS-DISCOVERY', N'The organization does not maintain complete or secure OT configuration baselines, or lacks formal review processes to detect unauthorized or unsafe changes. Baselines may not align with vendor guidance or operational safety constraints.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1440, 'OBS-IMPACT', N'Unauthorized configuration changes could introduce safety hazards, system instability, or create vulnerabilities that attackers exploit.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1440, 'OBS-RECOMMENDATION', N'Establish secure, version-controlled OT configuration baselines for all critical systems. Align baseline settings with vendor specifications and operational safety requirements. Review and validate baselines monthly or after significant changes. Implement alerts for deviations and require documented approval for any changes to the baseline.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1440, 'OBS-RISK', N'Lack of secure OT baselines allows systems to drift from known-good configurations, increasing operational risks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1440, 'OBS-RISK-STATEMENT', N'Without defined and regularly reviewed baselines, systems may drift from known-good states, enabling unauthorized changes that impact integrity, safety, or reliable OT operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1440, 'OBS-VULNERABILITY', N'Failure to establish and review secure OT baseline configurations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1441, 'OBS-DISCOVERY', N'The organization does not disable unnecessary services, ports, or features on OT systems, leaving potential attack surfaces unaddressed.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1441, 'OBS-IMPACT', N'Attackers could exploit unused services or protocols to gain unauthorized access, pivot between systems, or launch disruptive attacks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1441, 'OBS-RECOMMENDATION', N'Define required functionality per system or device type. Disable or restrict unused services, ports, and protocols. Monitor for reactivation or unauthorized use, and review configurations at least bi-annually or after significant operational changes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1441, 'OBS-RISK', N'Failure to disable unnecessary services, ports, or protocols leaves OT systems unnecessarily exposed.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1441, 'OBS-RISK-STATEMENT', N'Unused or excessive functionality increases the attack surface and can allow unauthorized access, lateral movement, or compromise of OT systems, especially where protocols or services are exposed without need.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1441, 'OBS-VULNERABILITY', N'Excessive services and ports left active on OT systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1442, 'OBS-DISCOVERY', N'OT systems include unnecessary services, ports, or legacy protocols that are not routinely reviewed or disabled.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1442, 'OBS-IMPACT', N'Adversaries may exploit unneeded services to disrupt ICS operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1442, 'OBS-RECOMMENDATION', N'Establish a baseline of required services per device role. Disable all non-essential ports and services. Periodically revalidate to catch drift or misconfiguration.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1442, 'OBS-RISK', N'Excess functionality broadens the attack surface.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1442, 'OBS-RISK-STATEMENT', N'Overexposed functionality increases risk of exploitation or misuse.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1442, 'OBS-VULNERABILITY', N'Permissive or unmonitored OT configurations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1443, 'OBS-DISCOVERY', N'Standardized baseline configurations for OT and IT systems are not documented or enforced. Deviations from secure settings may not be tracked or approved, resulting in inconsistent system hardening.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1443, 'OBS-IMPACT', N'Misconfigured OT systems could be exploited, leading to operational disruptions, unauthorized access, or safety breaches.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1443, 'OBS-RECOMMENDATION', N'Develop and enforce standardized secure configuration settings for all OT and IT systems. Document approved deviations based on operational requirements and ensure they are tracked. Use centralized tools or defined procedures to apply, verify, and monitor settings. Review configurations semi-annually or after major system changes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1443, 'OBS-RISK', N'Inconsistent enforcement of secure configuration standards increases exposure to misconfigurations and security flaws.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1443, 'OBS-RISK-STATEMENT', N'Inconsistent or undocumented configuration settings can create exploitable weaknesses, increase misconfiguration risk, and reduce the organization''s ability to harden and maintain OT and IT systems securely.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1443, 'OBS-VULNERABILITY', N'Lack of standardized secure configuration enforcement across OT assets.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1444, 'OBS-DISCOVERY', N'The organization does not use a centralized or effective manual process to regularly validate ICS/OT configuration settings, or does so only on an ad hoc basis. Configuration baselines are not monitored continuously or systematically, leading to undetected drift or misconfigurations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1444, 'OBS-IMPACT', N'Unauthorized or accidental changes in OT configurations could persist unnoticed, leading to vulnerabilities, operational instability, or production downtime.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1444, 'OBS-RECOMMENDATION', N'Uses automated tools(where feasible) to regularly compare ICS/OT device configurations against approved baselines,
Provides alerts or reports when deviations are detected,
Implements compensating manual checks for devices lacking automation,
Schedules routine reviews(weekly, monthly, etc.) to ensure all discovered deviations are assessed and remediated promptly.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1444, 'OBS-RISK', N'Absence of centralized or systematic configuration monitoring increases OT system drift and misconfiguration risks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1444, 'OBS-RISK-STATEMENT', N'Without continuous or periodic validation of configuration settings, unauthorized changes or misconfigurations in ICS/OT devices can persist indefinitely. This increases the risk of exploitation by adversaries, system instability, or operational downtime caused by insecure or inconsistent configurations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1444, 'OBS-VULNERABILITY', N'Lack of baseline validation and drift detection in OT device configurations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1445, 'OBS-DISCOVERY', N'The organization lacks a comprehensive OT inventory that includes all critical asset data, such as IP addresses, ports, protocols, and metadata like vendor relationships or physical locations. Inventory updates may be ad hoc, incomplete, or not integrated with other security functions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1445, 'OBS-IMPACT', N'Untracked assets or network pathways could become entry points for attacks, delay incident response, or complicate recovery from cyber events.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1445, 'OBS-RECOMMENDATION', N'Develop and maintain a comprehensive OT asset inventory program that includes all physical and virtual devices(e.g., PLCs, HMIs, servers, sensors), associated IP and MAC addresses, firmware versions, communication ports, and allowed protocols. Capture metadata such as device role, criticality, vendor relationship, and physical location. Include network topology, segmentation boundaries, and service-level communication mappings. Use automated asset discovery methods where possible, and validate with procurement records, diagrams, and physical inspections. Ensure the inventory is integrated with access control, risk management, and physical security. Review and update the inventory regularly—at least quarterly or after system changes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1445, 'OBS-RISK', N'Incomplete or outdated OT asset inventories hinder security visibility and operational recovery.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1445, 'OBS-RISK-STATEMENT', N'Incomplete or inaccurate OT inventory can lead to unidentified critical assets, unmonitored network pathways, and untracked communication protocols. This creates blind spots that hinder vulnerability management, delay incident response, and complicate system recovery or continuity efforts following outages or cyber events.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1445, 'OBS-VULNERABILITY', N'Absence of comprehensive and updated OT asset inventories.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1446, 'OBS-DISCOVERY', N'The organization does not enforce structured testing or approval of OT configuration changes. Changes may occur without sandbox validation, vendor input, or safety impact analysis.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1446, 'OBS-IMPACT', N'Unapproved or improperly tested changes could cause production downtime, safety incidents, or open security gaps in critical OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1446, 'OBS-RECOMMENDATION', N'Test changes in sandboxes; require vendor/safety sign-off; log all changes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1446, 'OBS-RISK', N'Configuration changes to OT systems are not controlled, tested, or approved, risking unsafe or unstable operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1446, 'OBS-RISK-STATEMENT', N'Uncontrolled changes could disrupt OT operations or safety, causing downtime.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1446, 'OBS-VULNERABILITY', N'Lack of formal OT configuration change management processes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1447, 'OBS-DISCOVERY', N'The organization has not identified or restricted access to critical OT data, risking exposure or misuse.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1447, 'OBS-IMPACT', N'Exposure of configuration files, system logs, or historian data could enable attackers to better understand and manipulate OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1447, 'OBS-RECOMMENDATION', N'Map critical data; enforce RBAC and encryption; integrate physical and logical access monitoring. Identify and classify critical OT data(e.g., configuration backups, system logs, engineering documentation) and ensure it is stored securely within OT-controlled infrastructure. Apply role-based access controls and encryption where feasible. Avoid storing sensitive OT data in enterprise IT systems unless secured and segmented appropriately. Integrate monitoring and audit capabilities to detect unauthorized access or data exfiltration, and validate protections regularly.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1447, 'OBS-RISK', N'Critical OT data repositories are unprotected or mismanaged, exposing sensitive operational data to unauthorized access.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1447, 'OBS-RISK-STATEMENT', N'Unprotected repositories may expose sensitive operational data or system configurations, enabling adversaries to understand or manipulate OT operations. Storing critical OT data within IT systems increases exposure, as IT is often targeted and compromised earlier in attacks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1447, 'OBS-VULNERABILITY', N'Lack of access control and monitoring on critical OT data repositories.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1448, 'OBS-DISCOVERY', N'The organization does not prioritize OT services based on operational impact. Documentation is missing or outdated, and service rankings are not integrated into continuity planning.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1448, 'OBS-IMPACT', N'Recovery efforts could focus on non-critical systems first, prolonging downtime for vital OT services and amplifying operational losses.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1448, 'OBS-RECOMMENDATION', N'Rank services by impact; document in continuity plans; validate priorities annually.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1448, 'OBS-RISK', N'Failure to prioritize OT services based on criticality delays recovery during incidents.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1448, 'OBS-RISK-STATEMENT', N'Mis prioritized services could delay OT recovery, prolonging downtime.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1448, 'OBS-VULNERABILITY', N'Lack of service prioritization based on disruption impact within OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1449, 'OBS-DISCOVERY', N'Malware protection across OT systems is inconsistent or absent. Some systems lack AV entirely, and compensating measures such as network isolation, application allowlisting, or media scanning are not formally implemented. Patch management and malware signature updates are ad hoc or unsupported on constrained devices.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1449, 'OBS-IMPACT', N'Malware infections could disable ICS processes, cause data loss, or enable lateral movement into critical assets.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1449, 'OBS-RECOMMENDATION', N'Deploy AV or anti-malware tools on compatible OT systems and configure them for non-intrusive monitoring where needed.
For systems that cannot support AV:

Isolate using VLANs or dedicated zones.

Enforce media control with scanning kiosks.

Use application allowlisting.

Monitor traffic to/from these devices with IDS or passive monitoring tools.
Ensure malware signatures and software are updated regularly and reviewed during scheduled maintenance windows. Document all exceptions and apply risk-based compensating controls.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1449, 'OBS-RISK', N'Absence of malware defenses in OT systems increases compromise risk.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1449, 'OBS-RISK-STATEMENT', N'Absence of malware defenses in OT systems increases the risk of adversary compromise, potentially leading to operational outages, unauthorized control actions, or unsafe physical states.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1449, 'OBS-VULNERABILITY', N'Failure to deploy malware detection and containment measures in OT.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1450, 'OBS-DISCOVERY', N'The organization does not consistently apply application allowlisting or lacks a process for approving new software, allowing unauthorized or malicious programs to run in OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1450, 'OBS-IMPACT', N'Malicious executables could bypass defenses, install backdoors, or disrupt OT operations where other endpoint protections may not be feasible.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1450, 'OBS-RECOMMENDATION', N'Implement application allowlisting across OT assets to limit software execution to known and approved programs. Establish a defined process for adding new entries and review the allowlist regularly to accommodate changes without weakening security.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1450, 'OBS-RISK', N'Absence of application allowlisting allows unauthorized executables to run within OT systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1450, 'OBS-RISK-STATEMENT', N'Without allowlisting, unauthorized executables can be introduced, leading to possible disruptions, breaches, or malware infections in OT systems, particularly where security agents or patching are not feasible.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1450, 'OBS-VULNERABILITY', N'Failure to implement application allowlisting on OT assets.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1451, 'OBS-DISCOVERY', N'The organization does not prevent unauthorized software execution on OT systems, or lacks enforcement of policies due to hardware limitations or operational constraints.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1451, 'OBS-IMPACT', N'Malicious or unapproved programs could execute within OT environments, causing operational disruptions or providing footholds for attackers.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1451, 'OBS-RECOMMENDATION', N'Implement software execution controls appropriate to the OT environment, such as execution policies, signed code requirements, or restricted user permissions. Consider OT platform limitations when designing controls, and verify enforcement regularly.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1451, 'OBS-RISK', N'Unauthorized software execution on OT systems increases the risk of instability or malicious activity.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1451, 'OBS-RISK-STATEMENT', N'Failure to restrict software execution allows unapproved or malicious code to run on OT systems, potentially introducing instability, enabling compromise, or violating vendor or safety requirements.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1451, 'OBS-VULNERABILITY', N'Lack of controls to prevent unauthorized software execution on OT systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1452, 'OBS-DISCOVERY', N'The organization does not maintain a structured patch management process across both IT and OT environments. Patches are often deployed without offline testing in critical OT systems, and there are no formal procedures for handling assets with limited update capability. Patching is ad hoc or inconsistent, increasing risk to availability and integrity of operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1452, 'OBS-IMPACT', N'Exploitation of known vulnerabilities or unexpected disruption from improper patching could affect operations, enable persistent access, or result in production downtime.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1452, 'OBS-RECOMMENDATION', N'Establish a centralized, risk-based patch management program that:
Maintains a complete inventory of patchable assets in IT and OT,
Schedules updates based on criticality and exposure,
Tests patches offline prior to deployment in sensitive OT environments,
Documents patch cycles, from testing through deployment,
Implements manual or compensating controls for OT devices that cannot be patched.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1452, 'OBS-RISK', N'Inadequate OT patching processes leave devices exposed to known vulnerabilities.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1452, 'OBS-RISK-STATEMENT', N'Without a structured patch management program, both OT and IT systems remain exposed to known vulnerabilities. Unpatched software can be exploited by adversaries, while untested patches in critical environments risk system instability, downtime, or unintended operational disruptions. Additionally, OT systems with limited update capabilities may remain vulnerable if not properly managed, potentially leading to security breaches or safety issues.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1452, 'OBS-VULNERABILITY', N'Inconsistent or ad hoc patching practices across critical environments, inadquite lack of structured processes for testing and applying updates in OT systems, and/or no compensating controls for devices that cannot be patched.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1453, 'OBS-DISCOVERY', N'The organization does not maintain a secure or operationally ready OT backup control center. Coordination with vendors and security stakeholders is informal or untested.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1453, 'OBS-IMPACT', N'Recovery delays could extend downtime, increasing operational and safety risks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1453, 'OBS-RECOMMENDATION', N'Secure control center with access controls; coordinate with vendors/security; test recovery annually.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1453, 'OBS-RISK', N'Insecure or poorly coordinated OT backup control centers could fail during recovery operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1453, 'OBS-RISK-STATEMENT', N'A weak control center could hinder OT recovery, risking prolonged outages.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1453, 'OBS-VULNERABILITY', N'Lack of secured, vendor-coordinated OT backup processing facilities.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1454, 'OBS-DISCOVERY', N'Backups are not tested to ensure effective continuity of operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1454, 'OBS-IMPACT', N'Failure to restore from backup could leave critical OT systems offline, halting operations and increasing recovery costs.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1454, 'OBS-RECOMMENDATION', N'Test backups quarterly in test environments; coordinate with vendors; document test results.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1454, 'OBS-RISK', N'Untested backups could fail during OT recovery, risking prolonged outages.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1454, 'OBS-RISK-STATEMENT', N'Untested backups could fail during OT recovery, risking prolonged outages.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1454, 'OBS-VULNERABILITY', N'Lack of tested OT backup recovery procedures.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1455, 'OBS-DISCOVERY', N'The organization does not have a formal reconstitution process for ICS/OT environments, lacks regularly tested backups or gold images, or does not verify integrity of restored systems. They may also lack procedures for using manual controls if the network must be taken offline for recovery.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1455, 'OBS-IMPACT', N'Incomplete recovery could leave systems vulnerable to repeat compromise, extended downtime, or unsafe operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1455, 'OBS-RECOMMENDATION', N'Develop and document a reconstitution plan specifically for ICS/OT, detailing how to rebuild or restore systems from trusted backups or gold images.
Regularly test backups and “gold images” in a non-production environment to ensure they can be restored quickly and safely.
Retain spare hardware(if feasible) to accelerate recovery when primary equipment is compromised or fails.
Validate system integrity(e.g., checksums, patches) before returning devices to production.
Train staff on using manual controls to maintain critical functions while reconstitution is in progress.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1455, 'OBS-RISK', N'Lack of formal OT reconstitution processes risks delayed or insecure recovery.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1455, 'OBS-RISK-STATEMENT', N'Without a robust reconstitution process, the organization cannot reliably or quickly recover ICS/OT from a compromise or major disruption. Systems may remain vulnerable or unverified post-rebuild, increasing the risk of repeated compromise, extended downtime, or unsafe operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1455, 'OBS-VULNERABILITY', N'Absence of ICS/OT-specific recovery and integrity validation procedures.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1456, 'OBS-DISCOVERY', N'The organization does not schedule or test system-level OT backups. Full system images are missing, and recovery verification is inconsistent or absent.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1456, 'OBS-IMPACT', N'Loss of system backups could leave critical OT devices unrecoverable after incidents.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1456, 'OBS-RECOMMENDATION', N'Schedule weekly system backups; encrypt and verify integrity; test recovery quarterly.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1456, 'OBS-RISK', N'Unscheduled or insecure system-level backups risk OT recovery failure.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1456, 'OBS-RISK-STATEMENT', N'Unsecured or untested system backups could fail, causing extended OT downtime.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1456, 'OBS-VULNERABILITY', N'Absence of verified, secured full-system OT backups.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1457, 'OBS-DISCOVERY', N'The organization does not ensure regular, secure backups of OT user-level data (e.g., operator configurations). Backup availability and integrity are not routinely verified.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1457, 'OBS-IMPACT', N'Rebuilding user configs manually could delay operator efficiency and restart processes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1457, 'OBS-RECOMMENDATION', N'Backup user configs regularly; encrypt backups; test restoration monthly.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1457, 'OBS-RISK', N'Loss of OT user-level configuration backups impairs operational recovery.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1457, 'OBS-RISK-STATEMENT', N'Lost user configs could delay OT recovery, impacting operator efficiency.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1457, 'OBS-VULNERABILITY', N'Inconsistent or missing OT user-level backup processes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1458, 'OBS-DISCOVERY', N'The organization does not plan for or test capacity requirements(power, communications) in OT continuity scenarios, risking inadequate resources during disruptions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1458, 'OBS-IMPACT', N'Insufficient capacity could cause critical OT systems to stall or fail during disruptions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1458, 'OBS-RECOMMENDATION', N'Include power/comms capacity in plans; conduct annual failover tests; secure vendor SLAs to guarantee support and resources during disruptions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1458, 'OBS-RISK', N'OT continuity plans fail to address critical capacity needs like backup power or communications.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1458, 'OBS-RISK-STATEMENT', N'Insufficient capacity planning could cause OT systems to stall or fail under adverse conditions, halting operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1458, 'OBS-VULNERABILITY', N'Lack of resource planning for OT continuity under adverse conditions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1459, 'OBS-DISCOVERY', N'OT continuity plans are not formally reviewed or approved by senior leadership and do not incorporate vendor or operational feedback, causing misalignment with organizational goals.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1459, 'OBS-IMPACT', N'Plans may be outdated or misaligned, leading to ineffective recovery during real disruptions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1459, 'OBS-RECOMMENDATION', N'Require senior approval; incorporate vendor/operational feedback into plan updates; schedule periodic plan reviews to maintain alignment with strategic and operational needs.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1459, 'OBS-RISK', N'Lack of senior official approval and feedback in OT continuity planning reduces plan authority and relevance.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1459, 'OBS-RISK-STATEMENT', N'Unapproved or outdated plans may lack authority and relevance, reducing their effectiveness during OT recovery.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1459, 'OBS-VULNERABILITY', N'Lack of governance and operational validation of OT continuity plans.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1460, 'OBS-DISCOVERY', N'The organization does not have formal OT continuity plans or fails to address safe shutdowns, vendor coordination, and annual testing, leaving production at risk after disruptions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1460, 'OBS-IMPACT', N'Poor planning could prolong downtime, impact production, or cause unsafe shutdowns.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1460, 'OBS-RECOMMENDATION', N'Develop OT-specific continuity plans; test annually with vendors; prioritize critical recovery.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1460, 'OBS-RISK', N'Weak OT continuity planning risks uncoordinated or ineffective recovery after interruptions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1460, 'OBS-RISK-STATEMENT', N'Weak continuity plans could prolong OT downtime, impacting production or safety.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1460, 'OBS-VULNERABILITY', N'Inadequate OT-specific continuity strategies and vendor coordination.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1461, 'OBS-DISCOVERY', N'No tabletop exercises are performed, or they do not address vendor dependencies or safety scenarios, leaving OT continuity plans unvalidated.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1461, 'OBS-IMPACT', N'Plans could fail during real incidents, delaying restoration and exacerbating operational impacts.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1461, 'OBS-RECOMMENDATION', N'Conduct annual tabletop drills tailored to OT environments; include vendor collaboration and safety concerns; document lessons learned and update continuity plans accordingly.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1461, 'OBS-RISK', N'No OT-focused tabletop exercises risks unvalidated and ineffective continuity plans.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1461, 'OBS-RISK-STATEMENT', N'Untested plans may fail during real OT incidents, delaying recovery and exacerbating disruptions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1461, 'OBS-VULNERABILITY', N'Lack of practical testing of OT continuity plans through exercises.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1462, 'OBS-DISCOVERY', N'No documented RTOs for OT processes, or RTOs are not tested, resulting in uncertain or extended recovery times if a disruption occurs.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1462, 'OBS-IMPACT', N'Missed RTOs could prolong outages and jeopardize production or safety.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1462, 'OBS-RECOMMENDATION', N'Define RTOs per critical process; conduct annual recovery drills; adjust RTO targets based on actual test results to ensure they align with operational requirements.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1462, 'OBS-RISK', N'Absence of documented and tested recovery time objectives(RTOs) in OT increases uncertainty.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1462, 'OBS-RISK-STATEMENT', N'Unrealistic or untested RTOs can delay OT restoration, affecting production and possibly safety.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1462, 'OBS-VULNERABILITY', N'Undefined or untested OT process recovery objectives.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1463, 'OBS-DISCOVERY', N'The organization does not track OT incidents from detection through resolution in a structured manner. Escalation processes are unclear, and coordination with vendors or physical security is ad hoc or absent.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1463, 'OBS-IMPACT', N'Failure to track incident resolution could result in repeat compromises, longer outages, and unaddressed root causes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1463, 'OBS-RECOMMENDATION', N'Log all OT incidents with details; establish escalation protocols; integrate with security/vendor teams.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1463, 'OBS-RISK', N'Untracked incidents could delay OT resolution, amplifying damage or safety risks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1463, 'OBS-RISK-STATEMENT', N'Untracked incidents could delay OT resolution, amplifying damage or safety risks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1463, 'OBS-VULNERABILITY', N'Lack of formal OT incident tracking and escalation processes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1464, 'OBS-DISCOVERY', N'The organization does not have a sufficient process for detecting unauthorized configuration changes in real time, or lacks defined incident response steps(e.g., alerting, containment, rollback). Unauthorized changes may go unnoticed or unaddressed.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1464, 'OBS-IMPACT', N'Unauthorized config changes could persist, leading to compromised OT control and critical system failures.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1464, 'OBS-RECOMMENDATION', N'Implement an incident handling process that:
Continuously monitors OT systems for config changes(via logs, alerts, or config monitoring tools);
Triggers alerts or notifications on unauthorized changes;
Defines response actions(e.g., rollback, containment) and roles for quick mitigation;
Documents each incident, analyzes root causes, and updates procedures to prevent recurrence.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1464, 'OBS-RISK', N'Without timely detection and response, unauthorized changes may go unnoticed or unaddressed, allowing adversaries or insiders to introduce harmful configurations that compromise system integrity, disrupt operations, or create safety risks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1464, 'OBS-RISK-STATEMENT', N'Without timely detection and response, unauthorized changes may go unnoticed or unaddressed, allowing adversaries or insiders to introduce harmful configurations that compromise system integrity, disrupt operations, or create safety risks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1464, 'OBS-VULNERABILITY', N'Lack of monitoring and incident handling for OT configuration changes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1465, 'OBS-DISCOVERY', N'The organization does not regularly conduct OT incident response exercises. Tabletops or simulations are infrequent, undocumented, or not coordinated to ensure operational safety and preparedness.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1465, 'OBS-IMPACT', N'Ineffective response increases incident severity, operational downtime, and safety risks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1465, 'OBS-RECOMMENDATION', N'Run annual OT tabletops; use off-hours or simulations; refine plans post-exercise.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1465, 'OBS-RISK', N'Untested IR could fail in real OT incidents, delaying mitigation.')
