INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1465, 'OBS-VULNERABILITY', N'Absence of simulated OT incident exercises.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1466, 'OBS-DISCOVERY', N'The organization does not maintain or test an OT-specific incident response policy. Roles and procedures are undefined, vendor coordination is not formalized, and systems with limited automation lack compensating measures.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1466, 'OBS-IMPACT', N'Delayed or disorganized response could prolong outages, safety risks, or cascading failures.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1466, 'OBS-RECOMMENDATION', N'Develop OT-specific IR policy; test with simulations yearly; define vendor roles clearly.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1466, 'OBS-RISK', N'Weak IR policy could slow OT incident response, risking safety or extended downtime.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1466, 'OBS-RISK-STATEMENT', N'Weak IR policy could slow OT incident response, risking safety or extended downtime.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1466, 'OBS-VULNERABILITY', N'Lack of OT-specific incident response policy and testing.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1467, 'OBS-DISCOVERY', N'OT incident response testing is not coordinated with business continuity plans. Testing efforts are siloed, and vendor participation is not consistently included in recovery strategy validation.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1467, 'OBS-IMPACT', N'Gaps between IR and business continuity plans could cause prolonged downtime, safety impacts, or chaotic recovery efforts.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1467, 'OBS-RECOMMENDATION', N'Align IR with continuity plans; include vendor input; test coordination annually.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1467, 'OBS-RISK', N'Misaligned IR and continuity could disrupt OT recovery, impacting operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1467, 'OBS-RISK-STATEMENT', N'Misaligned IR and continuity could disrupt OT recovery, impacting operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1467, 'OBS-VULNERABILITY', N'Inadequate integration of OT incident response with continuity planning.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1468, 'OBS-DISCOVERY', N'The organization does not assign or train OT personnel for specific incident response roles. Integration with physical security is not defined, and response responsibilities remain unclear or undocumented.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1468, 'OBS-IMPACT', N'Poorly handled incidents could escalate in severity, prolong downtime, and jeopardize physical safety.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1468, 'OBS-RECOMMENDATION', N'Assign clear IR roles; train annually; link with physical security response plans.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1468, 'OBS-RISK', N'Untrained staff could mishandle OT incidents, delaying recovery.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1468, 'OBS-RISK-STATEMENT', N'Untrained staff could mishandle OT incidents, delaying recovery.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1468, 'OBS-VULNERABILITY', N'Lack of training and assignment for OT-specific incident response roles.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1469, 'OBS-DISCOVERY', N'The organization does not consistently store or test backups offline, fails to protect them from ransomware or localized disasters, and/or does not maintain gold images and spare hardware, leaving OT environments vulnerable to data loss and prolonged downtime.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1469, 'OBS-IMPACT', N'Failed backups could prevent OT restoration after incidents, leading to prolonged outages.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1469, 'OBS-RECOMMENDATION', N'Perform frequent backups for all OT data(user configs and full images), store them offline or on immutable media to prevent tampering. Ensure encryption for media that is transported. Test backups regularly  to ensure restorability, maintain updated gold images and spare hardware for rapid rebuilds, and keep processes documented and aligned with evolving system needs.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1469, 'OBS-RISK', N'Inconsistent OT backup practices and lack of immutability increase risk of data loss.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1469, 'OBS-RISK-STATEMENT', N'Unsecured or untested backups could fail, prolonging OT downtime after incidents. If backups are online, untested, or incomplete, ransomware or other incidents can render them unusable—delaying OT recovery, increasing downtime, and jeopardizing operational integrity or safety.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1469, 'OBS-VULNERABILITY', N'Unprotected or untested OT system and config backups.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1470, 'OBS-DISCOVERY', N'The organization does not have a consistent process for selecting and configuring auditable events on OT systems. Logging is incomplete, and legacy devices lack compensating controls such as manual logbooks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1470, 'OBS-IMPACT', N'Missing logs for critical events could prevent early detection of breaches or inhibit forensic reconstruction of attacker activities in OT systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1470, 'OBS-RECOMMENDATION', N'Define key events(e.g., logins, changes); log via SIEM; use manual logs for legacy systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1470, 'OBS-RISK', N'Failure to configure key auditable events in OT environments hampers investigation and threat detection.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1470, 'OBS-RISK-STATEMENT', N'Inadequate event logging could hinder OT incident investigations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1470, 'OBS-VULNERABILITY', N'Incomplete event logging configuration in OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1471, 'OBS-DISCOVERY', N'The organization has not formally identified which events are security-relevant within the OT environment. In many cases, OT systems are left with vendor-default or minimal logging configurations that fail to capture critical changes or access attempts. Some systems may log certain events, but there is no unified or risk-based process to ensure coverage across the environment. Where event selection has occurred, it is often undocumented or applied inconsistently, limiting its usefulness during incident response or forensic analysis.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1471, 'OBS-IMPACT', N'Undetected events can result in prolonged compromise or failed response.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1471, 'OBS-RECOMMENDATION', N'Configure OT systems to generate audit logs and centralize them for review.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1471, 'OBS-RISK', N'Absence of audit logging across ICS environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1471, 'OBS-RISK-STATEMENT', N'Lack of audit logs prevents detection of unauthorized activity and hinders incident response.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1471, 'OBS-VULNERABILITY', N'Logging not enabled or aggregated.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1472, 'OBS-DISCOVERY', N'The organization does not consistently include OT-specific operational data (e.g., process states, sensor values, or control logic changes) in audit records. In many cases, audit logs are limited to basic system events and do not capture contextual information that would support situational awareness or troubleshooting. Some assets may support enhanced logging but are not configured to use it, and processes to evaluate or enrich logs with meaningful OT context are often missing.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1472, 'OBS-IMPACT', N'Without OT process data in audit logs, identifying attack stages, unauthorized changes, or early warning signs becomes more difficult, delaying incident response.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1472, 'OBS-RECOMMENDATION', N'Add process states to logs; integrate with monitoring tools; review usefulness quarterly.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1472, 'OBS-RISK', N'Missing OT-specific audit data reduces visibility and slows incident detection and response.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1472, 'OBS-RISK-STATEMENT', N'Missing OT-specific data could reduce incident context, slowing response.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1472, 'OBS-VULNERABILITY', N'Failure to include OT operational state information in audit records.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1473, 'OBS-DISCOVERY', N'OT systems often produce logs that lack essential metadata such as user attribution, device identifiers, or precise timestamps. In many cases, the available logs are overly generic or limited to high-level events, reducing their forensic value. Legacy systems may only provide operational summaries without actionable detail.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1473, 'OBS-IMPACT', N'Poorly detailed audit logs could delay incident detection, obscure attacker activity, and undermine recovery efforts following OT system breaches.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1473, 'OBS-RECOMMENDATION', N'Ensure OT audit records include detailed metadata: who performed the action, what occurred, when it happened, and on which system. Where native support is lacking, deploy external collectors or proxies to enrich logs. Validate that logs meet forensic standards and support timeline reconstruction.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1473, 'OBS-RISK', N'Sparse or inadequate audit logs hinder forensic analysis during OT cybersecurity investigations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1473, 'OBS-RISK-STATEMENT', N'Audit records lacking detail hinder detection, investigation, and response to threats within OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1473, 'OBS-VULNERABILITY', N'Lack of detailed audit record collection practices for OT events.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1474, 'OBS-DISCOVERY', N'The organization does not log privileged functions in OT systems. Configuration changes, firmware updates, and critical command executions may occur without traceability. Legacy or standalone devices often lack native support, and compensating controls (e.g., session logs, proxies) are not consistently implemented.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1474, 'OBS-IMPACT', N'Lack of privileged action logging could delay forensic investigations, allow undetected misuse of critical systems, and complicate accountability during breach investigations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1474, 'OBS-RECOMMENDATION', N'Enable privileged command logging on all OT systems where feasible. Where direct logging is not possible, implement manual logs, session recording, or external proxy monitoring. Centralize and review these logs to support incident detection and root cause analysis.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1474, 'OBS-RISK', N'Missing or incomplete privileged function logging obscures traceability during OT incidents and prevents changes from being monitored or correlated during investigations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1474, 'OBS-RISK-STATEMENT', N'Without privileged function logging, unauthorized or erroneous changes to critical systems may go undetected, hindering investigations and increasing the risk of sabotage or misconfiguration.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1474, 'OBS-VULNERABILITY', N'Lack of privileged activity logging for OT systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1475, 'OBS-DISCOVERY', N'Privileged OT accounts (e.g., root, admin, emergency) are not consistently subject to enhanced monitoring or alerting. These accounts are often used without correlation to facility access or operational context, reducing visibility into potentially high-impact actions. In several cases, use of privileged access lacks timely review, making it difficult to detect misuse or validate operational legitimacy.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1475, 'OBS-IMPACT', N'Unmonitored use of privileged accounts could enable undetected changes, data exfiltration, or system compromise in critical OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1475, 'OBS-RECOMMENDATION', N'Implement real-time alerting or event logging for all privileged account activity within OT systems. At a minimum, review usage logs weekly, with immediate investigation of anomalous behavior. Consider integrating physical access data, badge logs, or operator handoff procedures to improve confidence in event attribution and enable faster escalation during potential incidents.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1475, 'OBS-RISK', N'Insufficient monitoring of root/admin accounts in OT leaves critical assets vulnerable to misuse.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1475, 'OBS-RISK-STATEMENT', N'Unmonitored privileged accounts could enable undetected misuse, threatening OT stability.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1475, 'OBS-VULNERABILITY', N'Absence of privileged account monitoring and escalation processes for OT.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1476, 'OBS-DISCOVERY', N'Audit logs from OT systems are not consistently offloaded to external or centralized log collection points. In many cases, logs remain local to the device and are overwritten, lost during reboots, or inaccessible due to proprietary formats. Where offloading occurs, it may lack encryption, integrity validation, or centralized visibility (e.g., SIEM ingestion).')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1476, 'OBS-IMPACT', N'Overburdened devices may experience performance issues, and critical logs could be lost if devices are compromised, rebooted, or storage is exhausted.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1476, 'OBS-RECOMMENDATION', N'Implement secure and automated audit log offloading from OT systems to centralized logging solutions such as SIEMs or syslog collectors. Use encrypted transfer protocols (e.g., TLS, IPsec), and apply integrity verification (e.g., hashing or WORM storage) to prevent tampering. For systems without native support, use intermediate devices or log collectors. Configure alerts for offload failures and regularly test log availability and usability.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1476, 'OBS-RISK', N'Retaining logs solely on OT devices strains resources and risks log loss.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1476, 'OBS-RISK-STATEMENT', N'Without reliable and secure log offloading, audit records may be lost, tampered with, or remain siloed—hindering detection, response, and forensic analysis in OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1476, 'OBS-VULNERABILITY', N'Lack of external log offloading from OT systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1477, 'OBS-DISCOVERY', N'OT audit storage is not consistently monitored for capacity thresholds. Many systems do not issue proactive alerts when storage nears limits, and in some cases, logs are overwritten without notification. Legacy or embedded systems may lack any capacity monitoring, leading to silent data loss.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1477, 'OBS-IMPACT', N'Overwritten logs could erase evidence of compromise, insider activity, or operational incidents, complicating forensic investigations and regulatory compliance.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1477, 'OBS-RECOMMENDATION', N'Implement log storage monitoring with alert thresholds (e.g., 80% full) across OT infrastructure. For constrained devices, automate log offloading or increase storage capacity where possible. Review log retention policies quarterly to validate sufficiency and effectiveness of alerts.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1477, 'OBS-RISK', N'Inadequate audit storage monitoring risks overwriting critical OT security events.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1477, 'OBS-RISK-STATEMENT', N'Failure to monitor and alert on audit log storage limits can result in critical security data being overwritten before review, obstructing incident response and compliance activities.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1477, 'OBS-VULNERABILITY', N'Failure to monitor and alert on OT audit storage thresholds.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1478, 'OBS-DISCOVERY', N'The organization does not have sufficient real-time alerting mechanisms for critical OT logging failures. In some environments, alerts are not configured or are dependent on manual observation. Whether due to lack of centralized monitoring (e.g., no SOC) or limited integration with physical or IT security operations, audit disruptions may go unnoticed, delaying recovery efforts.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1478, 'OBS-IMPACT', N'Failure to respond promptly to audit logging disruptions increases the risk of undetected sabotage, data exfiltration, or unsafe process changes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1478, 'OBS-RECOMMENDATION', N'Implement real-time alerting for OT logging disruptions, regardless of organization size. Where SIEMs or SOCs are unavailable, configure device-level alerts or lightweight monitoring scripts. Establish escalation paths, including coordination with IT and physical security personnel, to ensure timely response and log restoration.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1478, 'OBS-RISK', N'Unnoticed audit logging failures could allow persistent threats to operate undetected in OT.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1478, 'OBS-RISK-STATEMENT', N'Logging failures could go unnoticed, missing OT threats and delaying response.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1478, 'OBS-VULNERABILITY', N'Lack of real-time alerting and response mechanisms for OT logging failures.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1479, 'OBS-DISCOVERY', N'OT environments do not consistently detect or respond to audit logging failures. Some systems lack alerts for full log storage, disabled logging, or communication failures with central collectors. In certain cases, fallback mechanisms like log offloading or local backups are either misconfigured or absent, and personnel are unaware of when logging failures occur.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1479, 'OBS-IMPACT', N'Missed logging events during failures could prevent early detection of attacks, allowing threats to escalate undetected.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1479, 'OBS-RECOMMENDATION', N'Deploy automated alerting for logging failures (e.g., storage capacity, transfer issues, service stops) across OT environments. Establish fallback strategies such as immediate log offloading or local buffering with alerts. Review failure scenarios during tabletop exercises and ensure logs are restored or captured through alternate means if primary logging fails.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1479, 'OBS-RISK', N'Lack of detection or fallback for logging failures leaves gaps in OT monitoring.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1479, 'OBS-RISK-STATEMENT', N'Failure to detect or mitigate audit logging failures can result in critical OT security events going unrecorded, undermining the ability to respond to incidents or reconstruct attacker actions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1479, 'OBS-VULNERABILITY', N'Absence of alerting and fallback processes for OT log failures.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1480, 'OBS-DISCOVERY', N'The organization has not adequately planned for audit log storage across critical systems. Some devices lack retention policies, have minimal onboard storage, or are not configured to offload logs to central repositories. As a result, logs may be overwritten or unavailable during investigations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1480, 'OBS-IMPACT', N'Loss of audit logs could obscure attacker activity, delay incident detection, and hinder forensic investigations into OT breaches.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1480, 'OBS-RECOMMENDATION', N'Assess each device or system for log storage capacity and configure them to retain logs for at least 90 days. Where native capacity is limited, offload logs regularly to secure central storage (e.g., SIEM, syslog server). Validate log availability and retention during regular audits.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1480, 'OBS-RISK', N'Insufficient log storage planning could cause critical OT logs to be overwritten or lost.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1480, 'OBS-RISK-STATEMENT', N'Inadequate planning for log storage could lead to the loss of critical audit data during an incident, preventing effective detection, investigation, or response.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1480, 'OBS-VULNERABILITY', N'Lack of log retention planning and offloading in critical environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1481, 'OBS-DISCOVERY', N'The organization does not consistently review or analyze audit logs from OT systems. In some cases, reviews occur only after an event or are limited to IT systems. Coordination with security, engineering, or incident response teams is informal or absent, leading to gaps in detection and delayed remediation.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1481, 'OBS-IMPACT', N'Ignored or unanalyzed OT logs could allow attackers to maintain presence, disrupt operations, or exfiltrate data without detection.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1481, 'OBS-RECOMMENDATION', N'Review OT audit logs at least monthly. For higher-impact systems or where alerting tools (e.g., SIEMs) are in place, perform reviews weekly or near real-time. Tailor review frequency based on system criticality, past incidents, and organizational capability. Ensure coordination with security teams to flag anomalies and support incident response.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1481, 'OBS-RISK', N'Failure to review OT audit records prevents timely detection of malicious activity or anomalies.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1481, 'OBS-RISK-STATEMENT', N'Unreviewed logs could miss OT incidents, delaying mitigation.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1481, 'OBS-VULNERABILITY', N'Lack of systematic OT log review and incident correlation processes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1482, 'OBS-DISCOVERY', N'Organization lacks effective monitoring or training mechanisms to detect phishing for information and early-stage reconnaissance activity.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1482, 'OBS-IMPACT', N'Successful reconnaissance leads to adversaries crafting more effective, targeted attacks, increasing likelihood of operational disruption.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1482, 'OBS-RECOMMENDATION', N'Implement user awareness programs, deploy anomaly-based detection tools, simulate phishing exercises, and monitor for suspicious information requests.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1482, 'OBS-RISK', N'Failure to detect reconnaissance enables targeted ICS/OT attacks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1482, 'OBS-RISK-STATEMENT', N'Without detection, adversary reconnaissance can progress undetected, enabling tailored attacks against ICS systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1482, 'OBS-VULNERABILITY', N'Lack of reconnaissance detection allows adversaries to gather detailed information to bypass defenses.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1483, 'OBS-DISCOVERY', N'OT events are not consistently logged or correlated with physical security, hindering detection of unauthorized actions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1483, 'OBS-IMPACT', N'Undetected OT events could delay incident response, leading to extended downtime or safety issues.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1483, 'OBS-RECOMMENDATION', N'Deploy SIEM for OT event logging; integrate with physical security; use manual logs for legacy systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1483, 'OBS-RISK', N'Lack of OT event monitoring hinders detection of suspicious activities.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1483, 'OBS-RISK-STATEMENT', N'Unmonitored events could miss OT threats, delaying detection and response.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1483, 'OBS-VULNERABILITY', N'Insufficient OT event logging and monitoring capabilities.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1484, 'OBS-DISCOVERY', N'The organization does not monitor for network traffic anomalies in OT environments. Conditions such as repeated TCP retransmissions, abnormal error rates, or unexpected protocol behavior go undetected, which could mask latent faults or early adversary activity.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1484, 'OBS-IMPACT', N'Failure to identify and act on traffic anomalies may result in system instability, degraded performance, or delayed response to malicious activity.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1484, 'OBS-RECOMMENDATION', N'Deploy passive network monitoring to identify baseline-normal behavior in OT traffic. Alert on deviations such as abnormal retransmission rates, packet corruption, or unexpected protocol usage. Incorporate findings into incident response and reliability workflows. Use these detections to support fault isolation, compromise detection, and proactive maintenance.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1484, 'OBS-RISK', N'Network anomalies go unobserved, increasing the chance of process disruption or hidden compromise.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1484, 'OBS-RISK-STATEMENT', N'Undetected anomalies in OT traffic — including frequent retransmissions, malformed packets, or performance inconsistencies — can degrade process reliability or indicate reconnaissance, denial of service attempts, or misconfigured devices. Failure to detect and respond may lead to operational downtime or undetected intrusion.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1484, 'OBS-VULNERABILITY', N'Lack of monitoring and analysis of anomolous traffic.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1485, 'OBS-DISCOVERY', N'The organization does not monitor internal OT traffic for anomalies such as excessive east-west communication, inter-VLAN broadcast storms, or unauthorized peer-to-peer behavior.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1485, 'OBS-IMPACT', N'Unchecked lateral traffic could allow malware spread, device compromise, or data exfiltration in OT.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1485, 'OBS-RECOMMENDATION', N'Deploy passive network monitoring or IDS tools capable of detecting ICS protocol anomalies and lateral movement. Create baseline communication maps and alert on deviations, such as unexpected cross-VLAN communication or new east-west connections. Investigate anomalies promptly and segment networks where feasible to contain propagation.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1485, 'OBS-RISK', N'Excessive east-west OT traffic is not monitored, masking potential attacks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1485, 'OBS-RISK-STATEMENT', N'Unmonitored internal traffic allows malware, attackers, or misconfigured systems to communicate laterally, bypassing perimeter defenses. Broadcast storms, inter-VLAN pivoting, and rogue peer-to-peer behavior can destabilize network performance and conceal malicious activity.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1485, 'OBS-VULNERABILITY', N'Lack of east-west traffic analysis and anomaly detection in OT networks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1486, 'OBS-DISCOVERY', N'The organization does not monitor or restrict OT-to-cloud communications. Unauthorized external data flows or integrations may occur without detection or enforcement.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1486, 'OBS-IMPACT', N'Unauthorized cloud integrations could expose sensitive OT operational data, risking regulatory violations or operational harm.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1486, 'OBS-RECOMMENDATION', N'Implement network segmentation and firewall rules to strictly control OT-to-cloud communications. Require approval for all cloud integrations and log outbound traffic to detect unauthorized flows. Use data loss prevention (DLP) or proxy solutions to inspect payloads when feasible, and regularly review cloud communication logs for anomalies or unapproved endpoints.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1486, 'OBS-RISK', N'Uncontrolled OT-to-cloud communication could leak critical data.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1486, 'OBS-RISK-STATEMENT', N'Unrestricted communication between OT systems and cloud services opens paths for data exfiltration, command-and-control (C2) activity, or unauthorized third-party integrations. Without visibility and control, these flows can bypass internal defenses, exposing sensitive operational data and threatening compliance.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1486, 'OBS-VULNERABILITY', N'Lack of restriction and monitoring over OT-cloud communication flows.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1487, 'OBS-DISCOVERY', N'Privileged activity within OT systems is not monitored or logged. SIEM integration is incomplete, and coordination with incident response or physical security teams is limited or nonexistent.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1487, 'OBS-IMPACT', N'Undetected privileged account abuse could allow system sabotage, data theft, or disruption of industrial processes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1487, 'OBS-RECOMMENDATION', N'Deploy SIEM for privileged monitoring; integrate with physical logs; train IR team on escalation.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1487, 'OBS-RISK', N'Privileged OT accounts are not adequately monitored for misuse.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1487, 'OBS-RISK-STATEMENT', N'Unmonitored privileged use could miss attacks, delaying OT incident response.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1487, 'OBS-VULNERABILITY', N'Lack of enhanced monitoring for OT privileged accounts.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1488, 'OBS-DISCOVERY', N'The organization does not use protections to prevent spam and malicious email.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1488, 'OBS-IMPACT', N'Phishing or malicious emails could provide attackers entry points into integrated OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1488, 'OBS-RECOMMENDATION', N'Filter email at gateways; restrict the OT network from accessing email services; monitor for phishing attempts.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1488, 'OBS-RISK', N'Spam or risky email content could introduce threats into IT/OT networks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1488, 'OBS-RISK-STATEMENT', N'')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1488, 'OBS-VULNERABILITY', N'Absence of spam and phishing protection at network boundaries.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1489, 'OBS-DISCOVERY', N'The organization does not monitor for publicly available internal information across IT and OT domains. Exposure sources such as websites, social media, third-party vendors, past RFPs, or passive network enumeration tools (e.g., Shodan) are not regularly reviewed. There is no cross-team coordination between IT, OT, and communications teams to identify or respond to external information exposure.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1489, 'OBS-IMPACT', N'Adversaries may exploit exposed OT diagrams, ICS vendor types, or staffing details to craft highly effective phishing, supply chain, or exploitation campaigns, leading to business disruption or physical process impact.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1489, 'OBS-RECOMMENDATION', N'Implement an organization-wide information exposure monitoring program. Use OSINT tooling (e.g., Shodan, Censys), metadata scrubbing, dark web monitoring, and web footprint reviews to identify publicly accessible IT and OT system details, business documentation, and personnel data. Coordinate between IT, OT, legal, and communications teams to ensure coverage and actionability. Review exposures at least quarterly and correlate with threat intelligence and internal risk registers.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1489, 'OBS-RISK', N'External visibility into technical or organizational details can accelerate attacker reconnaissance and targeting.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1489, 'OBS-RISK-STATEMENT', N'Failure to monitor and manage the organization’s external attack surface increases the likelihood of targeted cyberattacks by enabling adversaries to gather intelligence across both IT and OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1489, 'OBS-VULNERABILITY', N'No unified process exists to identify and mitigate externally exposed information across both IT and OT ecosystems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1490, 'OBS-DISCOVERY', N'The organization does not perform ICS-appropriate risk assessments or lacks tailored approaches(e.g., passive scanning/inventory checks), leaving OT vulnerabilities unassessed or undocumented.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1490, 'OBS-IMPACT', N'Adversaries could exploit unpatched or misconfigured systems, leading to operational disruptions or safety incidents.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1490, 'OBS-RECOMMENDATION', N'Implement an ICS-tailored risk assessment program that includes passive scanning tools(where feasible), regular hardware/software inventory reviews for patch/version status, and manual or offline configuration checks. Document findings in a risk register to prioritize mitigation actions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1490, 'OBS-RISK', N'Lack of tailored ICS risk assessments leaves vulnerabilities undetected.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1490, 'OBS-RISK-STATEMENT', N'Critical weaknesses may go undetected, enabling adversaries to exploit unpatched or misconfigured systems causing operational disruptions or safety incidents.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1490, 'OBS-VULNERABILITY', N'Incomplete or absent ICS-specific vulnerability identification.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1491, 'OBS-DISCOVERY', N'The organization does not maintain a defined process for identifying, prioritizing, and remediating OT-specific threats and vulnerabilities. Threat intelligence from vendors, CISA ICS advisories, or industry sources is not systematically tracked or distributed to OT stakeholders. Vulnerability management practices rely heavily on informal methods and are not risk-based. No documented strategy exists for passive asset discovery, safe vulnerability identification, or integrating threat intelligence into OT operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1491, 'OBS-IMPACT', N'Unaddressed vulnerabilities could lead to breaches, system manipulation, or operational downtime.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1491, 'OBS-RECOMMENDATION', N'Establish and document an OT-specific vulnerability management process that includes:

Passive asset discovery and monitoring to identify device types, firmware versions, and anomalies without disrupting operations.

Risk-based prioritization that considers system criticality, potential physical impact, and known threat activity (e.g., CISA KEV catalog).

Intelligence integration workflows to track and disseminate ICS vendor advisories, CISA ICS alerts, and sector-specific ISAC threat reports to OT personnel.

Remediation plans aligned with maintenance windows, emphasizing compensating controls when immediate patching is not feasible.

Regularly review and adjust priorities based on emerging threats, operational changes, and updated intelligence. Use downtime or lab environments to validate patches and scanning tools prior to production deployment.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1491, 'OBS-RISK', N'Failure to prioritize and act on OT vulnerabilities exposes critical systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1491, 'OBS-RISK-STATEMENT', N'Failure to adopt a structured, risk-informed approach to OT threat and vulnerability management increases the likelihood of undetected security gaps. Without passive discovery or intelligence integration, critical vulnerabilities may persist unaddressed. In an ICS environment, exploitation of these weaknesses could result in loss of visibility, unauthorized command execution, process manipulation, or unsafe operating conditions — especially if adversaries target safety-critical or mission-essential assets.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1491, 'OBS-VULNERABILITY', N'Inadequate OT vulnerability management and threat intelligence sharing.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1492, 'OBS-DISCOVERY', N'OT media is not labeled, tracked, or logged. Ownership and sensitivity are unclear, leading to potential misuse.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1492, 'OBS-IMPACT', N'Sensitive OT data may leave the environment or contaminated media may re-enter.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1492, 'OBS-RECOMMENDATION', N'Label removable media and maintain logs of use/ownership.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1492, 'OBS-RISK', N'No identification increases likelihood of mishandling.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1492, 'OBS-RISK-STATEMENT', N'Untracked media can be misused, lost, or reused improperly.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1492, 'OBS-VULNERABILITY', N'Lack of labeling or tracking for removable media.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1493, 'OBS-DISCOVERY', N'Peripheral device usage on ICS/OT systems is not consistently controlled. In some environments, USB ports remain enabled without technical enforcement or physical locks. Logging and monitoring of device connections is minimal or absent. Legacy systems may lack the ability to restrict or detect device usage, and compensating controls are often informal or reactive.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1493, 'OBS-IMPACT', N'Introduction of malware or unauthorized access via unmanaged peripherals can result in operational outages, unsafe process states, or loss of safety-critical system integrity. For example, malicious firmware or autorun payloads can alter logic in programmable controllers or facilitate C2 communications through bridged IT-OT paths.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1493, 'OBS-RECOMMENDATION', N'Implement technical controls to disable unauthorized peripherals, monitor connected devices, use physical port locks, and enforce removable media policies.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1493, 'OBS-RISK', N'Implement technical solutions to detect and restrict unauthorized peripherals.

Use device control software to enforce policy (e.g., block all but whitelisted hardware IDs).

Where unsupported, employ compensating controls like BIOS/UEFI port disablement, physical port blockers, or operational policies with supervisory validation.

Log all device connections and alert on unapproved usage.

Conduct regular audits of device control configurations and review usage logs monthly.

Train OT personnel on secure media handling and implement a clean media policy, including mandatory scanning/quarantine procedures.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1493, 'OBS-RISK-STATEMENT', N'Uncontrolled use of peripherals on ICS/OT systems presents a direct path for malware infection, misconfiguration, or data loss. Threat actors have historically leveraged USB devices for lateral movement and initial compromise (e.g., Stuxnet, Russia''s BlackEnergy campaign). Even accidental connection of infected media can bypass perimeter defenses, placing critical infrastructure at risk.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1493, 'OBS-VULNERABILITY', N'Lack of enforced monitoring and restriction of peripheral device usage allows adversaries or insider threats to bypass digital safeguards.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1494, 'OBS-DISCOVERY', N'Removable media can be used freely on ICS workstations or field devices.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1494, 'OBS-IMPACT', N'Malware or sensitive data loss due to unrestricted USB usage.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1494, 'OBS-RECOMMENDATION', N'Enforce technical/media controls, disable or restrict USB ports, and use media control policies.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1494, 'OBS-RISK', N'Lack of enforcement undermines boundary protections.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1494, 'OBS-RISK-STATEMENT', N'Media may introduce malware or exfiltrate data without control.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1494, 'OBS-VULNERABILITY', N'Media ports are active and unmonitored.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1495, 'OBS-DISCOVERY', N'OT networks lack outbound traffic monitoring or DPI. Exfiltration attempts may go unnoticed due to flat architecture or lack of detection tools.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1495, 'OBS-IMPACT', N'Exfiltration of sensitive data could enable external manipulation, espionage, or precise targeting of ICS operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1495, 'OBS-RECOMMENDATION', N'Implement egress monitoring tools, enforce strict outbound traffic policies, apply deep packet inspection to detect unauthorized data movement, and log/alert on suspicious transfers.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1495, 'OBS-RISK', N'Loss of ICS/OT data without detection.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1495, 'OBS-RISK-STATEMENT', N'Undetected exfiltration of sensitive ICS data could lead to theft of operational secrets or adversary attack planning.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1495, 'OBS-VULNERABILITY', N'Lack of boundary monitoring and controls allows undetected exfiltration of critical ICS information.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1496, 'OBS-DISCOVERY', N'The organization does not monitor OT communications at key network boundaries or internal junctions. Anomaly detection is limited or absent for cross-segment and vendor-related traffic.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1496, 'OBS-IMPACT', N'Breaches or disruptions could go undetected until major damage occurs, delaying containment.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1496, 'OBS-RECOMMENDATION', N'Install OT-specific IDS at boundaries and key points; log all traffic; integrate with vendor access monitoring.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1496, 'OBS-RISK', N'Unmonitored OT communications at critical points miss intrusion indicators.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1496, 'OBS-RISK-STATEMENT', N'Unmonitored OT traffic could miss attack indicators, delaying response to breaches or disruptions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1496, 'OBS-VULNERABILITY', N'Absence of IDS/monitoring at OT network boundaries and vendor connection points.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1497, 'OBS-DISCOVERY', N'Encrypted traffic can bypass inspection if the organization lacks a strategy to handle SSL/TLS at enforcement points, allowing hidden malicious data transfers.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1497, 'OBS-IMPACT', N'Attackers could leverage encrypted tunnels to hide malicious activity, exfiltrate sensitive data, or introduce malware into OT systems without detection.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1497, 'OBS-RECOMMENDATION', N'The organization should implement SSL/TLS decryption at security enforcement points to ensure that encrypted traffic is inspected before entering or leaving critical OT environments. Traffic should be monitored for anomalies, including encrypted remote access sessions, VPN tunnels, and vendor connections. Security gateways should enforce policies that prevent unauthorized encrypted communication channels from bypassing flow control mechanisms. If decryption is not feasible for certain OT protocols, alternative detection methods such as traffic behavior analysis, metadata inspection, and logging should be implemented to identify suspicious encrypted activity.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1497, 'OBS-RISK', N'Encrypted traffic may bypass flow control and content inspection mechanisms.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1497, 'OBS-RISK-STATEMENT', N'Without proper encrypted traffic flow enforcement, malicious actors can use encryption to hide unauthorized data transfers, bypass security monitoring, and execute hidden attacks within OT environments. A lack of inspection or decryption capabilities at security enforcement points increases the risk of data exfiltration, hidden command execution, and unauthorized remote access. Attackers could exploit encrypted tunnels to introduce malware into OT systems or disrupt industrial operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1497, 'OBS-VULNERABILITY', N'Lack of SSL/TLS inspection or alternative encrypted traffic monitoring for OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1498, 'OBS-DISCOVERY', N'The organization does not actively control how data moves within the OT environment, allowing unrestricted communication between devices and systems. Industrial protocols such as Modbus, DNP3, BACnet, and OPC-UA are not filtered or restricted, which could enable unauthorized lateral movement. There is no enforcement of one-way vs. bidirectional traffic policies, meaning that critical process control systems could be exposed to IT-originated threats. Additionally, message content inspection is not performed, leaving the environment vulnerable to malicious command execution or unauthorized reprogramming of controllers and PLCs.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1498, 'OBS-IMPACT', N'Malicious lateral movement or unauthorized command injection could disrupt critical OT processes, resulting in operational failure or safety incidents.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1498, 'OBS-RECOMMENDATION', N'The organization should implement strict protocol filtering at OT enforcement points to limit communication to only necessary, approved industrial protocols. One-way flow enforcement should be established where applicable to ensure telemetry data can leave OT but control commands cannot enter from IT. Deep packet inspection(DPI) should be used to validate and restrict control messages, blocking unauthorized commands. Network segmentation should also be leveraged to ensure that only approved systems within OT can communicate with each other, preventing lateral movement.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1498, 'OBS-RISK', N'Lack of enforced OT information flow controls allows unrestricted and potentially malicious communications.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1498, 'OBS-RISK-STATEMENT', N'Without information flow controls, unauthorized devices or compromised systems can send malicious commands to industrial controllers, disrupt critical processes, or move laterally across OT networks. A lack of directional data enforcement means IT-originated threats(e.g., ransomware, unauthorized remote access) could impact OT operations. Additionally, sensitive process data could be unknowingly sent to unauthorized destinations, leading to data exfiltration or compliance violations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1498, 'OBS-VULNERABILITY', N'Absence of protocol filtering, one-way flow enforcement, and message content inspection between OT and IT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1499, 'OBS-DISCOVERY', N'The organization does not prevent direct Internet access from OT devices. Network boundaries are not configured to block or monitor outbound connections, exposing OT systems to external threats.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1499, 'OBS-IMPACT', N'Direct internet exposure could allow adversaries to remotely exploit OT devices, manipulate operations, or steal sensitive data.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1499, 'OBS-RECOMMENDATION', N'Block OT Internet access; use proxies if needed; monitor for external connections.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1499, 'OBS-RISK', N'Internet-connected OT devices expose critical systems to external attacks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1499, 'OBS-RISK-STATEMENT', N'Internet exposure could allow OT attacks, risking operational integrity.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1499, 'OBS-VULNERABILITY', N'Lack of enforced network segmentation preventing direct Internet exposure for OT.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1500, 'OBS-DISCOVERY', N'The organization does not route OT Internet traffic through authenticated proxies, or lacks adequate logs and authentication checks, leaving egress traffic unmonitored or unrestricted.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1500, 'OBS-IMPACT', N'Unmonitored outbound traffic could expose sensitive OT data or allow threat actor command channels.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1500, 'OBS-RECOMMENDATION', N'Require all Internet traffic to pass through authenticated proxy servers, enforce strong credentials for proxy access, and monitor egress logs to detect anomalies or unauthorized connections.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1500, 'OBS-RISK', N'Unauthenticated OT Internet egress could leak data or enable backdoors.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1500, 'OBS-RISK-STATEMENT', N'Without controlled and authenticated egress, OT systems may be exposed to external threats or data leaks, and malicious traffic could go undetected.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1500, 'OBS-VULNERABILITY', N'Lack of authenticated proxy control over OT Internet traffic.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1501, 'OBS-DISCOVERY', N'Verify VLAN configs; test isolation; monitor traffic logs.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1501, 'OBS-IMPACT', N'Attackers could pivot across systems, expanding the scope of compromise or disrupting operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1501, 'OBS-RECOMMENDATION', N'Isolate OT VLANs; enforce with ACLs; monitor for cross-VLAN traffic.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1501, 'OBS-RISK', N'Lack of VLAN isolation allows unauthorized lateral movement across OT networks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1501, 'OBS-RISK-STATEMENT', N'Unisolated VLANs could allow OT breaches, risking operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1501, 'OBS-VULNERABILITY', N'Insufficient segmentation between OT and other networks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1502, 'OBS-DISCOVERY', N'The organization does not have formal security agreements or controls in place before allowing external systems to process or access OT data, leaving compliance and security requirements unverified.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1502, 'OBS-IMPACT', N'Insufficient controls on external systems could lead to OT data breaches, data corruption, or unauthorized manipulation of operational processes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1502, 'OBS-RECOMMENDATION', N'Require formal agreements(e.g., contracts, MOUs) that specify security controls for external systems handling OT data, ensure compliance reviews before granting access, and regularly audit external providers to confirm they meet organizational security standards.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1502, 'OBS-RISK', N'External systems may process or access OT data without verified security controls.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1502, 'OBS-RISK-STATEMENT', N'Without verified security measures or formal agreements, external systems could introduce vulnerabilities or fail to protect OT data, resulting in unauthorized access, data leaks, or compromises to critical processes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1502, 'OBS-VULNERABILITY', N'Absence of formal security agreements and verification processes for external systems accessing OT data.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1503, 'OBS-DISCOVERY', N'The organization does not effectively prohibit or monitor non-organizational devices from connecting to OT or IT networks, lacking NAC enforcement or a documented exception process.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1503, 'OBS-IMPACT', N'Unauthorized devices could introduce malware, enable unmonitored lateral movement, or expose OT systems to external attacks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1503, 'OBS-RECOMMENDATION', N'Use NAC or similar technical controls to ban personal devices by default, define a strict exception process with multi-factor authentication, and monitor all connections for compliance.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1503, 'OBS-RISK', N'Unauthorized non-organizational devices may connect to OT networks, bypassing security controls.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1503, 'OBS-RISK-STATEMENT', N'Unauthorized personal or vendor devices could introduce malware or create unmonitored access paths, compromising OT security and continuity.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1503, 'OBS-VULNERABILITY', N'Lack of controls to prohibit or monitor personal or third-party device connections in OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1504, 'OBS-DISCOVERY', N'The organization does not ensure portable devices encrypted storage or lacks procedures to verify compliance, leaving sensitive data(e.g., configs, logs) unprotected.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1504, 'OBS-IMPACT', N'Unprotected portable storage devices could leak sensitive OT configuration files, system logs, or operational data, aiding adversaries in targeting or disrupting control systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1504, 'OBS-RECOMMENDATION', N'Enforce encryption on all portable devices; use encrypted USB drives and physical locks for non-compliant devices; update inventory to track compliance.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1504, 'OBS-RISK', N'Lack of encryption on portable devices storing OT data exposes sensitive information to theft or misuse.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1504, 'OBS-RISK-STATEMENT', N'Unencrypted portable devices could expose sensitive data(e.g., configs, logs), leading to data breaches or data leakage for another attack vector.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1504, 'OBS-VULNERABILITY', N'Storage of sensitive data on unencrypted portable media.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1505, 'OBS-DISCOVERY', N'The organization does not have clear policies or technical controls governing portable storage device usage on external systems, leaving them unscanned or unrestricted before being introduced into OT or IT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1505, 'OBS-IMPACT', N'Malware introduced via unmanaged portable devices could spread across OT systems, disrupting operations or compromising critical assets.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1505, 'OBS-RECOMMENDATION', N'')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1505, 'OBS-RISK', N'Portable storage devices introduced through external systems could bypass security protections.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1505, 'OBS-RISK-STATEMENT', N'Unregulated portable storage devices can carry malware or unauthorized software, potentially compromising OT or IT systems, leading to data breaches, operational disruption, or spread of malicious code across the organization.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1505, 'OBS-VULNERABILITY', N'Lack of scanning or restriction policies for portable storage media in OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1506, 'OBS-DISCOVERY', N'Sensitive information may be leaked due to insecure sharing practices.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1506, 'OBS-IMPACT', N'Insecure sharing could leak OT configurations or operational data, enabling adversaries to craft targeted attacks or cause regulatory violations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1506, 'OBS-RECOMMENDATION', N'Use encrypted channels(e.g., SFTP) for sharing; verify recipient security; log all data transfers.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1506, 'OBS-RISK', N'Sensitive OT information may be shared through insecure channels without adequate verification.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1506, 'OBS-RISK-STATEMENT', N'Insecure sharing could leak OT data, enabling attacks or regulatory violations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1506, 'OBS-VULNERABILITY', N'Lack of encryption and verification controls when transferring OT-sensitive data to external parties.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1507, 'OBS-DISCOVERY', N'Mobile and transient devices used in OT environments are not handled consistently or in alignment with security best practices.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1507, 'OBS-IMPACT', N'Malware introduction, reconfiguration errors, or unauthorized access can lead to loss of visibility, operational downtime, or safety events.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1507, 'OBS-RECOMMENDATION', N'Establish a formal transient device policy requiring: pre-approval, hardened baselines, up-to-date AV/EDR, posture validation, minimal access privileges, disabled wireless interfaces, activity logging, and post-use sanitation. Tooling laptops used for configuration or diagnostics must be organization-controlled and treated as extensions of the ICS — not general-purpose endpoints. These devices must be purpose-built or assigned explicitly for OT use and maintained to the same standards as fixed ICS assets. Contractor laptops should be explicitly prohibited from connecting to OT environments. All external interactions must occur through organization-owned, approved systems under direct management.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1507, 'OBS-RISK', N'Transient devices bypass perimeter and baseline protections, and are a prime vector for introducing malware or ransomware into sensitive ICS environments. Their use can directly impact operational continuity, safety, and system integrity — often without detection.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1507, 'OBS-RISK-STATEMENT', N'Unregulated transient systems may introduce malware, misconfigure devices, or expose sensitive ICS networks to adversaries.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1507, 'OBS-VULNERABILITY', N'Lack of enforcement of controls for portable and transient devices used in ICS environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1508, 'OBS-DISCOVERY', N'The organization does not encrypt OT protocol traffic nor apply compensating controls for legacy systems that lack native encryption support. Communications such as Modbus or DNP3 remain in plaintext, including across trust boundaries (e.g., OT to IT or cloud), making them susceptible to interception, replay, or manipulation.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1508, 'OBS-IMPACT', N'Attackers could intercept control signals or exfiltrate sensitive OT data, risking operational stability and confidentiality.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1508, 'OBS-RECOMMENDATION', N'For internal OT zones: Acknowledge that many ICS protocols lack encryption by design. Prioritize zoning, segmentation, and firewalling to contain traffic and minimize exposure.

For OT-to-IT/cloud/internet paths: Implement encryption (e.g., TLS for Modbus/TCP, VPN tunnels, or secure gateways) when traffic leaves the protected OT enclave.

For legacy or fixed-function devices: Apply compensating controls such as unidirectional gateways, tightly controlled firewall rules, or overlay encryption via VPN concentrators.

Conduct monthly audits of communication paths that traverse trust boundaries to ensure appropriate protection mechanisms (encryption or compensating controls) are in place and documented.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1508, 'OBS-RISK', N'Unencrypted OT protocols expose communications to interception and tampering.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1508, 'OBS-RISK-STATEMENT', N'While many OT protocols prioritize safety and availability over data integrity, unencrypted communications, especially when traversing network boundaries, can be intercepted, manipulated, or replayed. This creates opportunities for man-in-the-middle attacks, external reconnaissance, and unauthorized command injection, particularly when traffic exits the trusted OT zone.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1508, 'OBS-VULNERABILITY', N'Unencrypted industrial protocol communications.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1509, 'OBS-DISCOVERY', N'The organization does not enforce a default-deny policy for OT network traffic. Unauthorized flows are permitted by default, and exceptions are not consistently documented or monitored.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1509, 'OBS-IMPACT', N'Unauthorized traffic could be used to move laterally, exfiltrate data, or disrupt critical OT processes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1509, 'OBS-RECOMMENDATION', N'Configure firewalls with default-deny; maintain a documented exception list; use SIEM to alert on unauthorized flows.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1509, 'OBS-RISK', N'Permissive OT network policies allow unauthorized communication flows.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1509, 'OBS-RISK-STATEMENT', N'Permissive policies could allow unauthorized OT traffic, risking system compromise or operational interference.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1509, 'OBS-VULNERABILITY', N'Absence of default-deny network rules within OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1510, 'OBS-DISCOVERY', N'The organization lacks a defined capability or procedure to promptly disable remote access and isolate OT environments during a security incident without causing operational disruption.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1510, 'OBS-IMPACT', N'Inability to rapidly isolate OT environments could allow threats to deepen footholds, escalate attacks, disrupt safety systems, and increase the potential for catastrophic operational impacts.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1510, 'OBS-RECOMMENDATION', N'Implement controlled remote access kill switches, network segmentation, and predefined incident response procedures to rapidly isolate OT from external threats.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1510, 'OBS-RISK', N'Lack of immediate OT isolation capability prolongs exposure to active threats during incidents.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1510, 'OBS-RISK-STATEMENT', N'Without the ability to immediately disconnect or isolate remote access, threats can persist, leading to system compromise, process disruption, or safety risks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1510, 'OBS-VULNERABILITY', N'Lack of rapid OT environment isolation capability in incident response processes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1511, 'OBS-DISCOVERY', N'Organization does not consistently encrypt remote sessions or relies on plaintext protocols(e.g., Telnet, VNC, SNMPv1, insecure RDP), leaving session data exposed to interception and manipulation.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1511, 'OBS-IMPACT', N'Unencrypted remote sessions could enable adversaries to steal credentials, inject malicious commands, or monitor critical operational traffic, leading to data breaches and system manipulation.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1511, 'OBS-RECOMMENDATION', N'Mandate secure communications for remote access and management(VPN, SSH, TLS, IPsec); block or detect any fallback to unencrypted connections')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1511, 'OBS-RISK', N'Failure to encrypt remote access sessions exposes sensitive OT communications to interception and manipulation.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1511, 'OBS-RISK-STATEMENT', N'Insecure or unencrypted remote protocols can allow attackers to intercept or alter OT traffic, stealing credentials or injecting malicious commands.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1511, 'OBS-VULNERABILITY', N'Reliance on plaintext protocols for remote OT access.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1512, 'OBS-DISCOVERY', N'Internal system authentication is not required after initial remote access into OT networks, such as transitions through VPNs, OT DMZ jump hosts, or bastion hosts. Users may be able to pivot across OT systems without reauthentication.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1512, 'OBS-IMPACT', N'Attackers gaining initial access could move freely within OT systems, modifying configurations or disrupting operations without detection.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1512, 'OBS-RECOMMENDATION', N'Implement re-authentication requirements at OT boundary points, including OT DMZ jump hosts and bastion hosts. Use system-level MFA or unique credentials per OT segment. For legacy OT assets that lack authentication capabilities, enforce manual operator validation procedures and enhance logging. Perform monthly audits to validate re-authentication events and control effectiveness.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1512, 'OBS-RISK', N'Lack of internal authentication enforcement after initial remote access increases lateral movement risks within OT networks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1512, 'OBS-RISK-STATEMENT', N'Lack of re-authentication after transitioning from IT to OT or remote OT access allowing potential unauthorized lateral movement. Attackers using valid credentials can access and pivot across sensitive OT systems, increasing the potential for disruption or manipulation without being challenged or logged.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1512, 'OBS-VULNERABILITY', N'Absence of secondary system-level authentication for remote OT access.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1513, 'OBS-DISCOVERY', N'The organization does not require multi-factor authentication for OT remote access. Systems using legacy protocols lack compensating access controls.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1513, 'OBS-IMPACT', N'Unauthorized remote access could lead to manipulation of critical systems, impacting safety and production.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1513, 'OBS-RECOMMENDATION', N'Enforce MFA(e.g., OTP) for remote access; deploy physical tokens for legacy systems; audit MFA usage monthly.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1513, 'OBS-RISK', N'Single-factor remote access could be easily breached, risking OT control takeover.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1513, 'OBS-RISK-STATEMENT', N'Single-factor remote access could be easily breached, risking OT control takeover.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1513, 'OBS-VULNERABILITY', N'Lack of enforced MFA for OT remote access.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1514, 'OBS-DISCOVERY', N'The organization does not use a documented/approved approach for remote OT access, lacks consistent usage restrictions, or fails to monitor remote sessions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1514, 'OBS-IMPACT', N'Weak or inconsistent remote access controls could enable attackers or unauthorized users to alter system configurations, disrupt operations, or compromise critical OT assets without detection.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1514, 'OBS-RECOMMENDATION', N'Establish a formal remote access policy with approved methods, IP filters/whitelists, MFA, and real-time monitoring. Ensure any legacy device has compensating controls if it cannot support modern methods.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1514, 'OBS-RISK', N'Lack of formalized remote access governance could allow unauthorized or insecure remote access pathways into OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1514, 'OBS-RISK-STATEMENT', N'Without a formal remote access policy, remote access may be abused or hijacked, leading to unauthorized changes, disruptions, or other adverse impacts on OT operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1514, 'OBS-VULNERABILITY', N'Lack of governance and security controls over OT remote access pathways.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1515, 'OBS-DISCOVERY', N'Organization is not enforcing any authentication(or uses weak credentials) prior to granting network access to OT assets')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1515, 'OBS-IMPACT', N'Failure to enforce strong pre-connection authentication could enable attackers to gain direct access to OT assets, manipulate operations, or bypass monitoring systems, resulting in safety or operational failures.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1515, 'OBS-RECOMMENDATION', N'Enforce strong pre-connection authentication(e.g., MFA); use physical tokens for latency-sensitive systems; log all access attempts.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1515, 'OBS-RISK', N'Weak or missing authentication for OT remote access could allow unauthorized access before connection establishment.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1515, 'OBS-RISK-STATEMENT', N'Weak or missing authentication could enable unauthorized remote control of OT systems, risking operational integrity or safety.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1515, 'OBS-VULNERABILITY', N'Lack of pre-connection authentication enforcement for remote OT access.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1516, 'OBS-DISCOVERY', N'Remote privileged access to OT systems is not consistently documented or restricted based on operational necessity and does not maintain a centralized approval process or audit trail for privileged commands executed remotely.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1516, 'OBS-IMPACT', N'Unauthorized or unmonitored remote privileged access could result in covert configuration changes, disabled safety systems, or disruption of critical processes, severely impacting system resilience.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1516, 'OBS-RECOMMENDATION', N'Implement just-in-time remote access to OT systems, enforce multi-factor authentication(MFA), require strict approvals for privileged access, and enable full session logging and monitoring to track and review all remote administrative actions')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1516, 'OBS-RISK', N'Lack of authorization and documentation for privileged remote access hinders traceability and increases the risk of undetected malicious actions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1516, 'OBS-RISK-STATEMENT', N'Without proper authorization and logging of privileged remote access, malicious actors or insider threats could modify OT configurations, disrupt critical processes, or disable safety mechanisms without detection.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1516, 'OBS-VULNERABILITY', N'Absence of privileged access approval, session recording, and auditability for OT remote operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1517, 'OBS-DISCOVERY', N'The organization does not restrict or document privileged commands, allowing remote users to issue OT commands without oversight.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1517, 'OBS-IMPACT', N'Unrestricted privileged commands issued remotely could cause unintended or malicious changes to control logic, safety systems, or production processes, leading to system instability or harm.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1517, 'OBS-RECOMMENDATION', N'Limit privileged commands to secure consoles/jump hosts; document approvals for operational needs; log & audit all privileged actions')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1517, 'OBS-RISK', N'Failure to restrict privileged network-based OT commands allows unauthorized remote system manipulation.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1517, 'OBS-RISK-STATEMENT', N'Unrestricted privileged commands can lead to remote OT manipulation, potentially causing safety hazards or operational disruptions')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1517, 'OBS-VULNERABILITY', N'Lack of privileged command enforcement and oversight for OT remote operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1518, 'OBS-DISCOVERY', N'The organization does not prevent the use of insecure remote management protocols in OT environments(e.g., Telnet, FTP, SNMPv1) or consistently monitor vendor remote sessions, leaving sensitive communications exposed.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1518, 'OBS-IMPACT', N'Use of insecure protocols could allow attackers to capture credentials, alter remote management traffic, and disrupt or manipulate critical OT functions, leading to operational degradation or safety issues.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1518, 'OBS-RECOMMENDATION', N'Block Telnet/others; monitor for use; restrict vendor protocols to secure options. Block or disable Telnet/others; monitor for usage; restrict vendor protocols to secure options(SSH, SFTP, SNMPv3); track and log vendor connections.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1518, 'OBS-RISK', N'Lack of restrictions on insecure remote protocols leaves OT environments vulnerable to interception and unauthorized access.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1518, 'OBS-RISK-STATEMENT', N'Insecure protocols(Telnet, FTP, SNMPv1) could be intercepted or exploited, risking safety or production.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1518, 'OBS-VULNERABILITY', N'Lack of enforced secure remote management protocol restrictions in OT.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1519, 'OBS-DISCOVERY', N'Wireless signals extend beyond operational boundaries, increasing the risk of external interception.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1519, 'OBS-IMPACT', N'Wireless signals extending beyond operational boundaries could be intercepted by adversaries to gain intelligence, hijack sessions, or introduce rogue devices, leading to system breaches.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1519, 'OBS-RECOMMENDATION', N'Optimize antenna placement and power levels, limit external exposure, and use shielding or frequency controls where needed.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1519, 'OBS-RISK', N'Excessive wireless signal propagation allows external interception and potential exploitation of OT networks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1519, 'OBS-RISK-STATEMENT', N'Unrestricted wireless signals allow for eavesdropping and could allow for unauthorized access.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1519, 'OBS-VULNERABILITY', N'Uncontrolled wireless signal propagation beyond physical perimeters.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1520, 'OBS-DISCOVERY', N'The organization does not consistently disable or verify unused wireless capabilities(e.g., Wi-Fi, Bluetooth, RF) on IT and OT devices before deployment, leaving them enabled and unmonitored.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1520, 'OBS-IMPACT', N'Unsecured wireless interfaces could be exploited by adversaries to infiltrate networks, bypass perimeter defenses, and compromise critical OT assets.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1520, 'OBS-RECOMMENDATION', N'Establish a process to disable all unused wireless functions on devices prior to deployment and regularly verify that these features remain disabled—particularly after firmware or software updates that might re-enable them.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1520, 'OBS-RISK', N'Failure to disable unused wireless features creates unmonitored access points into OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1520, 'OBS-RISK-STATEMENT', N'Unused but active wireless functions may provide unmonitored entry points for unauthorized access or exploitation, expanding the overall attack surface.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1520, 'OBS-VULNERABILITY', N'Failure to disable unnecessary wireless functionalities, expanding attack vectors.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1521, 'OBS-DISCOVERY', N'Wireless access policies are not clearly defined or enforced, leading to inconsistent security across wireless technologies.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1521, 'OBS-IMPACT', N'Inconsistent wireless security could enable unauthorized devices to connect, facilitating lateral movement, data exfiltration, or operational disruptions across OT systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1521, 'OBS-RECOMMENDATION', N'Develop and enforce clear wireless security policies for all technologies, with regular audits to ensure compliance.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1521, 'OBS-RISK', N'Absence of standardized wireless access controls could lead to unauthorized wireless access to OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1521, 'OBS-RISK-STATEMENT', N'Lack of standard policies and practice can lead to inconsistent enforcement of wireless technologies and can cause unauthorized access leading to system compromise.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1521, 'OBS-VULNERABILITY', N'Absence of standardized wireless access control policies in OT networks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1522, 'OBS-DISCOVERY', N'The organization does not consistently secure or authenticate various wireless connections(e.g., Wi-Fi, radio links, IIoT sensors), or lacks compensating controls for OT devices that cannot support modern encryption or authentication methods, leaving critical data transmissions exposed.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1522, 'OBS-IMPACT', N'Weak or missing wireless protections could result in adversaries compromising device communications, disrupting control operations, or staging broader attacks across OT networks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1522, 'OBS-RECOMMENDATION', N'Implement robust encryption and authentication(e.g., WPA2/WPA3 Enterprise, secure radio protocols, certificate-based IIoT authentication) across all wireless links. For devices lacking built-in security, deploy compensating controls such as dedicated frequency management, network segmentation, or physical isolation. Regularly audit wireless configurations, update firmware, and monitor for anomalous wireless activity.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1522, 'OBS-RISK', N'Wireless communications lacking authentication and encryption expose critical OT data to interception and manipulation.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1522, 'OBS-RISK-STATEMENT', N'Unsecured or weakly protected wireless communications can be intercepted, spoofed, or manipulated by adversaries, leading to unauthorized access, data exfiltration, or disruption of critical OT processes—especially if legacy devices or specialized radio links are not properly safeguarded.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1522, 'OBS-VULNERABILITY', N'Insecure wireless transmission and insufficient device authentication controls.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1523, 'OBS-DISCOVERY', N'The organization does not maintain a formal OT cybersecurity policy. Roles, resources, and technical controls are undefined or outdated, and there is no structured approval or review cycle.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1523, 'OBS-IMPACT', N'Security gaps due to outdated policies could lead to regulatory fines or operational risks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1523, 'OBS-RECOMMENDATION', N'Draft OT-specific policy; secure senior approval; update annually with stakeholder input.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1523, 'OBS-RISK', N'An outdated or weak OT cybersecurity policy could create misalignments in security practices and regulatory compliance.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1523, 'OBS-RISK-STATEMENT', N'Weak policy could misalign OT security, risking gaps or non-compliance.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1523, 'OBS-VULNERABILITY', N'Weak or outdated OT cybersecurity policy and control definitions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1524, 'OBS-DISCOVERY', N'The organization does not conduct independent OT cybersecurity assessments or fails to ensure assessments include vendor and operational dependencies. Review frequency and scope are inconsistent.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1524, 'OBS-IMPACT', N'Without objective evaluations, critical vulnerabilities in OT systems may persist, increasing the likelihood of successful attacks or compliance failures.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1524, 'OBS-RECOMMENDATION', N'Conduct annual third-party audits; include vendor/ops scope; act on findings promptly.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1524, 'OBS-RISK', N'Lack of independent OT cybersecurity assessments could allow risks to go unrecognized and unaddressed.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1524, 'OBS-RISK-STATEMENT', N'Biased or incomplete assessments could miss OT risks, weakening security.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1524, 'OBS-VULNERABILITY', N'Failure to conduct third-party or independent OT cybersecurity assessments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1525, 'OBS-DISCOVERY', N'The organization lacks a well-defined cybersecurity program that effectively addresses the unique needs and risks of its OT environment. In many cases, cybersecurity governance is either absent, inherited from IT without modification, or focused on basic documentation rather than operational continuity or resilience.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1525, 'OBS-IMPACT', N'Essential services such as power, water, or transportation may fail or degrade during IT-related incidents, with limited visibility or delayed response.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1525, 'OBS-RECOMMENDATION', N'Develop and implement a cybersecurity program that specifically addresses OT operational priorities, including service continuity, safety, and legacy system constraints. Where an IT cybersecurity program already exists, adapt it by incorporating input from OT operators, engineers, and vendors. Define how critical OT functions will be protected and sustained during IT outages, and review the program regularly for alignment with mission objectives and real-world constraints.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1525, 'OBS-RISK', N'Lack of tailored OT cybersecurity governance leaves critical systems vulnerable to disruption, unsafe states, or unmonitored compromise.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1525, 'OBS-RISK-STATEMENT', N'Without a cybersecurity program tailored to the operational and technical realities of OT, the organization is unprepared to maintain safe and reliable system performance during IT disruptions or cyber incidents. This increases the likelihood of uncontrolled failure, degraded service delivery, or delayed recovery in critical environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1525, 'OBS-VULNERABILITY', N'Cybersecurity planning and controls are inherited from IT and/or do not reflect the operational realities of OT.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1526, 'OBS-DISCOVERY', N'The organization does not define or communicate OT security responsibilities. Staff are not trained on cybersecurity expectations, and roles are either unclear or absent from formal documentation.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1526, 'OBS-IMPACT', N'Lack of clarity around OT roles could allow unauthorized actions or unaddressed vulnerabilities.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1526, 'OBS-RECOMMENDATION', N'Define roles in policy; train staff annually; test role comprehension.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1526, 'OBS-RISK', N'Undefined or unclear OT security roles reduce accountability, leading to security gaps.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1526, 'OBS-RISK-STATEMENT', N'Unclear roles could lead to OT security gaps, reducing accountability.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1526, 'OBS-VULNERABILITY', N'Lack of role definition and role-specific training for OT staff.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1527, 'OBS-DISCOVERY', N'The organization does not require signed acknowledgments of rules of behavior from OT users before access. Verification and coordination with HR or physical access systems are not in place.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1527, 'OBS-IMPACT', N'Users may violate security rules unknowingly, leading to breaches or operational risks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1527, 'OBS-RECOMMENDATION', N'Require signed acknowledgments; integrate with badge issuance; conduct annual refresher training.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1527, 'OBS-RISK', N'Failure to obtain acknowledgment of rules of behavior weakens user accountability and policy enforcement.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1527, 'OBS-RISK-STATEMENT', N'Unacknowledged rules could lead to policy violations, introduces a gab of understanding of expected physical and electornic behaviour, and increase the risk of mishaps and unintended consiquences.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1527, 'OBS-VULNERABILITY', N'Lack of enforced acknowledgment of rules of behavior for OT users.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1528, 'OBS-DISCOVERY', N'')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1528, 'OBS-IMPACT', N'Unvetted personnel could deliberately or accidentally compromise OT systems, endangering safety and operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1528, 'OBS-RECOMMENDATION', N'Establish a formal screening process for all OT personnel that includes background checks appropriate to the level of system access. Ensure this process is documented and coordinated with HR and physical security to track employment status changes, revocations, and revalidations. Re-screen individuals periodically, and maintain centralized records of screening outcomes to support audits and personnel risk reviews.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1528, 'OBS-RISK', N'Failure to screen OT personnel increases the risk of insider threats and unauthorized system access.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1528, 'OBS-RISK-STATEMENT', N'Unscreened or improperly vetted personnel may pose insider threats—either intentional or accidental—due to their privileged access to OT systems. Inadequate coordination with HR and physical security may result in gaps where terminated or high-risk individuals retain system access, increasing the risk of sabotage, intellectual property theft, or operational disruption.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1528, 'OBS-VULNERABILITY', N'Absence of mandatory background checks for OT personnel.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1529, 'OBS-DISCOVERY', N'The organization does not enforce robust physical entry logs or escort requirements for ICS areas, and lacks tamper-evident or environmental controls(e.g., seals or sensors) to detect unauthorized physical access or modifications.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1529, 'OBS-IMPACT', N'Unauthorized access could result in system sabotage, manipulation, or environmental tampering impacting safety and operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1529, 'OBS-RECOMMENDATION', N'Implement and enforce a comprehensive physical access control program that includes detailed logging of physical entries into ICS areas, mandatory escorts for visitors(vendors, integrators, non-authorized staff), deployment of tamper-evident seals and environments controls(sensors, alarms) to detect and response to unauthorized access or modifications.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1529, 'OBS-RISK', N'Lack of enforced entry logs or tamper controls could enable undetected unauthorized access to ICS areas.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1529, 'OBS-RISK-STATEMENT', N'Unauthorized individuals may gain access to critical areas, potentially leading to sabotage, unauthorized modifications, or environmental damage that can compromise system integrity and safety.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1529, 'OBS-VULNERABILITY', N'Absence of robust access logging and tamper-evident controls in ICS environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1530, 'OBS-DISCOVERY', N'Physical access to OT communication infrastructure is not sufficiently restricted or monitored. Cabling, junctions, and conduits are not consistently protected with physical controls or logging mechanisms.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1530, 'OBS-IMPACT', N'Tampered communication lines could cause operational failures or expose sensitive OT data.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1530, 'OBS-RECOMMENDATION', N'Lock comm line access points; log all physical access; install motion sensors or cameras.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1530, 'OBS-RISK', N'Lack of secured access to OT communication lines could enable sabotage or unauthorized interception of critical communications.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1530, 'OBS-RISK-STATEMENT', N'Unprotected comm lines could be sabotaged, disrupting OT operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1530, 'OBS-VULNERABILITY', N'Unrestricted physical access to OT communication lines.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1531, 'OBS-DISCOVERY', N'External connections(cables, ports) are not physically secured or monitored, leaving them vulnerable to tampering')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1531, 'OBS-IMPACT', N'Tampered external connections could disrupt operations or serve as entry points for attacks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1531, 'OBS-RECOMMENDATION', N'Secure connections with locks; monitor via cameras; limit vendor access with oversight, disable ports not in use.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1531, 'OBS-RISK', N'Unprotected external connections increase the risk of tampering or unauthorized access to OT systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1531, 'OBS-RISK-STATEMENT', N'Tampered connections could disrupt operations or allow unauthorized access.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1531, 'OBS-VULNERABILITY', N'Insecure or unmonitored external OT connections.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1532, 'OBS-DISCOVERY', N'The organization does not consistently enforce robust physical security measures across its ICS environments. This may include gaps in physical access controls such as locked cabinets, restricted areas, or effective video surveillance and monitoring systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1532, 'OBS-IMPACT', N'Unauthorized physical access could lead to sabotage, system manipulation, or operational disruption.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1532, 'OBS-RECOMMENDATION', N'Establish a layered physical security strategy that:
Restricts access to ICS rooms and cabinets using locked doors, badge systems, or biometrics.
Enhances video surveillance with real-time monitoring or alerting(e.g., motion detection, analytics) so security personnel are notified of unauthorized entry attempts immediately.
Implements access logs or sign-in procedures to track who enters restricted areas.
Conducts regular audits of both physical and video logs, ensuring cameras and alert systems are tested periodically and remain functional.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1532, 'OBS-RISK', N'Inconsistent enforcement of physical security measures leaves ICS environments vulnerable to unauthorized access or tampering.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1532, 'OBS-RISK-STATEMENT', N'Without comprehensive physical security controls, unauthorized individuals may gain direct access to critical equipment and networking closets. This increases the risk of tampering, sabotage, or inadvertent disruptions that could compromise operational integrity and safety.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1532, 'OBS-VULNERABILITY', N'Lack of comprehensive physical security across ICS facilities.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1533, 'OBS-DISCOVERY', N'OT output devices (e.g., HMI screens, printers) are not physically protected against unauthorized access. Screens may be exposed in shared areas, and there are no controls to prevent viewing or extraction of sensitive data.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1533, 'OBS-IMPACT', N'Viewing or theft of critical data from exposed OT output devices could compromise confidentiality and operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1533, 'OBS-RECOMMENDATION', N'Restrict access with locks or badges; monitor via logs; obscure displays from unauthorized view.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1533, 'OBS-RISK', N'Inadequate protection of OT output devices could result in unauthorized exposure of sensitive operational information.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1533, 'OBS-RISK-STATEMENT', N'Exposed output devices could leak OT data, compromising confidentiality.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1533, 'OBS-VULNERABILITY', N'Poor physical security and monitoring of OT output devices.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1534, 'OBS-DISCOVERY', N'The organization does not allocate dedicated resources (e.g., budget, personnel) to support the OT cybersecurity program. Strategic planning and compliance alignment are not tracked or reviewed.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1534, 'OBS-IMPACT', N'Lack of budget or staff could leave critical vulnerabilities unaddressed, increasing risk of compromise or regulatory penalties.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1534, 'OBS-RECOMMENDATION', N'Develop a formal OT cybersecurity budget that includes funding for asset inventory, monitoring tools, staff training, and risk assessments. Define clear OT security roles and responsibilities, and assign appropriate personnel. Track resource usage against strategic goals quarterly to ensure alignment with compliance requirements (e.g., NIST 800-82, IEC 62443) and adjust plans based on threat landscape evolution and organizational changes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1534, 'OBS-RISK', N'Insufficient resource allocation weakens OT cybersecurity posture.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1534, 'OBS-RISK-STATEMENT', N'Insufficient investment in OT cybersecurity results in degraded defensive capability, limited visibility into threats, and delayed remediation of known issues. Lack of alignment with organizational strategy and compliance mandates can lead to audit failures, regulatory penalties, or exploitable gaps in security architecture.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1534, 'OBS-VULNERABILITY', N'Underfunded OT cybersecurity programs.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1535, 'OBS-DISCOVERY', N'The organization does not consistently incorporate cybersecurity clauses into ICS/OT acquisition contracts, leaving supply chain partners without enforceable security obligations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1535, 'OBS-IMPACT', N'Vulnerable or backdoored vendor products could be exploited, resulting in OT disruption, data breaches, or safety incidents.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1535, 'OBS-RECOMMENDATION', N'Require all ICS/OT acquisition contracts to specify security requirements, including vulnerability management, incident notification, access controls, and regular audits or compliance checks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1535, 'OBS-RISK', N'Vendor-provided software or hardware could introduce unknown vulnerabilities into OT systems if not evaluated or validated before deployment.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1535, 'OBS-RISK-STATEMENT', N'Without contractual security requirements, vendors or suppliers may introduce vulnerabilities into the ICS/OT environment through insecure products, services, or access practices.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1535, 'OBS-VULNERABILITY', N'Absence of security validation processes for vendor-supplied hardware and software.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1536, 'OBS-DISCOVERY', N'The organization does not require subcontractors to comply with cybersecurity clauses, or lacks monitoring mechanisms to ensure subcontractor compliance.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1536, 'OBS-IMPACT', N'Exploitation of third-party weaknesses could cause cascading failures across OT environments, impacting operations and safety.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1536, 'OBS-RECOMMENDATION', N'Extend cybersecurity clauses to all subcontractors and implement regular monitoring(e.g., attestations, audits, site visits) to verify compliance throughout the ICS/OT supply chain.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1536, 'OBS-RISK', N'Lack of visibility into third-party supply chains leaves OT systems exposed to downstream risks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1536, 'OBS-RISK-STATEMENT', N'Unmonitored subcontractors could bypass security expectations, introducing vulnerabilities or access pathways into critical ICS/OT systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1536, 'OBS-VULNERABILITY', N'Insufficient monitoring or vetting of third-party providers and their cybersecurity posture.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1537, 'OBS-DISCOVERY', N'The organization does not have a formal process for managing supply chain risks, lacks specific cybersecurity clauses in vendor contracts, or fails to validate that vendor-supplied hardware and software meet required security standards.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1537, 'OBS-IMPACT', N'Compromised vendors could supply vulnerable hardware/software, enabling attacks deep into critical OT systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1537, 'OBS-RECOMMENDATION', N'Establish a supply chain risk management program that:
Incorporates explicit cybersecurity requirements into all vendor contracts;
Restricts the connection of vendor devices to critical systems;
Implements rigorous validation of vendor-supplied hardware and software before deployment.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1537, 'OBS-RISK', N'Supply chain risks are not managed for ICS/OT products and services.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1537, 'OBS-RISK-STATEMENT', N'Inadequate supply chain controls can introduce vulnerabilities through compromised or non-compliant vendor products, which may weaken the overall security posture of the ICS environment and increase exposure to potential attacks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1537, 'OBS-VULNERABILITY', N'Lack of ICS-specific supply chain security controls and validation processes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1538, 'OBS-DISCOVERY', N'The organization does not validate the cybersecurity of vendor or supplier development and maintenance environments, leaving supply chain integrity at risk.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1538, 'OBS-IMPACT', N'Undetected vulnerabilities in externally developed systems could enable adversaries to compromise OT operations or safety-critical functions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1538, 'OBS-RECOMMENDATION', N'Require vendors and suppliers to maintain secure development and maintenance environments, validated via attestations, audits, or certifications(e.g., SOC 2, ISO 27001), and incorporate supply chain security into vendor risk assessments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1538, 'OBS-RISK', N'Lack of independent validation of vendor-provided components can allow security flaws or malicious code to remain undetected in OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1538, 'OBS-RISK-STATEMENT', N'Insecure vendor environments can allow attackers to implant backdoors or malicious code into ICS/OT products before delivery.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1538, 'OBS-VULNERABILITY', N'Failure to independently test and validate third-party supplied systems and components.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1539, 'OBS-DISCOVERY', N'The organization does not formally document or manage external system services or third-party solutions that interface with critical systems. Additionally, there may be no clear contractual or technical requirements for vendors or integrators regarding remote access, leaving the organization without assurance that external providers adhere to security controls.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1539, 'OBS-IMPACT', N'Compromised vendors could gain undetected access to critical systems, impacting operations and safety.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1539, 'OBS-RECOMMENDATION', N'Implement a vendor management program that:

Documents and enforces security requirements(contractual clauses, technical controls) for all external service providers interfacing with critical systems.
Controls remote access by vendors or integrators(e.g., MFA, jump hosts, IP whitelisting), with real-time monitoring or logging of sessions.
Conducts periodic reviews of third-party compliance, ensuring external providers maintain the agreed-upon security posture and promptly address any discovered gaps.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1539, 'OBS-RISK', N'Poor management of third-party OT access increases risk of unauthorized actions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1539, 'OBS-RISK-STATEMENT', N'Without documented security expectations or monitoring of external service providers, unauthorized access or malicious activities may go unnoticed. A compromised vendor or lax third-party process could allow attackers to infiltrate critical systems, leading to operational disruptions, data breaches, or safety incidents.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1539, 'OBS-VULNERABILITY', N'Inadequate control and oversight of third-party OT system access.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1540, 'OBS-DISCOVERY', N'OT products—whether hardware or software—are not consistently evaluated for security compliance prior to procurement and deployment. In some cases, vendor products may be accepted without testing for default insecure configurations, protocol hardening, firmware validation, or secure update mechanisms. Alignment with operational and safety requirements is not confirmed before integration. Where evaluations are conducted, results are often undocumented or not mapped to OT cybersecurity standards.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1540, 'OBS-IMPACT', N'Non-compliant products could be exploited, causing OT system compromise or operational failures.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1540, 'OBS-RECOMMENDATION', N'Implement a pre-procurement testing and evaluation process that includes security assessments of firmware, interfaces, and configurations. Validate that vendor products meet functional and security requirements, and align with site operational needs. Leverage lab environments to test products under realistic conditions, and require vendors to provide security documentation and hardening guidance before purchase approval.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1540, 'OBS-RISK', N'Failure to validate vendor OT product security introduces vulnerabilities.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1540, 'OBS-RISK-STATEMENT', N'Acquiring OT products without security validation risks introducing untrusted firmware, unpatched vulnerabilities, or insecure configurations that undermine plant safety and resilience. Operational misalignment may result in system incompatibilities or reduced safety margins.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1540, 'OBS-VULNERABILITY', N'Lack of security validation for procured OT products.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1541, 'OBS-DISCOVERY', N'Cybersecurity responsibilities are undefined or inconsistently applied across vendors. There is no formal mechanism to review, enforce, or verify implementation of security requirements over the contract lifecycle. This gap weakens the organization''s ability to manage OT supply chain risks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1541, 'OBS-IMPACT', N'Compromised vendors could introduce backdoors or weaknesses, leading to breaches or system disruptions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1541, 'OBS-RECOMMENDATION', N'Embed OT-specific security requirements in all acquisition and service contracts. These should cover baseline security controls (e.g., secure development practices, support for patching, vulnerability disclosure timelines) and mandate alignment with standards like IEC 62443-4-1. Establish a process to regularly verify vendor compliance through audits, assessments, and performance reviews, and document enforcement actions or exceptions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1541, 'OBS-RISK', N'Weak vendor security controls increase OT supply chain risk.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1541, 'OBS-RISK-STATEMENT', N'Omitting cybersecurity clauses from OT procurement contracts creates opportunities for vendors to deliver insecure products or services without accountability. This weakens supply chain integrity and may introduce unmanaged vulnerabilities, backdoors, or software with exploitable flaws into the operational environment.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1541, 'OBS-VULNERABILITY', N'Absence of OT security requirements in vendor contracts.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1542, 'OBS-DISCOVERY', N'The organization does not consistently monitor vendor or integrator accounts after initial onboarding, risking unauthorized activities or privilege misuse in ICS/OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1542, 'OBS-IMPACT', N'Unauthorized access, data leakage, or disruptions could occur through unmanaged third-party access points or integrations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1542, 'OBS-RECOMMENDATION', N'Implement continuous monitoring of vendor/integrator accounts, including periodic access reviews, real-time activity logging, and integration with incident detection/response processes.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1542, 'OBS-RISK', N'Insufficient monitoring and control of OT third-party relationships can lead to unapproved or insecure vendor interactions.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1542, 'OBS-RISK-STATEMENT', N'Vendor or integrator accounts that are not actively monitored may be misused, retained unnecessarily, or leveraged in attacks against ICS/OT systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1542, 'OBS-VULNERABILITY', N'Lack of continuous oversight and reassessment of vendor and third-party access to OT systems.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1543, 'OBS-DISCOVERY', N'The organization does not systematically monitor or validate the cybersecurity practices of contractors, integrators, and vendors after initial onboarding, allowing potential security drift.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1543, 'OBS-IMPACT', N'Insider threats introduced through vendors or contractors could compromise system security, steal sensitive data, or sabotage operations.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1543, 'OBS-RECOMMENDATION', N'Establish a contractor/vendor cybersecurity validation program that includes periodic audits, access reviews, and security posture reassessments to ensure continued protection of ICS/OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1543, 'OBS-RISK', N'Inadequate vetting of third-party personnel or weak onboarding processes can introduce insider risks into OT environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1543, 'OBS-RISK-STATEMENT', N'Unmonitored third parties may introduce vulnerabilities, misconfigurations, or facilitate unauthorized access to ICS/OT systems, increasing operational and security risks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1543, 'OBS-VULNERABILITY', N'Lack of formal vetting and onboarding controls for external personnel accessing OT networks.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1544, 'OBS-DISCOVERY', N'The organization does not formally require vendors to report discovered vulnerabilities or provide timely remediation plans for ICS/OT components.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1544, 'OBS-IMPACT', N'Systems or components without embedded security controls may be vulnerable to exploitation, unauthorized access, or operational disruption.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1544, 'OBS-RECOMMENDATION', N'Require all ICS/OT vendors to notify the organization immediately upon discovering vulnerabilities, provide mitigation or patch timelines, and establish escalation procedures if remediation is delayed.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1544, 'OBS-RISK', N'Failure to define security requirements in acquisition and development processes for OT systems weakens overall security posture.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1544, 'OBS-RISK-STATEMENT', N'Without timely vendor notifications and remediation timelines, vulnerabilities could remain unaddressed in ICS/OT systems, increasing exploitation risk.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1544, 'OBS-VULNERABILITY', N'Lack of integration of cybersecurity requirements during OT system procurement or development.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1545, 'OBS-DISCOVERY', N'Cybersecurity training exists but does not include OT-specific content or scenarios. OT staff receive only general IT security training.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1545, 'OBS-IMPACT', N'ICS operators or engineers may overlook early indicators of attack due to lack of relevant context.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1545, 'OBS-RECOMMENDATION', N'Establish a cybersecurity training program that spans all departments and includes OT use cases, lateral movement paths, and role-specific awareness. Leverage CISA content or build ICS modules in-house.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1545, 'OBS-RISK', N'Training gaps between IT and OT increase the risk of cross-domain compromise.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1545, 'OBS-RISK-STATEMENT', N'Without OT-relevant content, training fails to prepare ICS personnel for threats like insecure engineering access, malicious firmware, or lateral movement from IT.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1545, 'OBS-VULNERABILITY', N'Security awareness programs do not account for ICS risk profiles or operational constraints.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1546, 'OBS-DISCOVERY', N'General training is provided to IT staff, but OT staff are excluded or unaware of threats specific to ICS environments.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1546, 'OBS-IMPACT', N'Untrained users may become an unwitting entry point for attacks that move laterally into OT.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1546, 'OBS-RECOMMENDATION', N'Deliver organization-wide literacy training that includes ICS/OT examples (e.g., USB drops on HMI, credential reuse on ICS jump hosts). CISA and other sources offer content. Ensure annual delivery and contractor coverage.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1546, 'OBS-RISK', N'Lack of ICS context in training increases the risk of successful initial access via human error.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1546, 'OBS-RISK-STATEMENT', N'Without literacy training relevant to ICS operations, basic phishing, USB-based delivery, or insider actions may go unreported.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1546, 'OBS-VULNERABILITY', N'Awareness programs treat ICS roles as out-of-scope or invisible.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1547, 'OBS-DISCOVERY', N'The organization does not provide role-specific cybersecurity training for ICS/OT personnel. Engineers, operators, and vendors receive only general IT modules or are excluded from training altogether.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1547, 'OBS-IMPACT', N'Untrained ICS roles may unknowingly facilitate attacker persistence, privilege escalation, or data manipulation.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1547, 'OBS-RECOMMENDATION', N'Develop role-specific training modules for engineering, operational, and third-party users interacting with ICS. Include secure remote access, anomaly response, and common ICS attack vectors.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1547, 'OBS-RISK', N'Failure to deliver RBST to ICS personnel increases the risk of misconfigurations and lateral pivot opportunities.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1547, 'OBS-RISK-STATEMENT', N'Without ICS-relevant RBST, key personnel may misconfigure systems, enable insecure access paths, or ignore warning signs of compromise.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1547, 'OBS-VULNERABILITY', N'RBST is not extended to ICS despite its operational importance.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1548, 'OBS-DISCOVERY', N'The organization does not maintain a reliable or centralized method for tracking cybersecurity training across all staff. Training delivery may be informal, decentralized, or lack verification steps to ensure ICS-specific roles and external personnel are properly included.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1548, 'OBS-IMPACT', N'Missed or incomplete training could lead to operational risk, audit failure, or unaddressed skill gaps in ICS roles.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1548, 'OBS-RECOMMENDATION', N'Implement a centralized training record system covering all ICS and non-ICS personnel, including contractors and vendors. Assign ownership, require regular updates, and audit records at least annually. These records serve as critical evidence during incidents, demonstrate due diligence for regulatory or insurance requirements, and help ensure ICS roles are not excluded from organizational training programs.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1548, 'OBS-RISK', N'Lack of training oversight reduces accountability and weakens the organization’s ability to demonstrate readiness or compliance.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1548, 'OBS-RISK-STATEMENT', N'Without a centralized system, the organization cannot verify that required personnel—including OT staff and contractors—have received appropriate training. This weakens readiness oversight, hinders compliance, and limits accountability.')
INSERT INTO [dbo].[MATURITY_QUESTION_PROPS] ([Mat_Question_Id], [PropertyName], [PropertyValue]) VALUES (1548, 'OBS-VULNERABILITY', N'Training status for ICS personnel and contractors is unverified or not maintained in a centralized record.')
